        next = length(texture(iChannel0, vec2(float(i)/float(iters), 0.0)));
        sum += last;//pow(abs(last-next), 1.0);
        last = next;
    }
    return sum/float(iters)*2.0;
}

float highAverage()
{
    const int iters = 32;
    float sum = 0.0;

    float last = length(texture(iChannel0, vec2(0.0)));
    float next;
    for(int i = 17; i < iters; i++)
    {
        next = length(texture(iChannel0, vec2(float(i)/float(iters), 0.0)));
        sum += last;//pow(abs(last-next), 1.0);
        last = next;
    }
    return sum/float(iters)*2.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 p = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;

    bassBoostLow += lowAverage()*1.0;
    bassBoostHigh += highAverage()*1.0;
    time = iTime+bassBoostLow*8.0*pi;

    p += .125;

    p += .5*vec2(cos(time), sin(time));

	p.x *= iResolution.x / iResolution.y;
	p *= 1.5+1.125*sin(time*.25);

	const vec2 e = vec2(0.06545465634, -0.05346356485);
	vec2 c = time*e;
	//c = 8.0*iMouse.xy/iResolution.xy;
	float d = 1.0;
	vec3 col = vec3(0.0);
	const float blursamples = 4.0;
	float sbs = sqrt(blursamples);
	float mbluramount = 1.0/iResolution.x/length(e)/blursamples*2.0;
	float aabluramount = 1.0/iResolution.x/sbs*4.0;
	for (float b = 0.0; b < blursamples; b++) {
		col += formula(
			p + vec2(mod(b, sbs)*aabluramount, b/sbs*aabluramount), 
			c + e*mbluramount*b);
	}
	col /= blursamples;
	fragColor = vec4(col, 1.0);
}

// The MINIMIZED version of https://www.shadertoy.com/view/Xl2XWt


const float MAX_TRACE_DISTANCE = 20.0;           // max trace distance
const float INTERSECTION_PRECISION = 0.0001;        // precision of the intersection
const int NUM_OF_TRACE_STEPS = 100;

struct Material
{
  float reflectivity;
  float ambient;
  vec3  color;
};


struct Ray{
    vec3 color;
	vec3 currentPos;
    vec3 currentDir;
    vec3 lastPos;
    vec3 lastDir;
    float power;
    float stepsTaken;
    float timesReflected;  
    float complete;
};





//----
// Camera Stuffs
//----
mat3 calcLookAtMatrix( in vec3 ro, in vec3 ta, in float roll )
{
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(sin(roll),cos(roll),0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    return mat3( uu, vv, ww );
}

// checks to see which intersection is closer
// and makes the y of the vec2 be the proper id
vec2 opU( vec2 d1, vec2 d2 ){

	return (d1.x<d2.x) ? d1 : d2;

}

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) +
         length(max(d,0.0));
}

float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}


//--------------------------------
// Modelling 
//--------------------------------
vec2 map( vec3 pos ){  

    pos -= vec3( 0., 0. , iTime * .1 );

    vec3 modVal =  vec3( .4 , .4 , .4 );
    vec3 q = mod( pos , modVal ) - modVal / 2.;
 	vec2 res = vec2( sdSphere( q - vec3( 0. , 0. , 0. ) , .1 ) , 1. ); 
    return res;  

}

//The inverse model, so that we can properly refract
vec2 iMap( vec3 pos ){  

    pos -= vec3( 0., 0. , iTime * .1 );

    vec3 modVal =  vec3( .4 , .4 , .4 );
    vec3 q = mod( pos , modVal ) - modVal / 2.;
 	vec2 res = vec2( sdSphere( q - vec3( 0. , 0. , 0. ) , .1 ) , 1. ); 
    return res;  

}



vec3 calcIntersection( in vec3 ro, in vec3 rd ){


    float h =  INTERSECTION_PRECISION*2.0;
    float t = 0.0;
	float res = -1.0;
    float id = -1.;
   	float stepsTaken = 0.;

    for( int i=0; i< NUM_OF_TRACE_STEPS ; i++ ){

        if( h < INTERSECTION_PRECISION || t > MAX_TRACE_DISTANCE ) break;
	   	vec2 m = map( ro+rd*t );
        h = m.x;
        t += h;
        id = m.y;
        stepsTaken = float( i );

    }

    if( t < MAX_TRACE_DISTANCE ) res = t;
    if( t > MAX_TRACE_DISTANCE ) id =-1.0;

    return vec3( res , id , stepsTaken );

}

vec3 calcIIntersection( in vec3 ro, in vec3 rd ){


    float h =  INTERSECTION_PRECISION*2.0;
    float t = 0.0;
	float res = -1.0;
    float id = -1.;
   	float stepsTaken = 0.;

    for( int i=0; i< NUM_OF_TRACE_STEPS ; i++ ){

        if( h < INTERSECTION_PRECISION || t > MAX_TRACE_DISTANCE ) break;
	   	vec2 m = iMap( ro+rd*t );
        h = m.x;
        t += h;
        id = m.y;
        stepsTaken = float( i );

    }

    if( t < MAX_TRACE_DISTANCE ) res = t;
    if( t > MAX_TRACE_DISTANCE ) id =-1.0;

    return vec3( res , id , stepsTaken );

}




// Calculates the normal by taking a very small distance,
// remapping the function, and getting normal for that
vec3 calcNormal( in vec3 pos ){

	vec3 eps = vec3( 0.001, 0.0, 0.0 );
	vec3 nor = vec3(
	    map(pos+eps.xyy).x - map(pos-eps.xyy).x,
	    map(pos+eps.yxy).x - map(pos-eps.yxy).x,
	    map(pos+eps.yyx).x - map(pos-eps.yyx).x );
	return normalize(nor);
}



// Takes in a ray, and 
// gives back a new ray w/ info!
Ray shootRay( Ray ray ){

    vec3 res = calcIntersection( ray.currentPos , ray.currentDir );

    if( res.y >= 0. ){


    	vec3 pos = ray.currentPos + ray.currentDir * res.x;
        vec3 nor = calcNormal( pos );
        //float ao = calcAO( pos , norm );

        vec3 refl = reflect( ray.currentDir , nor );
        vec3 refr = refract( ray.currentDir , nor , 1. / 1.2 );


   	 	float newSteps = ray.stepsTaken + res.z;

        vec3 color = nor * .5 + .5;
        return Ray(
            		color,
                    pos + refl * INTERSECTION_PRECISION * 2.,
                    refl,
                    ray.currentPos,
                    ray.currentDir,
                    ray.power - .8,
                    newSteps,
                    ray.timesReflected + 1.,
                    0.
        		);




    }else{

        // If it didn't hit anything, lets follow it to the end
        vec3 pos = ray.currentPos + ray.currentDir * MAX_TRACE_DISTANCE;

        return Ray( 
            vec3( 1. ),
            pos, 
            ray.currentDir ,
            ray.lastPos,
            ray.lastDir ,
            ray.power , 
            ray.stepsTaken,
            ray.timesReflected,
            1.
         );

    }




}


vec3 render( vec3 ro , vec3 rd ){



   Ray ray = Ray( vec3( 0. ),  ro , rd , ro , rd , 1. ,0.,0.,0.);

   vec3 col = vec3( 0. );

   for( int i = 0; i < 10; i++ ){

   	Ray newRay = shootRay( ray );

    if( newRay.power <= 0. || newRay.complete == 1. ){

      col = newRay.color ;
      break;

    }

    ray = newRay;


   }


   return col;


}






void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 p = (-iResolution.xy + 2.0*fragCoord.xy)/iResolution.y;

    vec3 ro = vec3( 0., 0., 2.);
    vec3 ta = vec3( 0. , 0. , 0. );

    // camera matrix
    mat3 camMat = calcLookAtMatrix( ro, ta, 0.0 );  // 0.0 is the camera roll

	// create view ray
	vec3 rd = normalize( camMat * vec3(p.xy,2.0) ); // 2.0 is the lens length

  	vec3 color = render( ro , rd );

	fragColor = vec4(color,1.0);



}

#define STARDISTANCE 150.
#define STARBRIGHTNESS 0.5
#define STARDENSITY 0.05

float hash13(vec3 p3)
{
	p3  = fract(p3 * vec3(.1031,.11369,.13787));
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

float stars(vec3 ray)
{
    vec3 p = ray * STARDISTANCE;
    float h = hash13(p);
    float flicker = cos(iTime * 1. + hash13(abs(p) * 0.01) * 13.) * 0.5 + 0.5;
    float brigtness = smoothstep(1.0 - STARDENSITY, 1.0, hash13(floor(p)));
    return smoothstep(STARBRIGHTNESS, 0., length(fract(p) - 0.5)) * brigtness * flicker;
}

vec3 camera(vec2 fragCoord)
{
	vec3 ray = normalize(vec3( fragCoord.xy-iResolution.xy*.5, iResolution.x));
    vec2 angle = vec2(3. + iTime * -0.01, 10. + iTime * 0.10);
    vec4 cs = vec4(cos(angle.x), sin(-angle.x), cos(angle.y), sin(angle.y));
    ray.yz *= mat2(cs.xy,-cs.y,cs.x); 
    ray.xz *= mat2(cs.zw,-cs.w,cs.z); 
    return ray;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 ray = camera(fragCoord);
    float s = stars(ray);
	fragColor = vec4(s, s ,s ,1.0);
}

#define MAX_RAY_DIST 100.0
#define MAX_RAY_ITERS 100
#define EPSILON 0.001

#define CAM_DIST 3.0
#define CAM_SPEED 0.01

#define PI 3.1415926535

#define EV vec2(EPSILON, 0.0)

#define GLOBAL_AMBIENT vec3(0.1)

float sphere(in vec3 pos, in float r) {
    return length(pos) - r;
}

float box(in vec3 pos, in vec3 dims) {
  vec3 d = abs(pos) - dims;
  return min(max(d.x,max(d.y,d.z)),0.0) +
         length(max(d,0.0));   
}

float plane(in vec3 pos) {
    return -pos.y;
}

float opU(in float d1, in float d2) {
    return min(d1, d2);
}

float opS(in float d1, in float d2) {
    return max(d1, -d2);
}

float bentBox(in vec2 bp, in vec3 pos, in vec3 dims) {
    float c = cos(bp.x * pos.y);
    float s = sin(bp.y * pos.y);
    mat2  m = mat2(c,-s,s,c);
    vec3  q = vec3(m * pos.xy, pos.z);
    return box(q, dims);
}

float map(in vec3 pos) {
    float d = box(pos, vec3(1.0));
    for (float x = -1.0; x <= 1.0; x += 2.0) {
    	for (float y = -1.0; y <= 1.0; y += 2.0) {
    		for (float z = -1.0; z <= 1.0; z += 2.0) {
        		d = opU(d, sphere(pos + vec3(x, y, z), 0.5));
    		}   
    	}
    }
    vec2 arms = vec2(2.0, 0.25);
    d = opS(d, box(pos, arms.xyy));
    d = opS(d, box(pos, arms.yxy));
    d = opS(d, box(pos, arms.yyx));

    return d;
}

vec3 normalAt(in vec3 ro) {
    return normalize(vec3(map(ro + EV.xyy) - map(ro - EV.xyy),
                          map(ro + EV.yxy) - map(ro - EV.yxy),
                          map(ro + EV.yyx) - map(ro - EV.yyx)));
}

vec3 colorAt(in vec3 ro) {
    return vec3(0.0, 1.0, 1.0);
}

float shadow(in vec3 ro, in vec3 lo, in vec3 ld) {
    float dist = EPSILON;
    float totalDist = 0.0;
    for (int i = 0; i < MAX_RAY_ITERS; i++) {
        if (abs(dist) < EPSILON ||
            totalDist > MAX_RAY_DIST) {
            break;
        }

        dist = map(lo);
        totalDist += dist;
        lo += dist * ld;
    }

    if (abs(length(ro - lo)) < 0.01) {
        return 1.0;
    } else {
        return 0.25;
    }
}

vec3 getLightPos(in int idx) {
    if (idx == 0) {
        return vec3(3.0);
    } else {
        return vec3(CAM_DIST * 2.0 * sin(iTime * PI), 
                    CAM_DIST * 2.0 * sin(iTime),
                    CAM_DIST * 2.0 * cos(iTime * PI));
    }
}

vec3 lighting(in vec3 ro, in vec3 rd, in float dist) {
    const float SHININESS = 90.0;
    float diffuse = 0.0;
    float spec = 0.0;
    float shade = 0.0;
    for (int i = 0; i < 2; i++) {
        vec3 lightPos = getLightPos(i);
    	vec3 L = normalize(lightPos - ro);
    	vec3 N = normalAt(ro);
    	vec3 V = normalize(-dist * rd);
	    vec3 R = normalize(reflect(-L, N));


	    diffuse += max(0.0, dot(N, L));
		spec += pow(max(0.0, dot(R, V)), 0.3 * SHININESS);

        shade = max(shade, shadow(ro, lightPos, -L));
    }
    diffuse = clamp(diffuse, 0.0, 1.0);
    spec = clamp(spec, 0.0, 1.0);
    shade = clamp(shade, 0.0, 1.0);

    vec3 light = shade * vec3(diffuse + spec);

    return GLOBAL_AMBIENT + light * colorAt(ro);
}

vec3 march(in vec3 ro, in vec3 rd) {
    float dist = EPSILON;
    float totalDist = 0.0;
    for (int i = 0; i < MAX_RAY_ITERS; i++) {
        if (abs(dist) < EPSILON ||
            totalDist > MAX_RAY_DIST) {
            break;
        }

        dist = map(ro);
        totalDist += dist;
        ro += dist * rd;
    }

    if (abs(dist) < EPSILON) {
        return lighting(ro, rd, totalDist);
    } else {
        return GLOBAL_AMBIENT;
    }
}

void setupCamera(in vec2 uv, out vec3 ro, out vec3 rd) {
    float camTheta = iMouse.x * CAM_SPEED;
    float camPhi = (iMouse.y / iResolution.y) * PI;
    vec3 cop = vec3(CAM_DIST * -sin(camTheta), 
                    CAM_DIST * cos(camPhi), 
                    CAM_DIST * cos(camTheta));

    vec3 target = vec3(0.0);
    vec3 up = vec3(0.0, 1.0, 0.0);

    vec3 cdir = normalize(target - cop);
    vec3 cright = normalize(cross(cdir, up));
    vec3 cup = cross(cright, cdir);

    rd = uv.x * cright + uv.y * cup + cdir;
    ro = cop;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = ((fragCoord.xy / iResolution.xy) - vec2(0.5)) * 2.0;
    uv.x *= iResolution.x / iResolution.y;

    vec3 ro;
    vec3 rd;

    setupCamera(uv, ro, rd);

	fragColor = vec4(march(ro, rd), 1.0);
}

#define STEPS 128

mat3 my(float beta)
{
	return mat3(cos(beta), 0, sin(beta),
				0, 1, 0,
				-sin(beta), 0, cos(beta));
}

mat3 mx(float beta)
{
	return mat3(1, 0, 0,
				0, cos(beta), sin(beta),
				0, -sin(beta), cos(beta));
}


float sphere ( vec3 p, float s )
{
    return length(p) - s;
}


float rep( vec3 p, vec3 c )
{
    vec3 q = mod(p, c) - 0.5 * c;
    return sphere(q, 0.05);
}

float map( vec3 ro, vec3 rd )
{
	float res;
	float val = -1.0;
	vec3 ray = ro + rd;
	for (int j = 0; j < STEPS; j++)
	{
		float t = float(j)/float(STEPS);

		float res = rep(ray, vec3(0.55));		

		if (res < .001)
		{
			val = length(ray);
            break;
		}

		if (length(ray) > 9.0) return -1.0;
		ray += res * rd;
	}
	return val;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float aspect = iResolution.x/iResolution.y;
	vec3 ro = vec3(0.0, 0.0, 3.0);
	vec3 rd = normalize(vec3( (-1.0+2.0*uv) * vec2(aspect, 1.0), -1.0));

    ro *= my(iTime) * mx(iTime * 0.1);
    rd *= my(iTime) * mx(iTime * 0.1);

    float c;
	float d = map(ro, rd);

	vec3 col;
	if (d > 0.0)
	{
        col = vec3(pow(d * 0.3, 0.9));
	}
	else
    {
		col = vec3(1.0); //background
    }



	fragColor = vec4(col, 1.0);
}

