float map(vec3 p) 
{
    //Repetition
    vec3 c = vec3 (1.0,1.0,1.0);
    vec3 q = mod(p,c)-0.5*c; 
    return sdTorus (q);   
}

float trace(vec3 o, vec3 r) //raymarching function
{ 
    float t = 0.0;
    for (int i=0; i<32;i++)
    {
     vec3 p = o + r * t;

     float d = map(p);

     t += d*1.0;   
    }

   return t;     
}

