// Double Binary Tree/Quaternary Tree as Ray-Traced Implicit Surface - written 2015-11-10 by Jakob Thomsen
// Closed-form expression Implicit Surface Tree - NO recursion/iteration, using only fract,exp2,ceil,log2 for branching.
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
// (ported from http://sourceforge.net/projects/shaderview/)

#define pi 3.1415926

float sfract(float val, float scale)
{
    return (2.0 * fract(0.5 + 0.5 * val / scale) - 1.0) * scale;
}

vec2 mirror2(vec2 v)
{
    return abs(2.0 * fract(v * 0.5) - 1.0);
}

vec3 BinarySubDiv(vec3 v )
{
    v = fract(exp2(ceil(-log2(v.z))) * v * vec3(.5, .5, 1.));
    v.xy = mirror2(2. * v.xy);
    return v;
}

float fn(vec3 v)
{
    v.z *= -1.0;
    v = 0.5 * v + 0.5;
    float z = v.z;
    vec3 p = vec3(0.5, .5, 0.);
    vec3 n = vec3(1.0, 1.0, 1.) - p;
    v = BinarySubDiv(v);
    v.xy = mirror2(v.xy);
    //return length((v - p) - dot(v - p, n) * n / dot(n, n)) - .01;
    //return length((v - p) - dot(v - p, n) * n / dot(n, n)) - .01 * ceil(-log2(1.0-v.z));
    //return length((v - p) - dot(v - p, n) * n / dot(n, n)) - .01 * exp2(ceil(-log2(1.0-v.z)));
    //return length((v - p) - dot(v - p, n) * n / dot(n, n)) - .01 * exp2(ceil(-log2(v.z)));
    return length((v - p) - dot(v - p, n) * n / dot(n, n)) * exp2(ceil(log2(z))) - .01;
}

vec3 nrm(vec3 v)
{
    vec3 n;
    float d = 0.01;
    n.x = fn(v + vec3(d, 0.0, 0.0)) - fn(v + vec3(-d, 0.0, 0.0));
    n.y = fn(v + vec3(0.0, d, 0.0)) - fn(v + vec3(0.0, -d, 0.0));
    n.z = fn(v + vec3(0.0, 0.0, d)) - fn(v + vec3(0.0, 0.0, -d));
    return normalize(n);
}

float comb(float v, float s)
{
    return pow(0.5 + 0.5 * cos(v * 2.0 * pi), s);
}

vec3 tex(vec3 v)
{
    float d = abs(v.z * 20. + sfract(-iTime, 4.) * 5.); 
    vec3 c = 0.5 + 0.5 * nrm(v);
    return exp(-d*d) + c * abs(nrm(v).x);
}

vec3 camera(vec2 uv, float depth)
{
    float t = iTime * 0.1;
    vec3 v;
    //v.x = uv.x * cos(t) + uv.y * sin(t);
    //v.y = uv.x * -sin(t) + uv.y * cos(t);
    //v.z = depth;

    // isometry
    vec3 iso;
//    iso.x =  v.x - v.y - v.z;
//    iso.y = -v.x - v.y - v.z;
//    iso.z =        v.y - v.z;

//    return iso;
    iso.x =  uv.x - uv.y - depth;
    iso.y = -uv.x - uv.y - depth;
    iso.z =         uv.y - depth;

    v.x = iso.x * cos(t) + iso.y * sin(t);
    v.y = iso.x * -sin(t) + iso.y * cos(t);
    v.z = iso.z;

    return v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime * 0.1;
    vec2 uv = 2.0 * fragCoord.xy / iResolution.xy - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    const int depth = 256;//128;
    //float m = 0.0;
    vec3 color = vec3(0.0, 0.0, 0.0);
    for(int layer = 0; layer < depth; layer++) // slow...
    {
        vec3 v = camera(uv, 2.0 * float(layer) / float(depth) - 1.0);

        if(abs(v.x) > 1.0 || abs(v.y) > 1.0 || abs(v.z) > 1.0)
            continue;

        if(abs(fn(v)) < 0.01)
        {
            //m = 2.0 * float(layer) / float(depth) - 1.0;
            color = tex(v);
        }
    }

    //fragColor = vec4(color * vec3(m), 1.0);
    fragColor = vec4(color, 1.0);
}

// Test for shorter starfield. 
// see also https://www.shadertoy.com/results?query=starfield&sort=newest
// This one is based on texture advection, quadtree and procedural stars.

float D=8., Z=3.;               // D: duration of advection layers, Z: zoom factor

#define R(U,d) fract( 1e4* sin( U*mat2(1234,-53,457,-17)+d ) )

float M(vec2 U, float t) {           // --- texture layer
// vec2 iU = ceil(U/=exp2(t-8.)),              // quadtree cell Id - infinite zoom
   vec2 iU = ceil(U/=exp2(t-8.)*D/(3.+t)),     // quadtree cell Id - with perspective
          P = .2+.6*R(iU,0.);                  // 1 star position per cell
    float r = 9.* R(iU,1.).x;                  // radius + proba of star ( = P(r<1) )
	return r > 1. ? 1. :   length( P - fract(U) ) * 8./(1.+5.*r) ;
}

void mainImage( out vec4 o,  vec2 U )
{
    o -= o;
    U = U / iResolution.y - .5;

    // --- prepare the timings and weightings of the 3  texture layers

    vec3 P = vec3(-1,0,1)/3., T,
         t = fract( iTime/D + P +.5 )-.5,  // layer time
         w = .5+.5*cos(6.28*t);                  // layer weight
    t = t*D+Z;  

    // --- prepare the 3 texture layers

    T.x = M(U,t.x),  T.y = M(-U,t.y),  T.z = M(U.yx,t.z); // avoid using same U for all layers
    //T = sin(100.*U.x/exp2(t3))+sin(100.*U.y/exp2(t3));  // try this for obvious pattern
    T = .03/(T*T);

    // --- texture advection: cyclical weighted  sum

    o += dot(w,T);
