    float time = iTime;

    vec2 point1 = vec2(sin(time*0.96),cos(time*2.12));
    point1 = (point1+1.)/2.;
    /*
    if (iMouse.z >= 1.) {
        point1 = vec2((((((iMouse.x*(iResolution.x/iResolution.y))*2.)/iResolution.x)-(iResolution.x/iResolution.y))+1.)/2.,iMouse.y/iResolution.y);
    }*/
    vec2 point2 = vec2(sin(time*3.11),cos(time*1.05));
    point2 = (point2+1.)/2.;

    float drawing = 0.;
