    pos.z = mod(pos.z,depth*2.)-0.5*depth*2.;

   	float cyl = sdHexPrism( pos, vec2(height-t, depth+t));
   	float scyl = sdHexPrism( pos, vec2(height-t*2.0, depth+t+.001));

    vec2 res = vec2(opS(scyl,cyl),1.5); 
    vec2 final = res;

    for (int i = 1; i < 3; i++) {

//        float c = cos(pos.z+iTime*0.5);
