// Equation of a general cone.
// Radius at z=Z0 is R0
// Radius at z=Z1 is R1
// Therefore r = R0 + ((z-Z0)/(Z1-Z0))*(R1-R0)
//             = sqrt(x^2+y^2)
//
// Simplify the first line a bit:
// Zo = R0 - ((Z0)/(Z1-Z0))*(R1-R0)
// Zs =          1/(Z1-Z0))*(R1-R0)
// Therefore:
//   r = Zo + z*Zs = sqrt(x^2+y^2)
//
// Line equation for the ray we're casting is (xyz) = p + d*v
//
// Substitute one in the other, solve for d:
//
// (p.x + d*v.x)^2+(p.y + d*v.y)^2
//     = (Zo + (p.z+d*v.z)*Zs)^2
//     = (Zo + p.z*Zs + d*v.z*Zs)^2
//
// Gather by powers of d:
//
// 0 =
//     d^2 * (v.x^2+v.y^2 - (v.z*Zs)^2)
//   + d   * 2*(p.x*v.x + p.y*v.y - v.z*Zs*(Zo + p.z*Zs))
//   +       (p.x^2 + p.y^2 - (Zo + p.z*Zs)^2

struct Cone
{
    float Z0, Z1;
    float R0, R1;
    int Material;
    float Zo, Zs;
};

void PrecomputeCone ( inout Cone cn )
{
    cn.Zs = (cn.R1-cn.R0)/(cn.Z1-cn.Z0);
    cn.Zo = cn.R0 - (cn.Z0*cn.Zs);
}

// Returns both distances to cone given a ray origin p, direction v
vec2 ConeDistances ( in Cone cone, in vec3 p, in vec3 v )
{
    // Convert to standard form A(d^2) + Bd + C = 0
    float t0 = v.z * cone.Zs;
    float t1 = cone.Zo + p.z*cone.Zs;
    float A = (v.x*v.x + v.y*v.y - t0*t0);
    float B = 2.0*(p.x*v.x + p.y*v.y - v.z*cone.Zs*t1);
    float C = (p.x*p.x + p.y*p.y - t1*t1);

	// Then solve, paying attention to numerical stability as shown in
	// https://en.wikipedia.org/wiki/Loss_of_significance#A_better_algorithm
	// x1 = (-B-sgn(B)*sqrt(B^2-4AC))/2A
    // x2 = C/(Ax1)
    float discriminant = B*B-4.0*A*C;
	if ( discriminant < 0.0 )
    {
        return vec2(-1.0,-1.0);
    }
	float d1 = (-B-sign(B)*sqrt(discriminant))/(2.0*A);
    float d2 = C/(A*d1);
    vec2 res = vec2(d1, d2);
	return res;
}

const int NumCones = 13;

Cone cones[NumCones];

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Yes, I am nerdy enough to have made sure these dimensions are correct!
    // Thanks Wikipedia! https://upload.wikimedia.org/wikipedia/commons/4/41/9x19mm_Parabellum.svg
    float q = -12.0;
    cones[ 0] = Cone(q+ 0.00,0.0, 0.00*0.5,0.0, 0, 0.0,0.0);
    cones[ 1] = Cone(q+ 0.00,0.0, 9.36*0.5,0.0, 0, 0.0,0.0);
    cones[ 2] = Cone(q+ 0.30,0.0, 9.96*0.5,0.0, 0, 0.0,0.0);
    cones[ 3] = Cone(q+ 1.27,0.0, 9.96*0.5,0.0, 0, 0.0,0.0);
    cones[ 4] = Cone(q+ 1.27,0.0, 8.79*0.5,0.0, 0, 0.0,0.0);
    cones[ 5] = Cone(q+ 2.17,0.0, 8.79*0.5,0.0, 0, 0.0,0.0);
    cones[ 6] = Cone(q+ 2.98,0.0, 9.93*0.5,0.0, 0, 0.0,0.0);
    cones[ 7] = Cone(q+19.15,0.0, 9.65*0.5,0.0, 0, 0.0,0.0);
    cones[ 8] = Cone(q+19.15,0.0, 9.03*0.5,0.0, 1, 0.0,0.0);
    cones[ 9] = Cone(q+21.50,0.0, 8.80*0.5,0.0, 1, 0.0,0.0);
    cones[10] = Cone(q+25.00,0.0, 7.40*0.5,0.0, 1, 0.0,0.0);
    cones[11] = Cone(q+28.00,0.0, 4.50*0.5,0.0, 1, 0.0,0.0);
    cones[12] = Cone(q+29.40,q+29.69, 2.00*0.5,0.0, 1, 0.0,0.0);

	for ( int cn = 0; cn < NumCones; cn++ )
	{
        if ( cn < NumCones-1 )
        {
        	cones[cn].Z1 = cones[cn+1].Z0;
            cones[cn].R1 = cones[cn+1].R0;
        }
		PrecomputeCone ( cones[cn] );
	}

	vec3 pos;
	vec3 vec;


    vec2 angles;
    angles.x = 0.02 * iMouse.x + 0.5*iTime;
    angles.y = 0.02 * iMouse.y;

    float viewdist = 40.0;

	pos.x = viewdist*sin(angles.x) * cos(angles.y);
	pos.z = viewdist*cos(angles.x) * cos(angles.y);
    pos.y = viewdist*                sin(angles.y);

	vec3 lookat = vec3(0.0,0.0,0.0);
	vec3 viewz = normalize(lookat-pos);
	vec3 viewx = normalize(cross(vec3(0.0,1.0,0.0),viewz));
	vec3 viewy = normalize(cross(viewz,viewx));

    vec3 lightpos;
	lightpos.x = viewdist*sin(0.5*iTime);
	lightpos.z = viewdist*cos(0.5*iTime);
	lightpos.y = viewdist*2.0;
    vec3 lightvec = normalize(lightpos);

	float tanfov = 0.6;

    vec2 ndc = fragCoord.xy - 0.5*iResolution.xy;
    ndc *= 2.0/iResolution.x;
	ndc *= tanfov;
	vec3 dirn = normalize(viewz + viewx*ndc.x + viewy*ndc.y);

	int closestCn = -1;
    Cone closestCone;
	float closestDist = 1000000000.0;
	vec3 hitpos = vec3(0.0,0.0,10000000.0);
    vec3 normal;
	for ( int cn = 0; cn < NumCones; cn++ )
	{
        if ( cones[cn].Z0 == cones[cn].Z1 )
        {
            // Flat disc section.
            // Intersect view vector with plane at Z0
            // Z0 = pos.z + dirn.z * dist;
            // dist = (Z0-pos.z)/dirn.z;
            float Z0 = cones[cn].Z0;
            float dist = ( Z0 - pos.z ) / dirn.z;
            if ( dist > 0.0 )
            {
                float R0 = cones[cn].R0;
                float R1 = cones[cn].R1;
                vec2 hit = pos.xy + dirn.xy * dist;
                float radius2 = hit.x*hit.x + hit.y*hit.y;
                if ( R0 > R1 )
                {
                    if ( ( radius2 < (R0*R0) ) && ( radius2 > (R1*R1) ) )
                    {
	                    if ( closestDist > dist )
    	                {
        	                closestDist = dist;
            	            closestCn = cn;
                	        closestCone = cones[cn];
                    	    hitpos = vec3 ( hit, Z0 );
                        	normal = vec3 ( 0.0, 0.0, 1.0 );
                    	}
                    }
                }
                else
                {
                    if ( ( radius2 < (R1*R1) ) && ( radius2 > (R0*R0) ) )
                    {
	                    if ( closestDist > dist )
    	                {
        	                closestDist = dist;
            	            closestCn = cn;
                	        closestCone = cones[cn];
                    	    hitpos = vec3 ( hit, Z0 );
                        	normal = vec3 ( 0.0, 0.0, 1.0 );
                    	}
                    }
                }
            }
        }
        else
        {
            vec2 dist = ConeDistances ( cones[cn], pos, dirn );
            float dists[2];
            dists[0] = dist.x;
            dists[1] = dist.y;
            for ( int i = 0; i < 2; i++ )
            {
                vec3 hit = pos + dirn * dists[i];
                if ( ( dists[i] > 0.0 ) && ( hit.z < cones[cn].Z1 ) && ( hit.z > cones[cn].Z0 ) )
                {
                    if ( closestDist > dists[i] )
                    {
                        closestDist = dists[i];
                        closestCn = cn;
                        closestCone = cones[cn];
                        hitpos = hit;
                        normal.xy = normalize(hitpos.xy);
                        normal.z = -closestCone.Zs;
                    }
                }
            }
        }
	}

	if ( closestCn < 0 )
	{
        vec2 uv = fragCoord.xy / iResolution.xx;
		fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
	}
	else
	{
		normal = normalize(normal);

        // wraparound diffuse
		float shade = dot(lightvec,normal);
		shade = shade * 0.5 + 0.5;

        vec3 col = vec3(1.0,1.0,1.0);
        float specshade = 1.0;
        if      ( closestCone.Material == 0 ) { col = vec3(1.0,1.0,0.5); specshade = 0.3; }
        else if ( closestCone.Material == 1 ) { col = vec3(0.3,0.3,0.3); specshade = 0.4; }

        col *= shade;
        //vec4 tex = texture ( iChannel0, vec2(hitpos.x, hitpos.z));
        //col *= tex.xyz;

        // Blinn-Phong specular
        vec3 lightvec = normalize(lightpos-hitpos);
        vec3 halfvec = normalize(lightvec-viewz);
        float spec = dot(halfvec,normal);
        if ( spec > 0.0 )
        {
	        col += pow(spec,10.0) * specshade;
        }

		fragColor = vec4(col, 1.0);
	}
}

#define _t iTime/10.

vec2 itere (vec2 uv)
{
    for (int i=0; i<8; i++)
    {
    	uv+=vec2(cos(uv.y*3.+_t),-sin(uv.x*3.))/3.;
        uv+=vec2(cos(_t+uv.y),sin(_t+uv.x))*0.5;
        uv*=1.3;
    }

    return uv;
}
float color (vec2 uv)
{
     uv = itere (uv);
     float sc = 2.;
     uv = mod(uv,sc)-sc/2.;
     return length(uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy / iResolution.xy - 0.5)*8.;
    float c = color(uv);
    float cx = color (uv+vec2(0.01,0.))-c;
    float cy = color (uv+vec2(0.,0.01))-c;
	fragColor = normalize((vec4(cx,sqrt(abs(cx*cy)),cy,c/2.)));
}

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

vec2 light;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    light = vec2(iMouse.x/iResolution.x, iMouse.y/iResolution.y);
    vec2 pos = vec2(fragCoord.x/iResolution.x, fragCoord.y/iResolution.y);
    float dist = sqrt((pos.x-light.x)*(pos.x-light.x) + (pos.y-light.y)*(pos.y-light.y));
	float col = 0.5 - sqrt(dist) + 0.075 * rand(pos) * rand(pos * vec2(iTime, iTime));
    if (col < 0.01)
        col = 0.;
	fragColor = vec4(col, col, col, 1.0);
}

// Created by sebastien durand - 2015
