// adapted from  BigWIngs : https://www.shadertoy.com/view/XlcSzM
vec3 background(vec3 upCol, vec3 r, vec2 uv) {
    float u = dot(r, vec3(0,1,0))*.5+.5;
    vec3 col = upCol*u*2.;
    float t = iTime*.1, 		
     	x = atan(r.x, r.z),					
     	y = 3.1415*0.5-acos(r.y),
        a = sin(r.x),
     	beam = clamp(sin(10.*x+a*y*5.+t),0.,1.) * clamp(sin(7.*x+a*y*3.5-t),0.,1.)
     		 + clamp(sin(42.*x+a*y*21.-t),0.,1.) * clamp(sin(34.*x+a*y*17.+t),0.,1.);
    col *= 1.+beam*.05;
    return col;
}

