vec4 _p_2 = vec4(0.033,0.750,1.000,0.400);
vec4 _p_3 = vec4(0.125,0.520,1.000,0.600);
vec4 _p_4 = vec4(0.244,0.070,1.000,0.800);
vec4 _p_5 = vec4(0.000,0.000,1.000,1.000);
vec4 _palette_lo(float i) {
  return  (i<=_p_1.w)?_p_0: (i<=_p_2.w)?_p_1: (i<=_p_3.w)?_p_2: (i<=_p_4.w)?_p_3: (i<=_p_5.w)?_p_4: _p_5;
}
vec4 _palette_hi(float i) {
  return  (i<=_p_1.w)?_p_1: (i<=_p_2.w)?_p_2: (i<=_p_3.w)?_p_3: (i<=_p_4.w)?_p_4: (i<=_p_5.w)?_p_5: _p_5;
}
/* end generated code */

vec3 palette(float i) {
    vec4 hi = _palette_hi(i);
    vec4 lo = _palette_lo(i);

    float i_p = clamp((i - lo.w) / (hi.w - lo.w), 0.0, 1.0);
    float i_pp = (1.0 - i_p);

    float xc = (lo.x*i_pp) + (hi.x*i_p);

    if (abs(hi.x - lo.x) > 0.5) {
        xc = ((lo.x + 1.0 + hi.x) / 2.0) - 1.0;
    }

    vec3 rlo = lo.xyz * i_pp;
    vec3 rhi = hi.xyz * i_p;
    vec3 r = rlo.xyz + rhi.xyz;
    r.x = xc;

    return r;
}

