    //q.x = q.z*0.1*cos(iTime);
    //q.x = q.x*.9;

    //return length(q) - 0.05;
    return length(q)-sin(iTime)/9.0-.3;
}


float trace(vec3 o, vec3 r)
{
    float t = 0.0;
    for(int i = 0; i < 32; i++) {
        vec3 p = o + r * t;
        float d = map(p);
        t += d * .5;
    }
    return t;
}

