void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 tex = texture(iChannel0, uv ).xyz; 	


	vec3 defaultColor = vec3(0.5, 1.0, 0.5);

    vec3 finalColor = defaultColor;
    float value = ceil(iTime / 10.0) - iTime / 10.0;
    vec2 aniuv = uv - value;
    finalColor *= abs (0.01 / sin((aniuv.y)) * 0.2 ) + 0.0;
    finalColor += tex * defaultColor;

	fragColor = vec4(finalColor, 1.0);
}

// Tweak seed values
#define INITIAL_POSITION_SEED 3.141592
#define RADIUS_SEED 2.0
#define RADIUS_MAX 0.2
#define METABALLS 15
#define METABALL_THRESHOLD 4.
#define THRESHOLD_ERROR 0.05


float hash(float seed)
{
	// Return a "random" number based on the "seed"
    return fract(sin(seed) * 43758.5453);
}

vec2 metaball_position( int metaball )
{
    float x = hash( pow(float(metaball),4.) + INITIAL_POSITION_SEED );
    float y = hash( x );
    float a = hash( x + y );
    float b = x + y + a;
    return vec2( x + 0.5 * cos( a * iTime ), hash( x ) + 0.5 * sin( b * iTime ) );
}

float metaball_radius( int metaball )
{
    float r = hash( pow(float(metaball),2.) + RADIUS_SEED );
    return min( r, RADIUS_MAX );
}

float metaball_function( in vec2 center, float radius, in vec2 p )
{
    vec2 r = p - center;
    //return radius / length(r); // 1 / r
    return (radius * radius) / dot( r,r ); // 1 / r^2

    //R / ( |x-x0| + |y-y0| )
    //return radius / ( abs(r.x) + abs(r.y) );

    // try other functions
	// (1-r^2)^2
    //r^4 - r^2 + 0.25
	//6r^5 - 15r^4 + 10r^3
}

// debugging purposes
float disk( in vec2 center, float radius, in vec2 p )
{
    return 1. - smoothstep( radius - 0.01, radius + 0.01, length( p - center ) );
    //return 1. - step( radius, length( p - center ) );
}

