void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y;

	fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
    //fragColor = vec4( 1. );
    float f = 0.;
    float t = 0.;
    for( int i = 0; i < METABALLS; ++i )
    {
        vec2 mp = metaball_position( i );
        float mr = metaball_radius( i );
        f += disk( mp, mr, uv );
        t += metaball_function( mp, mr, uv );
    }

    float e = smoothstep( METABALL_THRESHOLD - THRESHOLD_ERROR, METABALL_THRESHOLD + THRESHOLD_ERROR, t );
    if( e > 0. && e < 1. )
    {
        fragColor = vec4( 0.0 );
        return;
    }

    f = clamp( f, 0., 1. );
    fragColor = fragColor * f + (1.-f) * vec4( 1. );
}

