            //float phi = 3.1415926 * 2.0 * (iTime * 0.1 + r.z);
            float phi = 3.1415926 * 2.0 * (iTime * 0.1 + hash(fl + offset).x);
            vec2 pos = 0.5 + 0.5 * vec2(cos(phi), sin(phi));
            float d = length(fr - (pos + offset));
