vec3 gridnoise(vec2 v)
{
    //float s = 0.01; // NOTE: this setting causes the horizontal-line-artifacts (thanks, iq!)
    float s = 1.0 / 256.0;
    vec2 fl = floor(v);
    vec2 fr = fract(v);
    vec2 nearest = vec2(0.0, 0.0);
    float mindist = 1.0;
    for(float y = -1.0; y <= 1.0; y++)
    {
        for(float x = -1.0; x <= 1.0; x++)
        {
            vec2 offset = vec2(x, y);
    		//vec4 r = texture(iChannel0, (fl + offset) * s + iTime * 0.001);
            //vec2 pos = r.xy;
            //vec4 r = texture(iChannel0, (fl + offset) * s);
            //float phi = 3.1415926 * 2.0 * (iTime * 0.1 + r.z);
            float phi = 3.1415926 * 2.0 * (iTime * 0.1 + hash(fl + offset).x);
            vec2 pos = 0.5 + 0.5 * vec2(cos(phi), sin(phi));
            float d = length(fr - (pos + offset));
            if(d < mindist)
            {
                mindist = d;
                nearest = offset;
            }
        }
    }

    return vec3(fl + nearest, mindist);
}

