float whiteStreak(vec2 p1, vec2 p2, vec2 point) {
    float radius =0.2;
    float AA = 0.1;
    float a = p1.y-p2.y;
    float b = p2.x-p1.x;
    float distance =  abs(a*point.x+b*point.y+p1.x*p2.y-p2.x*p1.y) / sqrt(a*a+b*b);
    return smoothstep (radius/2.,radius/2.-AA,distance);

}

float buildColorRegion(vec2 p1, vec2 p2, vec2 point) {
