    	vec2 a = mirror((iTime - i * .2) * speed0);
    	vec2 b = mirror((iTime - i * .2) * speed1);
    	float d = PointLineSegDist2d(a, b - a, I);
		o = max(o, vec4(1.0 - smoothstep(0.0, 0.01, d))); // lines
