float simpleBitmap(float data, float w, float h, vec2 bitCoord) {
    // 0..1.0
    float x = floor(bitCoord.x / (1.0 / w));
    float y = floor(bitCoord.y / (1.0 / h));

    float i = y * w + x;

    float datum = float(data) / pow(2.0, i);

    datum = mod(datum, 2.0);

    return floor(datum);
}

float digitBitmaps(const in int x) {
	return x==0?961198.0:x==1?279620.0:x==2?953902.0:x==3?953998.0:x==4?700040.0:x==5?929422.0:x==6?929454.0:x==7?952456.0:x==8?962222.0:x==9?962184.0:0.0;
}

// resolves sprite texture data for entire uv space
vec3 resolveSpriteUV(const in int x, vec2 uv) {
    if (x < 10) {
        return simpleBitmap(digitBitmaps(x), 4.0, 5.0, uv) == 1.0 ? vec3(1.0, 1.0, 1.0) : vec3(-1.0, -1.0, -1.0);
    }
    return vec3(0.0, 0.0, 0.0);
}

vec3 resolveScaledSprite(vec2 pos, vec2 size, vec2 uv, float rotz, int sprite) {
    vec2 tl = pos - (size/2.0);
    vec2 br = tl + size;

    vec2 uvr = uv - tl - (size/2.0);
   	uvr = vec2(
        cos(rotz) * uvr.x - sin(rotz) * uvr.y,
        sin(rotz) * uvr.x + cos(rotz) * uvr.y
	);
    uvr += tl+size/2.0;

    // if this uv coordinate is within the sprites projected uv coordinates
    if ((uvr.x >= tl.x) && (uvr.y >= tl.y) && (uvr.x < br.x) && (uvr.y < br.y)) {
        vec2 sprite_uv = (uvr - tl) / size;
        return resolveSpriteUV(sprite, sprite_uv);
    }
    return vec3(-1.0, -1.0, -1.0);
}

vec3 spriteMix(vec3 a, vec3 b) {
    if (b.x == -1.0) {
        return a;
    }
