    vec2 p2 = vec2(rand(2.0, 0.0), rand(2.0, 1.0));

    float t = 0.5 + 0.5 * sin(PI * (fract(iTime) - 0.5));

    vec2 p01 = (1.0 - t) * p0 + t * p1;
    vec2 p12 = (1.0 - t) * p1 + t * p2;

	vec2 p = fragCoord.xy / iResolution.xy;

	float h = smoothstep(0.04, 0.025, length(p - p01));
    float g = smoothstep(0.03, 0.015, length(p - p12));

    vec3 v = BLUE * h + PINK * g + BACK * (1.0 - clamp(h - g, 0.0, 1.0)) ;

    fragColor = vec4(v,1.0);
}

