bool isPrime(float n)
{
    n=floor(n);
    if(n==2.)return true;
    if (n==1.||mod(n,2.)==.0)return false;
    for(float i=3.;i<=6000.;i+=2.)
    {
        if(i*i>n)
            break;
        if(mod(n,i)==.0)
            return false;
    }
    return true;
}

