#define EDGE_WIDTH 0.15

vec2 castRay(in vec3 ro, in vec3 rd, in float maxd, inout float hmin) {
    closest = 9999.; // reset closest trap
	float precis = .0006, h = EDGE_WIDTH+precis, t = 2., m = -1.;
    hmin = 0.;
    for( int i=0; i<RAY_STEP; i++) {
        if( abs(h)<t*precis || t>maxd ) break;
        t += h;
	    vec2 res = map(ro+rd*t);
        if (h < EDGE_WIDTH && res.x > h + 0.001) {
			hmin = 10.0;
		}
        h = res.x;
	    m = res.y;
    }

	//if (hmin != h) hmin = 10.;
    if( t>maxd ) m = -200.0;
    return vec2( t, m );
}

float softshadow( in vec3 ro, in vec3 rd, in float mint, in float maxt, in float k) {
	float res = 1.0;
    float t = mint;
    for( int i=0; i<26; i++ ) {
		if( t>maxt ) break;
        float h = map( ro + rd*t ).x;
        res = min( res, k*h/t );
        t += h;
    }
    return clamp( res, 0., 1.);
}

// normal with kali edge finder
float Edge=0.;
vec3 calcNormal(vec3 p, vec3 rd, float t) { 
    float pitch = .2 * t / iResolution.x; 
	pitch = max( pitch, .015 );

	vec3 e = vec3(0.0,2.*pitch,0.0);
	float d1=map(p-e.yxx).x,d2=map(p+e.yxx).x;
	float d3=map(p-e.xyx).x,d4=map(p+e.xyx).x;
	float d5=map(p-e.xxy).x,d6=map(p+e.xxy).x;
	float d=map(p).x;

	Edge=abs(d-0.5*(d2+d1))+abs(d-0.5*(d4+d3))+abs(d-0.5*(d6+d5)); //edge finder
	Edge=min(1.,pow(Edge,.55)*15.);

    vec3 grad = vec3(d2-d1,d4-d3,d6-d5);
	return normalize(grad - max(.0,dot (grad,rd ))*rd);
}




float calcAO( in vec3 pos, in vec3 nor) {
	float totao = 0.0;
    float sca = 1.0;
    for( int aoi=0; aoi<5; aoi++ ) {
        float hr = 0.01 + 0.05*float(aoi);
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos ).x;
        totao += -(dd-hr)*sca;
        sca *= .75;
    }
    return clamp( 1.0 - 4.0*totao, 0.0, 1.0 );
}

vec3 mandelbrot(in vec2 uv, vec3 col) {
    uv.x += 1.5;
    uv.x = -uv.x;

    float a=.05*sqrt(abs(Anim)), ca = cos(a), sa = sin(a);
    mat2 rot = mat2(ca,-sa,sa,ca);
    uv *= rot;
	float kk=0., k = abs(.15+.01*Anim);
    uv *= mix(.02, 2., k);
	uv.x-=(1.-k)*1.8;
    vec2 z = vec2(0);
    vec3 c = vec3(0);
    for(int i=0;i<50;i++) {
        if(length(z) >= 4.0) break;
        z = vec2(z.x*z.x-z.y*z.y, 2.*z.y*z.x) + uv;
        if(length(z) >= 4.0) {
            kk = float(i)*.07;
            break; // does not works on some engines !
        }
    }
    return clamp(mix(vec3(.1,.1,.2), clamp(col*kk*kk,0.,1.), .6+.4*Anim),0.,1.);
}

vec3 screen(in vec2 uv, vec3 scrCol) {
    // tv effect with horizontal lines and color switch
    vec3 oricol = mandelbrot(vec2(uv.x,uv.y), scrCol);
    vec3 col;
	float colorShift = .2*cos(.5*iTime);
    col.r = mandelbrot(vec2(uv.x,uv.y+colorShift), scrCol).x;
    col.g = oricol.y;
    col.b = mandelbrot(vec2(uv.x,uv.y-colorShift), scrCol).z;

	uv *= Rot3;	
	col =(.5*scrCol+col)*(.5+.5*cos(iTime*5.))*cos(iTime*10.+40.*uv.y);  
    return col*col;
}

// clean lines on the ground
float isGridLine(vec2 p, vec2 v) {
    vec2 k = smoothstep(.1,.9,abs(mod(p+v*.5, v)-v*.5)/.08);
    return k.x * k.y;
}

vec3 render( in vec3 ro, in vec3 rd, inout float hmin) { 
    // Test bounding sphere (optim)
    WithChameleon = intersectSphere(ro,rd,vec3(-.5,1.65,0),3.15); //2.95);

    vec2 res = castRay(ro,rd,60.0, hmin);
    float distCham = abs(closest);

#ifdef SHOW_EDGES
     if( res.y>-150.)  {
           vec3 pos = ro + res.x*rd;
     	vec3 nor = calcNormal(pos, rd, res.x);
     }
    return vec3(1);
#else

    float t = res.x;
	float m = res.y;
    vec3 cscreen = vec3(sin(.1+1.1*iTime), cos(.1+1.1*iTime),.5);
    cscreen *= cscreen;

    vec3 col;
	float dt;
    float glow = 1.-smoothstep(Anim + cos(iTime),.9+1.15,2.2);
    glow *= step(.3, hash(iTime)); //floor(.01+10.5*iTime)));

    if( m>-150.)  { 
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal(pos, rd, t);

        if( m>0. ) { // Chameleon
			col = vec3(.4) + .35*cscreen + .3*sin(1.57*.5*iTime + vec3(.05,.09,.1)*(m-1.) );
#ifdef NOISE_SKIN
            nor = doBumpMap(iChannel0, pos*.5, nor, 0.05);
#endif            
        } else if (m<-104.5) {  // bottom of screen
            col = vec3(.92);
            dt = dot(normalize(pos-vec3(-4,-4,0)), vec3(0,0,-1));
            col += (dt>0.) ? (.75*glow+.3)*dt*cscreen: vec3(0); 
        } else if (m<-102.5) {
           	if (pos.z<0.) { // screen
            	col = screen(pos.xy,cscreen);
                col += 20.*glow*col;
            } else { // back of screen
                col = vec3(.92);
            	distCham *= .25; // Hack for chameleon light on screen
            }
        } else if (m<-101.5) {
            col = .2+3.5*cscreen*glow;

        } else if(m<-100.5) {  // Ground
            float f = mod( floor(2.*pos.z) + floor(2.*pos.x), 2.0);
            col = 0.4 + 0.1*f*vec3(1.);
            col = .1+.9*col*isGridLine(pos.xz, vec2(2.));
            dt = dot(normalize(pos-vec3(-4,-4,0)), vec3(0,0,-1));
 			col += (dt>0.) ? (.75*glow+.3)*dt*cscreen: vec3(0);     
    		//col = clamp(col,0.,1.);
        } else {  // Screen
            col = vec3(.92);
            distCham *= .25; // Hack for chameleon light on screen
        }

        float ao = calcAO( pos, nor );

		vec3 lig = normalize( vec3(-0.6, 0.7, -0.5) );
		float amb = clamp( 0.5+0.5*nor.y, 0.0, 1.0 );
        float dif = clamp( dot( nor, lig ), 0.0, 1.0 );
        float bac = clamp( dot( nor, normalize(vec3(-lig.x,0.0,-lig.z))), 0.0, 1.0 )*clamp( 1.0-pos.y,0.0,1.0);

		float sh = 1.0;
		if( dif>0.02 ) { 
            WithChameleon = intersectSphere(pos,lig,vec3(-.5,1.65,0),2.95);
            sh = softshadow( pos, lig, 0.02, 13., 8.0 ); 
            dif *= sh; 
        }

		vec3 brdf = vec3(0.0);
		brdf += 1.80*amb*vec3(0.10,0.11,0.13)*ao;
        brdf += 1.80*bac*vec3(0.15,0.15,0.15)*ao;
        brdf += 0.8*dif*vec3(1.00,0.90,0.70)*ao;

		float pp = clamp( dot( reflect(rd,nor), lig ), 0.0, 1.0 );
		float spe = 1.2*sh*pow(pp,16.0);
		float fre = ao*pow( clamp(1.0+dot(nor,rd),0.0,1.0), 2.0 );

		col = col*brdf*(.5+.5*sh) + vec3(.25)*col*spe + 0.2*fre*(0.5+0.5*col);

        float rimMatch =  1. - max( 0. , dot( nor , -rd ) );
        col += vec3((.1+cscreen*.1 )* pow( rimMatch, 10.));
	}

	col *= 2.5*exp( -0.01*t*t );
    float BloomFalloff = 15000.; //mix(1000.,5000., Anim);
 	col += .5*glow*cscreen/(1.+distCham*distCham*distCham*BloomFalloff);

	return vec3( clamp(col,0.0,1.0) );
#endif    
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // animation
    float GlobalTime = iTime; // + .1*hash(iTime);

    Anim = clamp(5.6*cos(GlobalTime)*cos(4.*GlobalTime),-2.5,1.2);
    ca3 = cos(.275+.006*Anim); sa3 = sin(.275+.006*Anim);   
	Rot3 = mat2(ca3,-sa3,sa3,ca3);
    float a=.1+.05*Anim, ca = cos(a), sa = sin(a);
    Rotanim = mat2(ca,-sa,sa,ca);
    float b = mod(GlobalTime,12.)>10.?cos(8.*GlobalTime):.2*cos(4.*GlobalTime), cb = cos(b), sb = sin(b);
    Rotanim2 = mat2(cb,-sb,sb,cb);
    float time = 17. + /*14.5 +*/ GlobalTime;

    // mouse
    vec2 mo = iMouse.xy/iResolution.xy;

    // camera	
    float dist = 13.;
    vec3 ro = vec3( -0.5+dist*cos(0.1*time + 6.0*mo.x), 3.5 + 10.0*mo.y, 0.5 + dist*sin(0.1*time + 6.0*mo.x) );
    vec3 ta = vec3( -3.5, .5, 0. );

    // camera tx
    vec3 cw = normalize( ta-ro );
    vec3 cp = vec3( 0.0, 1.0, 0.0 );
    vec3 cu = normalize( cross(cw,cp) );
    vec3 cv = normalize( cross(cu,cw) );

    // render
    vec3 colorSum = vec3(0);
    int nbSample = 0;

 #if (ANTIALIASING == 1)	
	int i=0;
#else
	for (int i=0;i<ANTIALIASING;i++) {
#endif
        float randPix = 0.; //hash(iTime); // Use frame rate to improve antialiasing ... not sure of result
		vec2 subPix = .4*vec2(cos(randPix+6.28*float(i)/float(ANTIALIASING)),
                              sin(randPix+6.28*float(i)/float(ANTIALIASING)));
		//vec3 ray = Ray(2.0,fragCoord.xy+subPix);
		vec2 q = (fragCoord.xy+subPix)/iResolution.xy;
		//vec2 q = (fragCoord.xy+.4*vec2(cos(6.28*float(i)/float(ANTIALIASING)),sin(6.28*float(i)/float(ANTIALIASING))))/iResolution.xy;
        vec2 p = -1.0+2.0*q;
        p.x *= iResolution.x/iResolution.y;
        vec3 rd = normalize( p.x*cu + p.y*cv + 2.5*cw );

        nbSample++;
        float hmin = 100.;
        colorSum += sqrt(render( ro, rd, hmin));

#ifdef SHOW_EDGES
 		colorSum = vec3(1);
        if (Edge>0.3) colorSum = vec3(.6);  
        if (hmin>0.5) colorSum = vec3(0,0,0);   
        break;
#endif

#if (ANTIALIASING > 1)
        // optim : use antialiasing only on objects edges //exit if far from objects
        if (Edge<0.3 && hmin<0.5 ) break;
	}
#endif

    fragColor = vec4(colorSum/float(nbSample), 1.);
}

// CC0 1.0
// @sagzorz

// NOTE: if you are new to SDFs, do @cabbibo's tutorial first!!!
// 
// @cabbibo's original SDF tutorial --> https://www.shadertoy.com/view/Xl2XWt
// my original hacked up shader --> https://www.shadertoy.com/view/4d33z4

// this is a clean/from scratch re-implementation of my first shdaer/sdf,
// which was based on @cabbibo's awesome SDF tutorial
// also used functions from iq's super handy page about distance functions
// http://iquilezles.org/www/articles/distfunctions/distfunctions.htm
// resstructured to be closer to iq's Raymarching Primitives example
// https://www.shadertoy.com/view/Xds3zN

// NOW PROPERLY MARCHING THE RAY!
// (was using silly hack in original version to compensate for twist artifacts)
// Performs much better than old version

// the sd functions below are the same as from iq's page (link above)
// though when I wrote this version I derived from scratch as much as I could on my own 
// by thinking/sketching on paper etc. 
// The comments explain my interpretation of the funcs

// for all signed distance functions sd*() below,
// input p --> is ray position, where the object is at the origin (0,0,0)
// output float is distance from ray position to surface of sphere
// 	positive means outside of sphere
// 	negative means ray is inside
// 	0 means its exactly on the surface


// ~~~~~~~ signed fistance fuction for sphere
// input r --> is sphere radius
// pretty simple, just compare point to radius of sphere
float sdSphere(vec3 p, float r)
{
    return length(p) - r;
}

// ~~~~~~~ signed distance function for box
// input s -- > is box size vector (all postive values)
//
// the key to simply calcualting distance to surface to box is to first 
// force the ray position into the first octant (all positive values)
// this massively simplifies the math and is ok since distance to surf
// on a box is the same in the - or + direction on a given axis
// simple to figure by once you sketch out 2D equivalent problem on papaer
// 2D ex: distance to box of size (2,1) 
// for p of (-3,-2) == (-3, 2) == (3, -2) == (3, 2)
//
// now that all the coordinates are "normalized"/positive, its much easier,
// the next part is to figure out the diff between the box surface the and p
// a bit like the sphere function were you do p - "shape size", but
// you clamp the result to >0, done below by using max() with 0
// i'm having trouble putting this into words corretcly, but it was really easy
// to understand once I sketched out a rect and points on paper, 
// that was enough for me to be able to derive the 3D version 
//
// the last part is to account for is p is insde the box, 
// in which case we need to return a negative value
// for that value, its a simple check of which side is the closest
float sdBox(vec3 p, vec3 s)
{
    vec3 diffVec = abs(p) - s;
    float surfDiff_Outter = length(max(diffVec,0.0));
    float surfDiff_Inner = min( max(diffVec.z,max(diffVec.x,diffVec.y)),0.0);
    return surfDiff_Outter + surfDiff_Inner;              
}
/*
// Minimial IQ version
float sdBox( vec3 p, vec3 s )
{
  vec3 d = abs(p) - s;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}
*/

// ~~~~~~~ signed distance function for torus
// input t --> torus specs where:
// 	t.x = torus circumference
//	t.y = torus thickness
//  
// think of the torus as circles wrappeed around 1 large cicle (perpendicular)
// first flatten the y axis of p (by using p.xz) and get the distance to 
// the torus circumference/core/radius which is flat on the y axis
// then simply subtract the torus thickenss from that 
float sdTorus(vec3 p, vec2 t)
{
    float distPtoTorusCircumference = length(vec2( length(p.xz)-t.x , p.y));
    return distPtoTorusCircumference - t.y;
}
/*
// IQ version
float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

*/

// ~~~~~~~ smooth minimum function (polynomial version) from iq's page
// http://iquilezles.org/www/articles/smin/smin.htm
// input d1 --> distance value of object a
// input d1 --> distance value of object b
// output --> smoothed/blended output
float smin( float d1, float d2)
{
    float k = 0.6521;
    float h = clamp( 0.5+0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h);
}

// ~~~~~~~ distance deformation, blends 2 shapes based on their distances
// input d1 --> distance of object 1
// input d2 --> distance of object 2
// output --> blended object
float opBlend( float d1, float d2)
{    
    return smin( d1, d2 );
}

// ~~~~~~~ domain deformation, twists the shape
// input p --> original ray position
// input t --> twist scale factor
// output --> twisted ray position
// 
// need more max itterations on ray march for stronger/bigger domain deformation
vec3 opTwist( vec3 p, float t, float yaw )
{
    float  c = cos(t * p.y + yaw);
    float  s = sin(t * p.y + yaw);
    mat2   m = mat2(c,-s,s,c);
    return vec3(m*p.xz,p.y);
}

// ~~~~~~~ do Union / combine 2 sd objects
// input vec2 --> .x is the distance, .y is the object ID
// returns the closest object (basically does a min() but we use if() 
vec2 opU(vec2 o1, vec2 o2)
{
    if(o1.x < o2.x)
        return o1;
    else 
        return o2;
}

// ~~~~~~~ map out the world
// input p --> is ray position
// basically find the object/point closest to the ray by
// checking all the objects with respect to p
// move objects/shapes by messing with p
vec2 map(vec3 p)
{
    // results container
    vec2 res;    

    // define objects
    	// sphere 1
    	// sphere: radius, orbit radius, orbit speed, orbit offset, position
    float sR = 1.359997;
    float sOR = 2.666662;
    float sOS = 0.85;
    vec3 sOO = vec3(2.66662,0.0,0.0);
    vec3 sP = p - (sOO + vec3(sOR*cos(sOS*iTime),sOR*sin(sOS*iTime),0.0));
    vec2 sphere_1 = vec2( sdSphere(sP,sR), 1.0 );

    	//	torus 1    
    vec2 torusSpecs = vec2(1.6, 0.613333);
    float twistSpeed = 0.35;
    float twistPower = 5.0*sin(twistSpeed * iTime);
    	// to twist the torus (or any object), we actually distort p/space (domain) itself,
    	// this then gives us a distorted view of the object
    vec3 torusPos = vec3(0.0);
    vec3 distortedP = opTwist(p - torusPos, twistPower, 0.0) ;
        // 	domain distortion correction:
        // 	needed to find this by hand, inversely proportional to domain deformation
    float ddc = 0.25;
    vec2 torus_1 = vec2(ddc * sdTorus(distortedP, torusSpecs), 2.0);

    // combine and blend objects
    res = opU( sphere_1, torus_1 );
    res.x = opBlend( sphere_1.x, torus_1.x );    
    //res.x = torus_1.x;

    return res;
}

// ~~~~~~~ cast/march ray through the word and see what it hits
// input ro --> ray origin point/position
// input rd --> ray direction
// output is vec2 where
// 	.x = distance travelled by ray
// .y = hit object's ID
//
vec2 castRay( vec3 ro, vec3 rd)
{
	// variables used to control the marching process
    const int maxMarchCount = 300;
    float maxRayDistance = 20.0;
    float minPrecisionCheck = 0.001;

    float t = 0.0; // travelled distance by ray
    float id = -1.0; // object ID, default of -1 means background

    for(int i = 0; i < maxMarchCount; i++)
    {
        // get closest object to current ray position
        vec2 res = map(ro + rd*t);

        // stop itterating/marching once either we've past max ray length 
        // or
        // once we're close enough to an object (defined by the precision check variable)
       	if(t > maxRayDistance || res.x < minPrecisionCheck)
           break;

        // move ray forward by distance to closet object, see
        // http://http.developer.nvidia.com/GPUGems2/elementLinks/08_displacement_05.jpg
        t += res.x; 
        id = res.y;
    }

    // if ray goes beyond max distance, force ID back to background one
    if(t > maxRayDistance)
        id = -1.0;

    return vec2(t, id);
}

// ~~~~~~ calculate normal of closest objects surface given a ray position
// input p --> ray position (calculated previously from ray cast position, no iteration now
// output --> surface normal vector
//
// gets the surface normal by sampling neaby points and getting direction of diffs

vec3 calculateNormal(vec3 p)
{
    float normalEpsilon = 0.0001;
    vec3 eps = vec3(normalEpsilon,0,0);
    vec3 normal = vec3( map(p + eps.xyy).x - map(p - eps.xyy).x,
                       	map(p + eps.yxy).x - map(p - eps.yxy).x,
                       	map(p + eps.yyx).x - map(p - eps.yyx).x
                       );
    return normalize(normal);
}

// ~~~~~~~ render pixel --> find closest surface and apply color accordingly
// input ro --> pixel's ray original position
// input rd --> pixel's ray direction
// output --> pixel color
vec3 render(vec3 ro, vec3 rd)
{
    vec3 bkgColor = vec3(0.75);
    vec3 light = normalize( vec3(1.0, 4.0, 3.0) );
    vec3 objectColor_1 = vec3(1.0, 0.0, 0.0);
    vec3 objectColor_2 = vec3( 0.25 , 0.95 , 0.25 );
    vec3 objectColor_3 = vec3(0.12, 0.12, 0.9);
    vec3 ambientLightColor = vec3( 0.3 , 0.1, 0.2 );

    vec2 res = castRay(ro, rd);
    float t = res.x;
    float id = res.y;

    // hard set pixel value if its a background one
    if(id == -1.0)
    	return bkgColor;
    else
    {
        // calculate pixel normal
        vec3 pos = ro + t*rd;
        vec3 normal = calculateNormal(pos);
        vec3 objectColor = vec3(1);

        if(id == 1.0)
            objectColor = objectColor_1;
        else if(id == 2.0)
            objectColor = objectColor_2;
        else if(id == 3.0)
            objectColor = objectColor_3;

        float surf = clamp(dot(normal, light), 0.0, 1.0);
        vec3 pixelColor = objectColor * surf + ambientLightColor;
		return pixelColor;            
    }
}

// ~~~~~~~ creates camera matrix used to transform ray point/direction
// input camPos --> camera position
// input targetPos --> look at target position
// input roll --> how much camera roll
// output --> camera matrix used to transform
mat3 setCamera( in vec3 camPos, in vec3 targetPos, float roll )
{
	vec3 cw = normalize(targetPos - camPos);
	vec3 cp = vec3(sin(roll), cos(roll),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    // get pixel (range from -1.0 to 1.0)
    vec2 p = ( -iResolution.xy + 2.0 * fragCoord.xy ) / iResolution.y;

    // camera stuff
    float camOrbitSpeed = 0.5;
    float camOrbitRadius = 7.3333;
    float camPosX = camOrbitRadius * cos( camOrbitSpeed * iTime);
    float camPosZ = camOrbitRadius * sin( camOrbitSpeed * iTime);
    vec3 camPos = vec3(camPosX, 0.5, camPosZ);
    vec3 lookAtTarget = vec3(0.0);
    mat3 camMatrix = setCamera(camPos, lookAtTarget, 0.0);

    // determines ray direction based on camera matrix
    // "lens length" seems to be related to field of view / ray divergence
    float lensLength = 2.0;
    vec3 rd = camMatrix * normalize( vec3(p.xy,2.0) );
    vec3 col = render(camPos,rd);

	fragColor = vec4(col,1.0);
}

#define PI 3.14159
#define MAXSTEPS 256
#define EPSILON 0.001
#define AMBIENT 0.2
#define MAXDEPTH 100.0
#define SHADOWDEPTH 32.0
#define SOFTSHADOWFAC 32.0
#define FRACTALITERATIONS 16
#define AOSAMPLES 5.0

const vec3 lightCol = vec3(0.7, 0.5, 0.8);
const vec3 diffuse = vec3(1, 1, 1);

vec3 lightDir;

struct Camera
{
	vec3 pos;
	vec3 dir;
} cam;

// Rotation / Translation of a point p with rotation r
vec3 rotate( vec3 p, vec3 r )
{
	r.x *= PI/180.0;
	r.y *= PI/180.0;
	r.z *= PI/180.0;

	mat3 xRot = mat3 (	1,	0,				0,
						0,	cos(r.x),	-sin(r.x),
						0,	sin(r.x),	cos(r.x) );
	mat3 yRot = mat3 ( 	cos(r.y),		0,	sin(r.y),
						0,					1,	0,
						-sin(r.y),		0,	cos(r.y) );
	mat3 zRot = mat3 (	cos(r.z),	-sin(r.z),	0,
						sin(r.z),	cos(r.z),	0,
						0,				0,				1 );
	return xRot * yRot * zRot * p;
}

// give the distance to a plane from a point p and normal n, shifted by y
float distPlane( vec3 p, vec3 n, float y )
{
	return dot(p-vec3(0,y,0),n);
}

// http://blog.hvidtfeldts.net/index.php/2011/08/distance-estimated-3d-fractals-iii-folding-space/
float distFrac(vec3 p)
{
	const float scale = 1.8;
	const float offset = 2.0;

	for(int n=0; n< FRACTALITERATIONS; n++)
	{
		p.xy = (p.x+p.y < 0.0) ? -p.yx : p.xy;
		p.xz = (p.x+p.z < 0.0) ? -p.zx : p.xz;
		p.zy = (p.z+p.y < 0.0) ? -p.yz : p.zy;

		p = scale*p-offset*(scale-1.0);
	}

	return length(p) * pow(scale, -float(FRACTALITERATIONS));
}

// return positive values when outside and negative values inside,
// --> distance of the nearest surface.
float distanceField(vec3 p)
{
	//Rotate scene around y-axis
	vec3 rotP = rotate(p, vec3(0, mod(iTime*20.0, 360.0),0));
	rotP = rotate(rotP-vec3(0,-0.6,0), vec3(0, 45,55));


	float dPlane = distPlane(p, vec3(0,1,0), -2.0);
	float dFrac = distFrac(rotP);
	return min(dFrac, dPlane);
}

// marching along the ray at step sizes, 
// and checking whether or not the surface is within a given threshold
vec4 raymarch(vec3 rayOrigin, vec3 rayDir)
{
	float totalDist = 0.0;
	for(int j=0; j<MAXSTEPS; j++)
	{
		vec3 p = rayOrigin + totalDist*rayDir;
		float dist = distanceField(p);
		if(abs(dist)<EPSILON)	//if it is near the surface, return an intersection
		{
			return vec4(p, 1.0);
		}
		totalDist += dist;
		if(totalDist>=MAXDEPTH) break;
	}
	return vec4(0);
}

// Approximates the (normalized) gradient of the distance function at the given point.
// If p is near a surface, the function will approximate the surface normal.
vec3 getNormal(vec3 p)
{
	float h = 0.0001;
	return normalize(vec3(
		distanceField(p + vec3(h, 0, 0)) - distanceField(p - vec3(h, 0, 0)),
		distanceField(p + vec3(0, h, 0)) - distanceField(p - vec3(0, h, 0)),
		distanceField(p + vec3(0, 0, h)) - distanceField(p - vec3(0, 0, h))));
}

// calculate shadow, ro=origin, rd=dir
// look for nearest point when raymarching, factor k gives smoothnes, 2=smooth, 128=hard
// dl is distance to light, so only return if distance is smaller
float shadow(vec3 ro, vec3 rd, float k)
{
	float res = 1.0;
    float t = EPSILON;
    for( int i=0; i<1000; i++)
    {
        float h = distanceField(ro + rd*t);
        if( h<EPSILON )
            return AMBIENT;
        res = min( res, k*h/t );
        t += h;
        if(t>=SHADOWDEPTH) break;
    }
    return res;
}

//calculate ambient occlusion
float ambientOcclusion(vec3 p, vec3 n)
{
	float res = 0.0;
	float fac = 1.0;
	for(float i=0.0; i<AOSAMPLES; i++)
	{
		float distOut = i*0.3;	//go on normal ray AOSAMPLES times with factor 0.3
		res += fac * (distOut - distanceField(p + n*distOut));	//look for every step, how far the nearest object is
		fac *= 0.5;	//for every step taken on the normal ray, the fac decreases, so the shadow gets brighter
	}
	return 1.0 - clamp(res, 0.0, 1.0);
}

//calculate the color, the shadow, the lighting for a position
vec3 shading(vec3 pos, vec3 rd, vec3 n)
{
	vec3 light = max(AMBIENT, dot(n, lightDir)) * lightCol;	//lambert light with light Color
	light *= diffuse;	//diffuse lighting, area lit lighting
	light *= shadow(pos, lightDir, SOFTSHADOWFAC);	//add shadow
	light += ambientOcclusion(pos, n) * AMBIENT;
	return light;
}

vec4 renderImage()
{
	float fov = 60.0;
	float tanFov = tan(fov / 2.0 * 3.14159 / 180.0) / iResolution.x;
	vec2 p = tanFov * (gl_FragCoord.xy * 2.0 - iResolution.xy);

	cam.pos = vec3(0,0,-8);
	cam.dir = normalize(vec3( p.x, p.y, 1 ));

    lightDir = normalize(vec3(-1,0.8,-1));

	vec4 res;

	res = raymarch(cam.pos, cam.dir);
	res.xyz = (res.a==1.0) ? clamp(shading(res.xyz, cam.dir, getNormal(res.xyz)), 0.0, 1.0) : vec3(0);

	return vec4(res.xyz, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	fragColor = renderImage();
}

