// Some work I did recently in collaboration with Beibei Wang.
//
// This is follow up work to Morgan McGuire's excellent blog post on best practices in terrain
// rendering:
// http://casual-effects.blogspot.co.uk/2014/04/fast-terrain-rendering-with-continuous.html
//
// The problem we address is that the transitions between lods are not perfect. there are verts
// in a LOD level that are located in the middle of an edge in the next LOD. this can lead to
// artifacts in the render. to combat this one can manually sample at both LOD levels and
// then interpolate the result. this still doesn't get a 100% accurate result - to get a vert
// to match the middle of an edge, it needs to take the average of the 2 verts that are connected
// by the edge, not the average of all 4 surrounding verts.
//
// We eliminate this issue by gradually transitioning the vert layout. just before LODs change,
// the verts match exactly. this means that one can simply do a single trilinear sample from the
// terrain data texture and know the the transition will be 100% smooth.
//
// Most of the complexity in this shader comes from drawing a mesh. the novel code here
// is small and concise - see the lod transition function smoothTransVert and surrounding
// comments. this can be added to a vertex shader, leading to a very nice, simple and robust
// terrain renderer. 
//

bool doSmoothLods;

float vert_spacing_at_1m = 0.1;
float min_vert_spacing = exp2(-5.);
// interpolation to next lod
float frac_high;

float line( vec2 p, vec2 start, vec2 end, float wp );
void smoothTransVert( inout vec2 v, float ss );

#define SCALE 10.
vec2 uvToWorld( vec2 uv );

// this function just generates geometry - not necessary with a mesh
void emit( vec2 uv, vec2 v0, float ss, inout float l )
{
    vec2 v1 = v0+vec2(ss,0.);
    vec2 v2 = v0+vec2(0.,ss);
    vec2 v3 = v0+vec2(ss,ss);

    bool odd = mod( v0.y, 2. * ss ) > 0.25*ss;
    if( mod( v0.x, 2. * ss ) > 0.25*ss )
        odd = !odd;

    // the magic sauce to transition lod levels is in this fn
    smoothTransVert( v0, ss );
    smoothTransVert( v1, ss );
    smoothTransVert( v2, ss );
    smoothTransVert( v3, ss );

    float w = 3.;
    l = max( l, line( uv, v0, v1, w ) );
   	l = max( l, line( uv, v2, v3, w ) );
    l = max( l, line( uv, v0, v2, w ) );
    l = max( l, line( uv, v1, v3, w ) );

    if( odd )
	    l = max( l, line( uv, v1, v2, w ) );
    else
	    l = max( l, line( uv, v0, v3, w ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    doSmoothLods = mod(iTime, 10.) < 5.;

	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uvToWorld( uv );

    vec2 camPos = vec2(SCALE/2.,SCALE/4.)+vec2(cos(iTime),sin(iTime));

    // player can posess detail center using mouse action
    if( iMouse.z > 0. )
        camPos = uvToWorld( iMouse.xy/iResolution.xy);

    float dist = max( abs(camPos.x-uv.x),abs(camPos.y-uv.y) );

    float desSS = max( dist * vert_spacing_at_1m, min_vert_spacing );
    float desSS2 = exp2( floor( log2( desSS ) ) );

    // this is pure hackery to make the output look like a mesh when smoothing is
    // NOT enabled (this can be knocked out for simplicity)
    if( !doSmoothLods )
    {
        // trial and error programming at its best
        vec2 camPos2 = camPos - mod( camPos, desSS2*2. );
        float dist2 = max( abs(camPos2.x-uv.x),abs(camPos2.y-uv.y) );
        if( 
            !(exp2(floor(log2(max(dist2*vert_spacing_at_1m, min_vert_spacing )))) > desSS2
           ||exp2(floor(log2(max((dist2+desSS2*2.)*vert_spacing_at_1m, min_vert_spacing )))) > desSS2 ))
            desSS2 /= 2.;
    }

    // blend factor between LODs
    frac_high = doSmoothLods ? clamp( desSS/desSS2-1.,0.,1.) : 1.;

    desSS = desSS2;

    vec2 uvf = desSS*floor(uv/desSS);
    vec2 v0 = uvf;

    float l = 0.;

    // emit the local neighbourhood of "quads" (sets of 2 triangles)
    emit( uv, v0, desSS, l );
    emit( uv, v0-vec2(desSS,0.), desSS, l );
    emit( uv, v0+vec2(desSS,0.), desSS, l );
    emit( uv, v0-vec2(0.,desSS), desSS, l );
    emit( uv, v0+vec2(0.,desSS), desSS, l );
    emit( uv, v0+vec2( desSS, desSS), desSS, l );
    emit( uv, v0+vec2(-desSS, desSS), desSS, l );
    emit( uv, v0+vec2( desSS,-desSS), desSS, l );
    emit( uv, v0+vec2(-desSS,-desSS), desSS, l );    

    fragColor.rgb = 1. - mix( vec3(.8), vec3(.1), l );
}

// this does the smooth transitions between lods. for each set of 4x4 patches
// as shown below, the close neighbour verts labelled N will be transitioned
// towards the center vert labelled C:
// x--x--x--x--x
// |  |  |  |  |
// x--N--N--N--x
// |  |  |  |  |
// x--N--C--N--x
// |  |  |  |  |
// x--N--N--N--x
// |  |  |  |  |
// x--x--x--x--x
void smoothTransVert( inout vec2 v, float desSS )
{
    // size of 4x4 patch
	float squareSize_4 = 4. * desSS;
    // vert fractional position in patch (0-1)
	vec2 m = fract(v/squareSize_4);
    // offset from center of patch (at 0.5 fractionally)
	vec2 offset = m - 0.5;
    // if offset is less than a radius of ~1 square, shift it towards the center
	float minRadius = 0.25*1.1; //1.1 is "epsilon" for numerical issues
	if( abs(offset.x) < minRadius ) v.x += offset.x*frac_high*squareSize_4;
	if( abs(offset.y) < minRadius ) v.y += offset.y*frac_high*squareSize_4;
    // thats it!
}

// distance from p to line from v to w. sorry i've lost the reference, if this looks
// like your code let me know and i'll cite you!
// iq gives a condensed version in the comments.
float line_d(vec2 p, vec2 v, vec2 w)
{
  // Return minimum distance between line segment vw and point p
  float l2 = dot(v-w, v-w);  // i.e. |w-v|^2 -  avoid a sqrt
  if (l2 == 0.0) return distance(p, v);   // v == w case
  // Consider the line extending the segment, parameterized as v + t (w - v).
  // We find projection of point p onto the line. 
  // It falls where t = [(p-v) . (w-v)] / |w-v|^2
  float t = dot(p - v, w - v) / l2;
  if (t < 0.0) return distance(p, v);       // Beyond the 'v' end of the segment
  else if (t > 1.0) return distance(p, w);  // Beyond the 'w' end of the segment
  vec2 projection = v + t * (w - v);  // Projection falls on the segment
  return distance(p, projection);
}
// line helper
float line( vec2 p, vec2 start, vec2 end, float wp )
{
    float d = line_d( p, start, end );
    float w = wp/iResolution.x;
    return smoothstep(w+5./iResolution.x,w,d);
}

vec2 uvToWorld( vec2 uv )
{
    uv.y *= iResolution.y/iResolution.x;
    uv *= SCALE;
    return uv;
}

float circleDist(vec2 p, float radius)
{
	return length(p) - radius;
}


vec2 translate(vec2 p, vec2 t)
{
	return p - t;
}


float boxDist(vec2 p, vec2 size, float radius)
{
	size -= vec2(radius);
	vec2 d = abs(p) - size;
  	return min(max(d.x, d.y), 0.0) + length(max(d, 0.0)) - radius;
}


float fillMask(float dist)
{
	return clamp(-dist, 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float between = abs(sin(iTime*0.5));//0.3;//clamp(iTime*0.0001, 0.0, 1.0);
    vec2 p = fragCoord.xy;
    float C = circleDist(translate(p, vec2(300, 200)), 90.0);
	float S =  boxDist(translate(p, vec2(300, 200)), vec2(90, 90), 	10.0);

    float d = (1.0 - between)*S+C*between;
    fragColor = vec4(vec3(d),1.0);//mix(vec4(0), vec4(1), fillMask(d));//
}

vec3 drawHolo(vec2 pos,vec2 uv, float range,float power,float aspect){
 	uv.x*=aspect; 
    float dis = distance(uv.xy,pos.xy);
    vec3 result = vec3(0.0,0.0,0.0);
    if(dis<range)
        result = mix(vec3(0.36,0.174,0.119),vec3(0.0,0.0,0.0),pow(dis/range,power));
    return result;
}

vec3 drawSun(vec2 pos,vec2 uv, float range,float sunrange,float power,float aspect){
    uv.x*=aspect;
    float dis = distance(uv.xy,pos.xy);
    vec3 result = vec3(0.0,0.0,0.0);
    if(dis<range)
        result = vec3(1.0,0.974,0.647);
    else if(dis>=range && dis<range+sunrange)
        result = mix(vec3(1.0,0.974,0.007),vec3(0.0,0.0,0.0),pow((dis-range)/sunrange,power));
    return result;
}

vec3 drawWave(float speed, float range, float height,float offset, float power, vec2 uv, float dis){
 	vec3 finb = mix(vec3(0.796,0.796,0.745),vec3(0.513,0.513,0.486),dis);
    float siny = offset + height*pow(sin(uv.x * range + iTime * speed)+1.0,power);
    if(uv.y>siny)
        finb = vec3(1.0,1.0,1.0);
    return finb;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aspect = iResolution.x/iResolution.y;
    vec2 uv = fragCoord.xy / iResolution.xy;
    float dis = distance(uv.xy,vec2(0.5,0.5));
    vec4 fin = mix(vec4(0.91,0.91,0.87,1),vec4(0.65,0.65,0.517,1),dis);
    fin.rgb += drawHolo(vec2(0.5,0.7),uv,0.7,0.7,aspect);
    fin.rgb *= drawWave(1.4,70.0,0.017,0.5,1.3,uv,dis);
    fin.rgb *= drawWave(1.8,60.0,0.015,0.487,1.3,uv,dis);
    fin.rgb *= drawWave(2.2,50.0,0.013,0.462,1.3,uv,dis);
