float TAU = 6.28318530718;

float xyToR(vec2 uv, float r) {
    float variance = 55./iResolution.y; // via @FabriceNeyret2
    float mag = length(uv);

    // Draw as a smoothed line
    return smoothstep(r - variance, r , mag)
        - smoothstep(r, r + variance, mag);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 R = iResolution.xy;
    vec2 uv = (fragCoord - .5*R) / R.y;
    uv *= 8.; // Scale

	fragColor = vec4(vec3(xyToR(uv,
       1.
       + sin(TAU/2. * uv.x)*sin(iTime)
       + sin(TAU * uv.y) * sin(2.*iTime)
       + cos(TAU/4.*uv.x*uv.y) * sin(3.*iTime)
    )), 1.0);
}

void mainImage( out vec4 o,  vec2 U )
{
    o = vec4(0.0);
    float r=.2, z=4., t=iTime, H = iResolution.y, uz;
    U /=  H;                              // object : disc(P,r)
    vec2 P = .5+.5*vec2(cos(t),sin(t*.7)), C=vec2(-.7,0), fU;  
    U =(U-C)/z; P=(P-C)/z; r/= z;         // unzoom for the whole domain falls within [0,1]^n

    mat2 M = mat2(1,0,.5,.87), IM = mat2(1,0,-.577,1.155);
    U = IM*U;         // goto triangular coordinates (there, regular orthonormal grid + diag )

    o.b = .25;                            // backgroud = cold blue

    for (int i=0; i<7; i++) {             // to the infinity, and beyond ! :-)
        fU = min(U,1.-U); uz = 1.-U.x-U.y;
        if (min(min(fU.x,fU.y),abs(uz)) < z*r/H) { o--; break; } // cell border
    	if (length(P-M*vec2(.5-sign(uz)/6.)) - r > .6) break;    // cell is out of the shape

                // --- iterate to child cell
        fU = step(.5,U);                  // select grid-child
        U = 2.*U - fU;                    // go to new local frame
        P = 2.*P - M*fU;  r *= 2.;

        o += .13;                         // getting closer, getting hotter
    }

	o.gb *= smoothstep(.9,1.,length(P-M*U)/r); // draw object
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec4 startColor = vec4(1.0, 0.0, 0.0, 1.0);
    vec4 endColor = vec4(1.0, 1.0, 0.0, 1.0);
    float currentAngle = -(iTime * 36.0);

    vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 origin = vec2(0.5, 0.5);
    uv -= origin;

    float angle = radians(90.0) - radians(currentAngle) + atan(uv.y, uv.x);

    float len = length(uv);
    uv = vec2(cos(angle) * len, sin(angle) * len) + origin;

    fragColor = mix(startColor, endColor, smoothstep(0.0, 1.0, uv.x));
}

