float maggot(vec3 pos)
{
    pos.y -= 0.5;
    pos.y *= 0.3;
    pos.xz *= 1.0 + smoothstep(0.0, 3.0, sin(pos.y * 120.0)) * 0.055;
    pos.x += sin(iTime * 5.0 - pos.y * 10.0) * 0.1;
	float nl = noise(pos * 6.0) * 0.3;
    float endMask = smoothstep(0.1, 0.2, pos.y);
    return (length(pos) - 1.0 + clamp(pos.y * 3.0, 0.5, 1.0)) + nl * endMask;
}

