//Main
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 
    /////SCENE SETUP/////
    //Uv coordinates in -1 to 1 space
	vec2 uv = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;
    //Aspect ratio correction
    uv.x *= iResolution.x / iResolution.y;
    //Camera Position with some tilting animation
    vec3 cameraOrigin = vec3(0.0, 0.0, 2.0);
    //Camera Target
    vec3 cameraTarget = vec3(0.0);
    //World Up Direction
    vec3 upDirection = vec3(0.0, 1.0, 0.0);
    //Camera Direction
    vec3 cameraDir = normalize(cameraTarget - cameraOrigin);
    //Camera Right Direction
    vec3 cameraRight = normalize(cross(upDirection, cameraOrigin));
    //Camera Up Direction
    vec3 cameraUp = cross(cameraDir, cameraRight);
    //Ray Direction
    vec3 rayDir = normalize(cameraRight * uv.x + cameraUp * uv.y + cameraDir);

    /////RAY MARCHING////
    //How far the ray has travelled so far
    float totalDist = 0.0;
    float dist = EPSILON;
    //Point to start at
    vec3 pos = cameraOrigin;

    for(int i = 0; i < 32; i++)
    {
        //If the object has been hit or the ray has gone out of bounds, break.
        if(dist < EPSILON || totalDist > 20.0)
            break;
        //Evaluate the distance at current point using a distance function
        dist = distfunc(pos);
        //Progress the total distance covered
        totalDist += dist;
        //Progress the point along the ray vector by the distance travelled
        pos += dist * rayDir;
    }

    /////Lighting/////
    //Check if object was hit
    if(dist < EPSILON)
    {
        //Calculate the surface normal by sampling fragments of EPSILON distance from this fragment 
        vec2 eps = vec2(0.0, EPSILON);
        vec3 normal = normalize(vec3(
        	distfunc(pos + eps.yxx) - distfunc(pos - eps.yxx),
            distfunc(pos + eps.xyx) - distfunc(pos - eps.xyx),
            distfunc(pos + eps.xxy) - distfunc(pos - eps.xxy)));

        //NDotL lighting, assuming the light source is the camera
        float brightness = 0.5;
        vec3 lightSource = normalize(vec3(1.0, 0.0, 2.0)) * brightness;
        float diffuse = max(0.0, dot(lightSource, normal));
        float specular = pow(diffuse, 32.0) * 0.15;
        vec3 baseColor = vec3(0.8, 0.3, 0.0);
        vec3 result = baseColor + vec3(diffuse + specular);
        //Final frag result
		fragColor = vec4(result, 1.0);

    }
    //If not hit, use background colour
    else
    {
        fragColor = vec4(0.1,0.15,0.25,0.1);
    }
}

// change these values to 0.0 to turn off individual effects
float vertJerkOpt = 1.0;
float vertMovementOpt = 1.0;
float bottomStaticOpt = 1.0;
float scalinesOpt = 2.0;
float rgbOffsetOpt = 1.0;
float horzFuzzOpt = 4.0;

// Noise generation functions borrowed from: 
// https://github.com/ashima/webgl-noise/blob/master/src/noise2D.glsl

vec3 mod289(vec3 x) {
  return x - floor(x * (1.0 / 289.0)) * 289.0;
}

vec2 mod289(vec2 x) {
  return x - floor(x * (1.0 / 289.0)) * 289.0;
}

vec3 permute(vec3 x) {
  return mod289(((x*34.0)+1.0)*x);
}

float snoise(vec2 v)
  {
  const vec4 C = vec4(0.211324865405187,  // (3.0-sqrt(3.0))/6.0
                      0.366025403784439,  // 0.5*(sqrt(3.0)-1.0)
                     -0.577350269189626,  // -1.0 + 2.0 * C.x
                      0.024390243902439); // 1.0 / 41.0
// First corner
  vec2 i  = floor(v + dot(v, C.yy) );
  vec2 x0 = v -   i + dot(i, C.xx);

// Other corners
  vec2 i1;
  //i1.x = step( x0.y, x0.x ); // x0.x > x0.y ? 1.0 : 0.0
  //i1.y = 1.0 - i1.x;
  i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
  // x0 = x0 - 0.0 + 0.0 * C.xx ;
  // x1 = x0 - i1 + 1.0 * C.xx ;
  // x2 = x0 - 1.0 + 2.0 * C.xx ;
  vec4 x12 = x0.xyxy + C.xxzz;
  x12.xy -= i1;

// Permutations
  i = mod289(i); // Avoid truncation effects in permutation
  vec3 p = permute( permute( i.y + vec3(0.0, i1.y, 1.0 ))
		+ i.x + vec3(0.0, i1.x, 1.0 ));

  vec3 m = max(0.5 - vec3(dot(x0,x0), dot(x12.xy,x12.xy), dot(x12.zw,x12.zw)), 0.0);
  m = m*m ;
  m = m*m ;

// Gradients: 41 points uniformly over a line, mapped onto a diamond.
// The ring size 17*17 = 289 is close to a multiple of 41 (41*7 = 287)

  vec3 x = 2.0 * fract(p * C.www) - 1.0;
  vec3 h = abs(x) - 0.5;
  vec3 ox = floor(x + 0.5);
  vec3 a0 = x - ox;

// Normalise gradients implicitly by scaling m
// Approximation of: m *= inversesqrt( a0*a0 + h*h );
  m *= 1.79284291400159 - 0.85373472095314 * ( a0*a0 + h*h );

// Compute final noise value at P
  vec3 g;
  g.x  = a0.x  * x0.x  + h.x  * x0.y;
  g.yz = a0.yz * x12.xz + h.yz * x12.yw;
  return 130.0 * dot(m, g);
}

float fnoise(vec2 v) {
	return fract(sin(dot(v, vec2(12.9898, 78.233))) * 43758.5453) * 0.55;
}

float staticV(vec2 uv) {
    float staticHeight = fnoise(vec2(9.0,iTime*1.2+3.0))*0.3+5.0;
    float staticAmount = fnoise(vec2(1.0,iTime*1.2-6.0))*0.1+0.3;
    float staticStrength = fnoise(vec2(-9.75,iTime*0.6-3.0))*2.0+2.0;
	return (1.0-step(fnoise(vec2(5.0*pow(iTime,2.0)+pow(uv.x*7.0,1.2),pow((mod(iTime,100.0)+100.0)*uv.y*0.3+3.0,staticHeight))),staticAmount))*staticStrength;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv =  fragCoord.xy/iResolution.xy;

	float fuzzOffset = fnoise(vec2(iTime*15.0,uv.y*80.0))*0.003;
	float largeFuzzOffset = fnoise(vec2(iTime*1.0,uv.y*25.0))*0.004;

    float vertMovementOn = (1.0-step(snoise(vec2(iTime*0.2,8.0)),0.4))*vertMovementOpt;
    float vertJerk = (1.0-step(fnoise(vec2(iTime*1.5,5.0)),0.6))*vertJerkOpt;
    float vertJerk2 = (1.0-step(fnoise(vec2(iTime*5.5,5.0)),0.2))*vertJerkOpt;
    float yOffset = abs(sin(iTime)*4.0)*vertMovementOn+vertJerk*vertJerk2*0.3;
    float y = mod(uv.y+yOffset,1.0);

	float xOffset = (fuzzOffset + largeFuzzOffset) * horzFuzzOpt;

    float staticVal = 0.0;

    for (float y = -1.0; y <= 1.0; y += 1.0) {
        float maxDist = 5.0/200.0;
        float dist = y/200.0;
    	staticVal += staticV(vec2(uv.x,uv.y+dist))*(maxDist-abs(dist))*1.5;
    }

    staticVal *= bottomStaticOpt;

	float red 	=   texture(	iChannel0, 	vec2(uv.x + xOffset -0.01*rgbOffsetOpt,y)).r+staticVal;
	float green = 	texture(	iChannel0, 	vec2(uv.x + xOffset,	  y)).g+staticVal;
	float blue 	=	texture(	iChannel0, 	vec2(uv.x + xOffset +0.01*rgbOffsetOpt,y)).b+staticVal;

	vec3 color = vec3(red,green,blue);
	float scanline = sin(uv.y*800.0)*0.04*scalinesOpt;
	color -= scanline;

	fragColor = vec4(color,1.0);
}

float circleedge(vec2 p, vec2 o, float radius)
{
    float s = length(p - o) - radius;
    return 1.0 - exp2(-28000.0 * s*s);
}

// polynomial smooth min (k = 0.1) from inigo quilez:
float smin(float a, float b, float k)
{
    float h = clamp(0.5 + 0.5 * (b - a) / k, 0.0, 1.0);
    return mix(b, a, h) - k * h * (1.0 - h);
}

float circlething(vec2 p)
{
    float a0 = iTime * 0.065;
    float r_sin = sin(iTime * 0.35);

    float result = 1.0;

    const float num_a_circles = 2.0 * 7.0, inv_num_a_circles = 1.0 / num_a_circles;
    const float num_r_circles = 1.0 * 5.0, inv_num_r_circles = 1.0 / num_r_circles;
    for (float i = 0.0; i < num_r_circles; i += 1.0)
    for (float j = 0.0; j < num_a_circles; j += 1.0)
    {
        float a = a0 + j * 6.283185307179586476925286766559 * inv_num_a_circles;

        float r = (i + 1.0) * inv_num_r_circles * 0.5;
        vec2 o_i = vec2(cos(a), sin(a)) * r;

        float r2 = 0.13 + (1.0 - r) * (1.0 + r_sin * r_sin * r_sin) * 0.136;
        float r_ = sqrt(r2);
        result = smin(result, circleedge(p, o_i, r_), 0.75);
    }

    return max(0.0, result);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 pixel_scale = vec2(0.75 / iResolution.x);

   	float s = 0.0;
    const float samples = 5.0;
    const float norm = 1.0 / samples;
	for (float z = 0.0; z < samples; z += 1.0)
    {
		float a = z * norm * 3.0 * 6.283185307179586476925286766559;
        float r0 = sqrt(z * norm);
        float r = r0 * 1.15;
        vec2 aa = vec2(cos(a), sin(a)) * r;
        float w = 1.0 - r0;

		s += circlething((fragCoord - iResolution.xy * 0.5 + aa) * pixel_scale) * w;
    }
    s *= norm;

    vec3 c = vec3(15.8, 1.9, 0.4) * (s * 64.0);

    float gamma_r = sqrt(c.x);
    float gamma_g = sqrt(c.y);
    float gamma_b = sqrt(c.z);
    fragColor = vec4(gamma_r, gamma_g, gamma_b, 1.0);
}

