//////////////////////////////////////////////
// Implementation of Separating Axis Test (SAT)
// SAT test can be used to find collision between convex polygons. Visualization a bit lacking,
// basicly the black lines coming out of the objects are showing the axis that were found to separate the objects, and 
// the gap between the objects in that axis.
// reference: http://gamedevelopment.tutsplus.com/tutorials/collision-detection-using-the-separating-axis-theorem--gamedev-169
///////////////////////////////////////////////

// change this to have more vertices in the objects (more vertices will create more round objects)
#define POINTS 3

#define COL1 vec4(0.0, 1.0, 1.0, 0.0)
#define COL2 vec4(1.0, 0.0, 1.0, 0.0)


vec2 g_uv;    
struct Poly
{
    vec2 origin;
	vec2 points[POINTS];
    vec2 normals[POINTS];
};

Poly polys[2];

float drawvec(vec2 from, vec2 to)
{
    vec2 uvv = g_uv - from;
    vec2 a = to - from;
    if(dot(uvv, uvv) - dot(a, a) > 0.0) return 0.0;
    float d = dot(normalize(uvv), normalize(a)) * length(uvv);
    if(d < 0.0) return 0.0;
    float b = length((d * normalize(a)) - uvv);
    float w = 0.002;
    return max(w - b, 0.0) / w;
}

float drawPoly(in Poly p)
{
    float x = 0.0;
    for (int i = 0; i < POINTS - 1; ++i)
    {
	    x += drawvec(p.origin + p.points[i], p.origin + p.points[i + 1]);
    }
    x += drawvec(p.origin + p.points[POINTS - 1], p.origin + p.points[0]);
    return clamp(x, 0.0, 1.0);
}

vec2 rotate(vec2 point, float angle)
{
    float x = point.x; float y = point.y;
    point.x = x * cos(angle) - y * sin(angle);
    point.y = y * cos(angle) + x * sin(angle);
    return point;
}

void rotate(inout Poly p, float angle)
{
    for (int i = 0; i < POINTS; ++i)
    {
	    p.points[i] = rotate(p.points[i], angle);
    }
}

// finds minimum and maximum values 
// (dot product between axis and the vertex position gives the projected length on that axis)
vec2 minmax(in Poly p, in vec2 axis)
{
    float m = dot(p.origin + p.points[0], axis);
    vec2 mm = vec2(m, m);
    for (int i = 1; i < POINTS; ++i)
    {
        float d = dot(p.origin + p.points[i], axis);
        mm.y = max(mm.y, d);
        mm.x = min(mm.x, d);
    }
    return mm;
}

void calcNormals(inout Poly p)
{
    for (int i = 0; i < POINTS - 1; ++i)
    {
        p.normals[i] = (normalize(p.points[i] - p.points[i+1])).yx * vec2(-1.0, 1.0);
    }
    p.normals[POINTS - 1] = (normalize(p.points[POINTS - 1] - p.points[0])).yx * vec2(-1.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(1.0);
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= vec2(0.5);
    uv.y /= iResolution.x / iResolution.y;
    g_uv = uv;

	const int GRID = 30;
    for (int i = 0; i < GRID; ++i)
    {
        float x = -0.5 + float(i) * 1.0 / float(GRID); 
        fragColor -= drawvec(vec2(x, -0.5), vec2(x, 0.5)) * (i == GRID / 2 ? 0.4 : 0.2);
        fragColor -= drawvec(vec2(-0.5, x), vec2(0.5, x)) * (i == GRID / 2 ? 0.4 : 0.2);
    }


    vec2 m = iMouse.xy / iResolution.xy;
    m -= 0.5;
    m.y /= iResolution.x / iResolution.y;

    if(iMouse.xy == vec2(0.0)) { m = vec2(0.0);}

    // creating polygons
    float size = 0.1;
    polys[0].origin = vec2(sin(iTime * 0.15) * 0.2, cos(iTime * 0.15) * 0.2);
    polys[1].origin = m;

    float stp = (3.14159 * 2.0) / float(POINTS);

    for (int i = 0; i < POINTS; ++i)
    {
        polys[0].points[i] = vec2(rotate(vec2(size, size), float(i) * stp));
        polys[1].points[i] = vec2(rotate(vec2(size, size), float(i) * stp));
    }

    rotate(polys[0], iTime * 0.1);
    rotate(polys[1], iTime * 0.2);

    calcNormals(polys[0]);
    calcNormals(polys[1]);

    // the actual SAT test happens here.
    bool hit = true;
    float gap = 0.0;
    for (int j = 0; j < 2; ++j)
    {
        for (int i = 0; i < POINTS; ++i)
        {
            vec2 axis = polys[j].normals[i];

            vec2 minm1 = minmax(polys[0], axis);
            vec2 minm2 = minmax(polys[1], axis);

            // values need to be swapped
            // if maximum of first object is smaller than minimum of second object
            if(minm1.y > minm2.x)
            {
                vec2 tmp = minm2;
                minm2 = minm1;
                minm1 = tmp;
            }

            float gap = minm2.x - minm1.y;
            gap = max(gap, 0.0);


            if(gap > 0.0)
            {
                hit = false;    
            }

			// some visualization         
            fragColor -= drawvec(polys[j].origin, polys[j].origin + axis * gap);
        }
    }

    fragColor -= drawPoly(polys[0]) * (hit ? COL1 : COL2);
    fragColor -= drawPoly(polys[1]) * (hit ? COL1 : COL2);        



}

// Double Binary Tree/Quaternary Tree as Ray-Traced Implicit Surface - written 2015-11-10 by Jakob Thomsen
// Closed-form expression Implicit Surface Tree - NO recursion/iteration, using only fract,exp2,ceil,log2 for branching.
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
// (ported from http://sourceforge.net/projects/shaderview/)

// NOTE: the name was choosen to avoid confusion with the quad-tree data-structure.

#define pi 3.1415926

float sfract(float val, float scale)
{
    return (2.0 * fract(0.5 + 0.5 * val / scale) - 1.0) * scale;
}

vec2 mirror2(vec2 v)
{
    return abs(2.0 * fract(v * 0.5) - 1.0);
}

vec3 BinarySubDiv(vec3 v )
{
    v = fract(exp2(ceil(-log2(v.z))) * v * vec3(.5, .5, 1.));
    v.xy = mirror2(2. * v.xy);
    return v;
}

float fn(vec3 v)
{
    v.z *= -1.0;
    v = 0.5 * v + 0.5;
    vec3 p = vec3(0.5, .5, 0.);
    vec3 n = vec3(1.0, 1.0, 1.) - p;
    v = BinarySubDiv(v);
    v.xy = mirror2(v.xy);
    return length((v - p) - dot(v - p, n) * n / dot(n, n)) - .01;
    //return length((v - p) - dot(v - p, n) * n / dot(n, n)) - .01 * ceil(-log2(1.0-v.z));
    //return length((v - p) - dot(v - p, n) * n / dot(n, n)) - .01 * exp2(ceil(-log2(1.0-v.z)));
    //return length((v - p) - dot(v - p, n) * n / dot(n, n)) - .01 * exp2(ceil(-log2(v.z)));
}

vec3 nrm(vec3 v)
{
    vec3 n;
    float d = 0.01;
    n.x = fn(v + vec3(d, 0.0, 0.0)) - fn(v + vec3(-d, 0.0, 0.0));
    n.y = fn(v + vec3(0.0, d, 0.0)) - fn(v + vec3(0.0, -d, 0.0));
    n.z = fn(v + vec3(0.0, 0.0, d)) - fn(v + vec3(0.0, 0.0, -d));
    return normalize(n);
}

float comb(float v, float s)
{
    return pow(0.5 + 0.5 * cos(v * 2.0 * pi), s);
}

vec3 tex(vec3 v)
{
    float d = abs(v.z * 20. + sfract(-iTime, 4.) * 5.); 
    vec3 c = 0.5 + 0.5 * nrm(v);
    return exp(-d*d) + c * abs(nrm(v).x);
}

vec3 camera(vec2 uv, float depth)
{
    float t = iTime * 0.1;
    vec3 v;
    //v.x = uv.x * cos(t) + uv.y * sin(t);
    //v.y = uv.x * -sin(t) + uv.y * cos(t);
    //v.z = depth;

    // isometry
    vec3 iso;
//    iso.x =  v.x - v.y - v.z;
//    iso.y = -v.x - v.y - v.z;
//    iso.z =        v.y - v.z;

//    return iso;
    iso.x =  uv.x - uv.y - depth;
    iso.y = -uv.x - uv.y - depth;
    iso.z =         uv.y - depth;

    v.x = iso.x * cos(t) + iso.y * sin(t);
    v.y = iso.x * -sin(t) + iso.y * cos(t);
    v.z = iso.z;

    return v;
}

