{
	const float scaleFactor=1.4;
	vec2 uv = scaleFactor*(fragCoord.xy-0.5*iResolution.xy) / iResolution.y;
    uv.y+=0.5;
	init(); 
	fragColor = vec4(color(uv),1.0);
}

