float sbox( vec3 p, vec3 b ){
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

float map(vec3 p)
{
    float d=  sbox(p, vec3(1.));
#ifdef MOUSE_Y_CUTS_SPACE
#if (TYPE == 1)
    d = max(d, -sbox(p + vec3(.9 + mo.y*0.35,0.0,0), vec3(1.)));
#else
    d = max(d, -sbox(p + vec3(1.4 + mo.y*0.35,0.0,0), vec3(1.)));
#endif
#endif
    return d;
} 

vec3 marchCol(in vec3 ro, in vec3 rd)
{
    float h = 1.0;
    float d = 0.;
    vec3 col = vec3(0);
    vec3 colLast = vec3(0);
    const float prc = 1e-8;
    const float sz = 115.;
    for( int i=0; i<240; i++ )
    {
        d += clamp(h,0.01,1.);
        vec3 pos = ro+rd*d;
    #if (TYPE == 1)
        vec3 col2 = lab2rgb(vec3(pos.x*sz,pos.y*sz , pos.z*sz));
    #elif (TYPE == 2)    
        vec3 col2 = xyz2rgb(vec3(pos.x+0.5, pos.y+0.5, pos.z+0.5));
    #else
        vec3 col2 = vec3(pos.x+0.5, pos.y+0.5, pos.z+0.5);
	#endif
        col2 = sRGB(col2);

        h = map(pos);
        col2 = clamp(col2, 0., 1.);

        if (h < 0.)
        if ((abs(col2.r-colLast.r) > prc)  && (abs(col2.g-colLast.g) > prc) && (abs(col2.b-colLast.b) > prc))
        {
        	col= col2;
            break;
        }
        colLast = col2;
    }
	return col;
}

vec3 rotx(vec3 p, float a){
    float s = sin(a), c = cos(a);
    return vec3(p.x, c*p.y - s*p.z, s*p.y + c*p.z);
}
vec3 roty(vec3 p, float a){
    float s = sin(a), c = cos(a);
    return vec3(c*p.x + s*p.z, p.y, -s*p.x + c*p.z);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{	
	vec2 p = fragCoord.xy/iResolution.xy-0.5;
	p.x*=iResolution.x/iResolution.y;
	mo = iMouse.xy / iResolution.xy-.5;
    mo = (mo==vec2(-.5))?mo=vec2(-0.15,1.):mo;
	mo.x *= iResolution.x/iResolution.y;
	mo *= 4.;

    #if (TYPE == 1)
	vec3 ro = vec3(0.,-0.1,3.5);
    #elif (TYPE == 2)
    vec3 ro = vec3(0.,0.0,2.5);
    #else
    vec3 ro = vec3(0.,0.0,3.0);
    #endif
    vec3 rd = normalize(vec3(p,-1.5));
    float rx = mo.x+time*0.1;
    float ry = sin(time*0.6)*0.2;
    ro = rotx(ro,ry), rd = rotx(rd, ry);
    ro = roty(ro, rx), rd = roty(rd, rx);

    vec3 col = marchCol(ro, rd);
	fragColor = vec4( col, 1.0 );
}

#define EPS .008
#define X_EPS vec2(EPS,0.)
#define Y_EPS vec2(0., EPS)

vec3 col(vec2 uv)
{
    float time = iTime *.75;

    float x = atan(uv.y,uv.x)/3.14159*3.;
    float d = dot(uv,uv);
    float y=.5/d+time; 
    vec2 s = sin(3.14159*vec2(x,y)/.1);
    float v1=step(s.x*s.y,0.);
    float v2=cos(x+2.*y);
    float v=mix(v1,v2,smoothstep(1.,13.,mod(time,16.)));
    v*=1.-smoothstep(1.2,0.,.8*length(uv)); 

    return vec3(v,v*.2*x,v*v);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.*fragCoord.xy - iResolution.xy) / iResolution.y;

    vec3 c = col(uv)+
             col(uv+X_EPS)+
             col(uv+Y_EPS)+
             col(uv-X_EPS)+
             col(uv-Y_EPS);
    c /= 4.;


    fragColor = vec4(c,1.);
}

