	p1 = Roty(pos,PI/3.);
		p1.xz = abs(p1.xz)-0.5;
	p2 = Roty(pos,PI/6.);
		p2.xz = abs(p2.xz)-0.5;

	p3 = pos;
    	p3.xz = abs(p3.xz)-0.21;
