float hash1( float n )
{
    return fract(sin(n)*43758.5453123);
}

float hash1( in vec2 f ) 
{ 
    return fract(sin(f.x+131.1*f.y)*43758.5453123); 
}


const float PI = 3.1415926535897932384626433832795;
const float PHI = 1.6180339887498948482045868343656;

vec3 forwardSF( float i, float n) 
{
    float phi = 2.0*PI*fract(i/PHI);
    float zi = 1.0 - (2.0*i+1.0)/n;
    float sinTheta = sqrt( 1.0 - zi*zi);
    return vec3( cos(phi)*sinTheta, sin(phi)*sinTheta, zi);
}

float sca = 0.5 + 0.15*sin(iTime-10.0);
vec4 grow = vec4(1.0);

vec3 mapP( vec3 p )
{
    p.xyz += 1.000*sin(  2.0*p.yzx )*grow.x;
    p.xyz += 0.500*sin(  4.0*p.yzx )*grow.y;
    p.xyz += 0.250*sin(  8.0*p.yzx )*grow.z;
    p.xyz += 0.050*sin( 16.0*p.yzx )*grow.w;
    return p;
}

float map( vec3 q )
{
    vec3 p = mapP( q );
    float d = length( p ) - 1.5;
	return d * 0.05;
}

float intersect( in vec3 ro, in vec3 rd )
{
	const float maxd = 7.0;

	float precis = 0.001;
    float h = 1.0;
    float t = 1.0;
    for( int i=0; i<1256; i++ )
    {
        if( (h<precis) || (t>maxd) ) break;
	    h = map( ro+rd*t );
        t += h;
    }

    if( t>maxd ) t=-1.0;
	return t;
}

vec3 calcNormal( in vec3 pos )
{
    vec3 eps = vec3(0.005,0.0,0.0);
	return normalize( vec3(
           map(pos+eps.xyy) - map(pos-eps.xyy),
           map(pos+eps.yxy) - map(pos-eps.yxy),
           map(pos+eps.yyx) - map(pos-eps.yyx) ) );
}

float calcAO( in vec3 pos, in vec3 nor, in vec2 pix )
{
	float ao = 0.0;
    for( int i=0; i<64; i++ )
    {
        vec3 ap = forwardSF( float(i), 64.0 );
		ap *= sign( dot(ap,nor) ) * hash1(float(i));
        ao += clamp( map( pos + nor*0.05 + ap*1.0 )*32.0, 0.0, 1.0 );
    }
	ao /= 64.0;

    return clamp( ao*ao, 0.0, 1.0 );
}

float calcAO2( in vec3 pos, in vec3 nor, in vec2 pix )
{
	float ao = 0.0;
    for( int i=0; i<32; i++ )
    {
        vec3 ap = forwardSF( float(i), 32.0 );
		ap *= sign( dot(ap,nor) ) * hash1(float(i));
        ao += clamp( map( pos + nor*0.05 + ap*0.2 )*100.0, 0.0, 1.0 );
    }
	ao /= 32.0;

    return clamp( ao, 0.0, 1.0 );
}

vec4 texCube( sampler2D sam, in vec3 p, in vec3 n, in float k )
{
	vec4 x = texture( sam, p.yz );
	vec4 y = texture( sam, p.zx );
	vec4 z = texture( sam, p.xy );
    vec3 w = pow( abs(n), vec3(k) );
	return (x*w.x + y*w.y + z*w.z) / (w.x+w.y+w.z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (-iResolution.xy+2.0*fragCoord.xy)/iResolution.y;
	vec2 q = fragCoord/iResolution.xy;

    vec2 m = vec2(0.5);
	if( iMouse.z>0.0 ) m = iMouse.xy/iResolution.xy;



    grow = smoothstep( 0.0, 1.0, (iTime-vec4(0.0,1.0,2.0,3.0))/3.0 );


    //-----------------------------------------------------
    // camera
    //-----------------------------------------------------

	float an = 1.1 + 0.05*(iTime-10.0) - 7.0*m.x;

	vec3 ro = vec3(6.5*sin(an),1.0,6.5*cos(an));
    vec3 ta = vec3(0.0,-0.0,0.0);
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
	// create view ray
	vec3 rd = normalize( p.x*uu + p.y*vv + 1.5*ww );


    //-----------------------------------------------------
	// render
    //-----------------------------------------------------

	vec3 col = vec3(0.07)*clamp(1.0-length(q-0.5),0.0,1.0);

	// raymarch
    float t = intersect(ro,rd);

    if( t>0.0 )
    {
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal(pos);
		vec3 ref = reflect( rd, nor );
        vec3 sor = nor;

        vec3 q = mapP( pos );
        float occ = calcAO( pos, nor, fragCoord ); occ = occ*occ;

        // materials
		col = vec3(0.04);
        float ar = clamp(1.0-0.7*length(q-pos),0.0,1.0);
        col = mix( col, vec3(2.1,2.0,1.2), ar);
        col  *= 0.3;          
        col *= mix(vec3(1.0,0.4,0.3), vec3(0.8,1.0,1.3), occ);
        float occ2 = calcAO2( pos, nor, fragCoord );


        col *= 1.0*mix( vec3(2.0,0.4,0.2), vec3(1.0), occ2*occ2*occ2 );
        float ks = texCube( iChannel0, pos*1.5, nor, 4.0 ).x;
        ks = 0.5 + 1.0*ks;
        ks *= (1.0-ar);

        // lighting
        float sky = 0.5 + 0.5*nor.y;
        float fre = clamp( 1.0 + dot(nor,rd), 0.0, 1.0 );
        float spe = pow(max( dot(-rd,nor),0.0),8.0);
		// lights
		vec3 lin  = vec3(0.0);
		     lin += 3.0*vec3(0.7,0.80,1.00)*sky*occ;
             lin += 1.0*fre*vec3(1.2,0.70,0.60)*(0.1+0.9*occ);
        col += 0.3*ks*4.0*vec3(0.7,0.8,1.00)*smoothstep(0.0,0.2,ref.y)*(0.05+0.95*pow(fre,5.0))*(0.5+0.5*nor.y)*occ;
        col += 4.0*ks*1.5*spe*occ*col.x;
        col += 2.0*ks*1.0*pow(spe,8.0)*occ*col.x;
        col = col * lin;

        // dust
        col = mix( col, 0.2*fre*fre*fre+0.6*vec3(0.6,0.55,0.5)*sky*(0.8+0.4*texCube( iChannel0, pos*8.0, nor, 4.0 ).xyz), 0.6*smoothstep(0.3,0.7,nor.y)*sqrt(occ) );

        col *= 2.6*exp(-0.2*t);
    }

	col = pow(col,vec3(0.4545));

    col = pow( col, vec3(1.0,1.0,1.4) ) + vec3(0.0,0.02,0.14);

    col += (1.0/255.0)*hash1( fragCoord );

    fragColor = vec4( col, 1.0 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy - .5;
    uv *= 7.+sin(iTime*.1)*4.;
    uv += vec2(sin(iTime*.33),cos(iTime*.2))*3.;

    for (int i = 0; i < 4; i++) {
        float ang = abs(atan(uv.y,uv.x))/4.+
            cos((abs(uv.x)+abs(uv.y))/1.5);
        uv *= mat2(sin(ang), cos(ang), -cos(ang), sin(ang));
    }

    float dst = max(0., 1.-(length(max(abs(sin(uv))-.1,0.))));
    fragColor = vec4(cos(dst*16.+iTime)*.5+.5,
                     dst,
                     dst,1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 R = iResolution.xy,
        uv = (fragCoord - .5*R) / iResolution.y;

    vec3 rp = vec3(0.,0.,iTime);
    vec3 rd = normalize(vec3(uv,1.));

    vec3 c = vec3(0.);
    float s = 0.;

    for (int i = 0; i < 74; i++) {
        vec3 hp = rp+rd*s;
        float d = length(cos(hp*.6+
                             cos(hp*.3+iTime*.5)))-.2;
        float cc = clamp(1.-(d*.5+(d*5.)/s),-1.,1.);

        c += (cos(vec3(hp.xy,s))*.5+.5 + cos(vec3(s+iTime,hp.yx)*.1)*.5+.5 + 1.)/3.
              *cc*.02;

        s += d;
        rd = normalize(rd+vec3(d*.01,d*-.006,0.));
    }

    fragColor = vec4(c,1.);
}

#define PI 3.14
#define TAU 6.28318530718

const float MAT0 = 0.0;
const float MAT1 = 1.0;
const float MAT2 = 2.0;
const float MAT3 = 3.0;

float smin(float a, float b) {

    float k = 0.05;
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

vec3 rgb2hsv(vec3 c) {
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c) {
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

float noise(in vec2 uv) {
	return sin(1.5*uv.x)*sin(1.5*uv.y);
}

const mat2 m = mat2( 0.80,  0.60, -0.60,  0.80 );

float fbm(vec2 uv) {
    float f = 0.0;
    f += 0.5000*noise(uv); uv = m*uv*2.02;
    f += 0.2500*noise(uv); uv = m*uv*2.03;
    f += 0.1250*noise(uv); uv = m*uv*2.01;
    f += 0.0625*noise(uv);
    return f/0.9375;
}

float fbm2(in vec2 uv) {
   vec2 p = vec2(fbm(uv + vec2(0.0,0.0)),
                 fbm(uv + vec2(5.2,1.3)));

   return fbm(uv + 4.0*p);
}

float rand(in vec2 uv) {
    return fract(sin(dot(uv.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float voronoi(vec2 coord) {
    vec2 coordInt = floor(coord);
    vec2 coordFraction = fract(coord);

	float result = 8.0;
    for (int i = -1; i <= 1; i++)
    	for (int j = -1; j <= 1; j++)
        {
         	vec2 cellPoint = vec2(float(i), float(j));

            float offset = rand(coordInt + cellPoint);
            offset = 0.5 + 0.5*sin( iTime + 6.2831*offset );

            vec2 randomPoint = cellPoint + offset - coordFraction;
            float d = dot(randomPoint, randomPoint);

            result = min(result, d);

        } 
    return (sqrt(result));
}

vec2 add(in vec2 a, in vec2 b) {

    float mat;
    if(a.x < b.x) {
      mat = a.y;
    } else {
      mat = b.y;
    }

    return vec2(smin(a.x,b.x), mat);
}

vec2 sub(in vec2 a, in vec2 b) {

    float mat;
    if(a.x < b.x) {
      mat = b.y;
    } else {
      mat = a.y;
    }

    return vec2(max(a.x, b.x), mat);
}

vec3 rep(in vec3 p, in vec3 c) {
    vec3 q = mod(p,c)-0.5*c;
    return q;
}

vec2 rotate(in vec2 p, in float ang) {
    float c = cos(ang), s = sin(ang);
    return vec2(p.x*c - p.y*s, p.x*s + p.y*c);
}

vec2 torus(in vec3 p, in vec2 t, in float mat) {
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return vec2(length(q)-t.y, mat);
}

vec2 sphere(in vec3 p, in float r, in float mat) {
    return vec2(length(p) - r, mat);
}

vec2 plane(in vec3 p, in vec4 n, in float mat) {
  return vec2(dot(p,n.xyz) + n.w, mat);
}

vec2 cylinder(in vec3 p, in vec3 c, in float mat) {
  return vec2(length(p.xz-c.xy)-c.z, mat);
}

vec2 box(in vec3 p, in vec3 b, in float mat) {
  return vec2(
      length(
          max(abs(p)-b,0.0)
      ) * length(p*.05)
  , mat);
}

float map(in vec3 p, inout float mat) {

	vec2 scene = vec2(999.0, MAT0);

    float lp = length(p);

	vec2 s1 = box(p, vec3(.25), MAT0);

	scene = add(scene, s1);

    p = rep(p, vec3(.5));

    vec2 s = sphere(p, length(p) * .5, MAT1);
    scene = add(scene, s);

	mat = scene.y;

 	return scene.x;
}

mat3 setLookAt(in vec3 ro, in vec3 ta,in float cr) {
	vec3  cw = normalize(ta-ro);
	vec3  cp = vec3(sin(cr), cos(cr),0.0);
	vec3  cu = normalize( cross(cw,cp) );
	vec3  cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

float softshadow(in vec3 ro, in vec3 rd, in float mint, in float tmax) {
	float res = 1.0;
    float t = mint;

    float mat = MAT3;
    for( int i = 0; i < 16; i++ ) {
		float h = map(ro + rd*t, mat);
        res = min( res, 4.0*h/t );
        t += clamp( h, 0.02, 0.10 );
        if( h<0.001 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 p = -1.0 + 2.0*uv;
    p.x *= iResolution.x / iResolution.y;

    // camera	
    vec3 ro = vec3(
        -1.7*cos(iTime * .5),
        .0,
        1.7*sin(iTime * .5)
   	);
    vec3 ta = vec3(0.0);
    float roll = 0.0;

    // camera tx
    mat3 ca = setLookAt( ro, ta, roll );
    vec3 rd = normalize( ca * vec3(p.xy,1.75) );

    float t = 0.001;    // Near
    float tmax = 120.0; // Far

    float h = 0.001;
    float hmax = 0.001;

    float mat = MAT0;

    vec3 c = vec3(0.0);
    vec3 ao = vec3(0.0);

    const int steps = 100;
    for(int i = 0 ; i < steps ; i++) {

        if(h < hmax || t > tmax ) {
        	ao = vec3(1.0) - float(i)/float(steps);
            break;
        }

        h = map(ro + t *rd, mat);
        t += h;
    }

    if(t < tmax) {
        vec3 pos = ro+rd*t;

        vec2 r = vec2(0.001,0.0);
        vec3 nor = normalize(vec3(map(pos+r.xyy, mat)-map(pos-r.xyy, mat),
                                  map(pos+r.yxy, mat)-map(pos-r.yxy, mat),
                                  map(pos+r.yyx, mat)-map(pos-r.yyx, mat)));
      	vec3 ref = reflect(rd,nor);

        if(mat == MAT0) {
          c = vec3(1.0,0.0,0.0);
        }

        if(mat == MAT1) {
          c = vec3(1.0); 
        }

        vec3 lig = vec3(0.5773);
        c *= softshadow(pos, lig, 0.03, 4.0);
        c *= clamp(dot(nor,lig), 0.1, 1.0);

        c *= 1.0 + clamp(dot(ref,lig), 0.0, .5) * .3;

        c *= 1.0 + ao * 20.;

        c += vec3(.1,.5,.9) * abs(pos.x * pos.y * pos.z) * .5 * length(1.0 / p * p);

        c = rgb2hsv(c);

        c.y *= 1.01;
        c.z *= 1.5;

        c.x += length(p) + fbm2(pos.xy) *.1;

        c = hsv2rgb(c);
    } 

	fragColor = vec4(c,1.0);
}

#define EPS 0.004

#define BUTTERFLY

vec2 rot(in vec2 uv, float a)
{
    a = radians(a);
    float c, s;
    c = cos(a);
    s = sin(a);
    return vec2(uv.x * c + uv.y * s, uv.x * -s + uv.y * c);
}

vec3 f(in vec2 uv, in float radius)
{
    for (float a=0.0; a < 6.28 * 2.; a += .01)
    {
        float co = cos(a);
        float co4 = cos(4. * a);
    	float p = radius * (exp(co) - 2. * co4 - pow(sin(a / 12.), 5.));
        //vec2 xy = p * vec2(co, sin(a));
    	float x = abs(p * co);
    	float y = abs(p * sin(a));

        if (abs(uv.x) < x + EPS && abs(uv.x) > x - EPS && abs(uv.y) < y + EPS && abs(uv.y) > y - EPS)
            return vec3(abs(rot(vec2(x, abs(x - y)), iTime * 50.)), y);
    }
    return vec3(0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = 2. * fragCoord.xy / iResolution.xy - 1.;
    #ifndef BUTTERFLY
    	uv.x *= iResolution.x / iResolution.y;
    #else
    	uv.x *= ((abs(sin(mod(iTime * 2.0, 360.))) * 2. + 1.) * iResolution.x) / iResolution.y;
    	uv.y += sin(uv.x + 1.56) - .8;
	#endif
    fragColor = vec4(f(uv, .2), 1.0);
}

