const float PI = 3.14159265358979323846;
const vec4 red   = vec4 (1., .0, .0, 1.);
const vec4 green = vec4 (.0, 1., .0, 1.);
const vec4 white = vec4 (1.);
const vec4 black = vec4 (.0, .0, .0, 1.);
const int MAX_STEPS = 64;
const float epsilon = 0.0001;

mat4 trans (vec3 t)
{
    mat4 mat = mat4 (vec4 (1., .0, .0, .0),
                     vec4 (.0, 1., .0, .0),
                     vec4 (.0, .0, 1., .0),
                     vec4 (t.x, t.y, t.z, 1.));
    return mat;
}

vec3 opTransf (vec3 p, mat4 m)
{
    return vec4 (m * vec4 (p, 1.)).xyz;
}

mat4 rotX (in float angle)
{
    float rad = radians (angle);
    float c = cos (rad);
    float s = sin (rad);

    mat4 mat = mat4 (vec4 (1.0, 0.0, 0.0, 0.0),
                     vec4 (0.0,   c,   s, 0.0),
                     vec4 (0.0,  -s,   c, 0.0),
                     vec4 (0.0, 0.0, 0.0, 1.0));

    return mat;
}

mat4 rotY (in float angle)
{
    float rad = radians (angle);
    float c = cos (rad);
    float s = sin (rad);

    mat4 mat = mat4 (vec4 (  c, 0.0,  -s, 0.0),
                     vec4 (0.0, 1.0, 0.0, 0.0),
                     vec4 (  s, 0.0,   c, 0.0),
                     vec4 (0.0, 0.0, 0.0, 1.0));

    return mat;
}

mat4 rotZ (in float angle)
{
    float rad = radians (angle);
    float c = cos (rad);
    float s = sin (rad);

    mat4 mat = mat4 (vec4 (  c,   s, 0.0, 0.0),
                     vec4 ( -s,   c, 0.0, 0.0),
                     vec4 (0.0, 0.0, 1.0, 0.0),
                     vec4 (0.0, 0.0, 0.0, 1.0));

    return mat;
}

float opSubtract (float d1, float d2)
{
    return max (-d1, d2);
}

float opIntersect (float d1, float d2)
{
    return max (d1, d2);
}

float sphere (vec3 p, float r)
{
    return length (p) - r;
}

float box (vec3 p, vec3 b, float r)
{
    return length (max (abs (p) - b + vec3 (r), .0)) - r;
}

float cylinder (vec3 p, vec3 n)
{
    return length (p.xz - n.xy) - n.z;
}

float wavyfloor (vec3 p, vec4 n)
{
    float tm = iTime;
    return dot (p.xyz, n.xyz) + n.w - .1 * sin (4. * p.x) * cos (4. * p.z);
}

float csgdice (vec3 p)
{
    float d1 = .0;
    float d2 = .0;

    // dice with holes
    mat4 m = trans (vec3 (.0, .0, .0));
    mat4 m1 = rotX (90.) * trans (vec3 (.35, .5, 2.75));
    mat4 m2 = trans (vec3 (.35, .2, .5));
    d1 = opIntersect (box (opTransf (p, m), vec3 (.55), .0),
                      sphere (opTransf (p, m), .7));
    d2 = opSubtract (cylinder (opTransf (p, m1), vec3 (.35, .5, .35)),
                     d1);
    d1 = opSubtract (cylinder (opTransf (p, m2), vec3 (.35, .5, .35)),
                     d2);
    m1 = rotZ (90.) * trans (vec3 (1.25, -.35, .5));
    d2 = opSubtract (cylinder (opTransf (p, m1), vec3 (.35, .5, .35)), d1);

    return d2;
}

float map (vec3 p, inout int index, bool withoutlights)
{
    float d = .0;
    float ground = .0;
    float dicea = .0;
    float diceb = .0;
    float lighta = .0;
    float lightb = .0;
    float t = iTime;

    // floor
    ground = wavyfloor (p, normalize (vec4 (.0, -1., .0, 1.)));

    // first dice
    mat4 m = rotX (30. * t) * rotY (40. * t) * rotZ (50. * t) * trans (vec3 (.0, .5, 1.));
    dicea = csgdice (opTransf (p, m));

    // second dice
    m = rotX (40. * t) * rotY (50. * t) * rotZ (30. * t) * trans (vec3 (-2., .5, 1.));
    diceb = csgdice (opTransf (p, m));

    d = min (ground, min (dicea, diceb));

    // "lightsources"
    if (!withoutlights) {
        m = trans (vec3 (.0, .5, 1.));
        lighta = sphere (opTransf (p, m), .05);
        m = trans (vec3 (-2., .5, 1.));
        lightb = sphere (opTransf (p, m), .05);
        d = min (d, min (lighta, lightb));
        if (d == lighta || d == lightb) {
            index = 4;
        }
    }

    // assign "color/material"-index
    if (d == ground) {
        index = 1;
    } else if (d == dicea) {
        index = 2;
    } else if (d == diceb) {
        index = 3;
    }

    return d;
}

vec3 normal (vec3 p)
{
    vec3 e = vec3 (.0001, .0, .0);
    int index = 0;

    vec3 n = vec3 (map (p + e.xyy, index, false) - map (p - e.xyy, index, false),
                   map (p + e.yxy, index, false) - map (p - e.yxy, index, false),
                   map (p + e.yyx, index, false) - map (p - e.yyx, index, false));

    return normalize (n);
}

float shadow (vec3 ro, vec3 rd, float tmin, float k)
{
    float s = 1.;
    float d = .0;
    float t = tmin;
    int index = 0;
    for (int i = 0; i < 64; i++) {
        d = map (ro + rd * t, index, true);
        if (d < epsilon) {
            return .0;
        }
        s = min (s, k * d / t);
        t += d*.75;
    }
    return s;
}

vec4 shade (vec3 p, vec4 color)
{
	// animate the light-sources a bit
    float t = 1.5 * iTime;
    vec3 lPos0 = vec3 (.0, -.5, -1.);
    vec3 lPos1 = vec3 (4. * cos (radians (1.)), -4., 4. * sin (radians (1.)));
    vec3 lPos2 = vec3 (2., -.5, -1.);

    vec4 lCol = white;
    vec3 n = normal (p);
    vec3 lDir0 = normalize (lPos0 - p);
    vec3 lDir1 = normalize (lPos1 - p);
    vec3 lDir2 = normalize (lPos2 - p);
    vec4 lInt0 = lCol * max (dot (n, lDir0), .0);
    vec4 lInt1 = lCol * max (dot (n, lDir1), .0);
    vec4 lInt2 = lCol * max (dot (n, lDir2), .0);
    vec4 col = shadow (p+.001*normalize (lPos0 - p), normalize (lPos0 - p), .001, 64.) * .7 * lInt0 * color;
    col += shadow (p+.001*normalize (lPos1 - p), normalize (lPos1 - p), .001, 64.) * .7 * lInt1 * color;
    col += shadow (p+.001*normalize (lPos2 - p), normalize (lPos2 - p), .001, 64.) * .7 * lInt1 * color;

    return col;
}

mat3 camera (vec3 eye, vec3 aim, float r)
{
    vec3 cw = normalize (aim - eye);
    vec3 cp = vec3 (sin (r), cos (r), .0);
    vec3 cu = normalize (cross (cw, cp));
    vec3 cv = normalize (cross (cu, cw));
    return mat3 (cu, cv, cw);
}

float march (vec3 ro, vec3 rd, float pixelSize, inout int index)
{
    bool forceHit = true;
    float infinity = 10000.0;
    float t_min = .00001;
    float t_max = 100.0;
    float t = t_min;
    float candidate = t_min;
    float candidate_error = infinity;
    float w = .5;
    float lastd = .0;
    float stepSize = .0;
    int mindex = 0;
    float sign = map (ro, mindex, false) < .0 ? -1. : 1.;

    for (int i = 0; i < MAX_STEPS; i++)
	{
        float signedd = sign * map (ro + rd * t, index, false);
        float d = abs (signedd);
        bool fail = w > 1. && (d + lastd) < stepSize;

        if (fail) {
            stepSize -= w * stepSize;
            w = .25;
        } else {
            stepSize = signedd * w;
        }

		lastd = d;

        float error = d / t;
        if (!fail && error < candidate_error) {
            candidate_error = error;
            candidate = t;
        }

        if (!fail && error < pixelSize || t > t_max) {
        	break;
		}

        t += stepSize;

	}

    if ((t > t_max || candidate_error > pixelSize) && !forceHit) {
        return infinity;
    }

	return candidate;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
    vec2 mouse = iMouse.xy / iResolution.xy;
    vec3 eye = vec3 (cos (8. * mouse.x + 25.) * 9.,
					 -6.,
					 sin (8. * mouse.x + 25.) * 9.);
    vec3 aim = vec3 (.0, .0, .0);
    mat3 cam = camera (eye, aim, .0);
    vec2 uv = vec2 (-.75 + fragCoord.x / iResolution.y, .5 - fragCoord.y / iResolution.y);

    vec3 ro = eye;
    vec3 rd = cam * normalize (vec3 (uv, 1.8));
    vec4 bg = vec4 (.0);
    float pixelSize = 1. / iResolution.x;
    int index = 0;
    float t = march (ro, rd, pixelSize, index);
    vec4 color;
    if (index  == 1) {
	    color = mix (shade (ro + rd * t, white), bg, length (rd * t) / 30.);
    } else if (index == 2) {
    	color = mix (shade (ro + rd * t, red), bg, length (rd * t) / 30.);
    } else if (index == 3) {
    	color = mix (shade (ro + rd * t, green), bg, length (rd * t) / 30.);
    } else if (index == 4) {
    	color = white;
    }

    fragColor = color;
}

#define STEPS 80

#define sr(a) (a * 2.0 - 1.0)
#define rs(a) (a * 0.5 + 0.5)
#define sq(a) (a * vec2(1, iResolution.y / iResolution.x))

float random(vec2 co) {
   return fract(sin(dot(co.xy,vec2(12.9898,78.233))) * 43758.5453);
}

float smin(float a, float b, float k) {
  float h = clamp(0.5 + 0.5 * (b - a) / k, 0.0, 1.0);
  return mix(b, a, h) - k * h * (1.0 - h);
}

float sdBox( vec2 p, vec2 b )
{
  vec2 d = abs(p) - b;
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float t = iTime * 1.;

float map(vec2 p, vec4 ro) {
    vec2 P = p;

    float w = 0.15;
    float r = 0.03125;
    float a = t;
    float d = 0.0;

    p.y += iTime * 0.15;

    vec2 idx = floor(p / w * 0.5 + 0.5);

    r += sin((idx.x + idx.y) * 2. + t * 5.) * 0.009;
    a += sin((idx.x + idx.y) * 2.);
    a += random(idx) * 5.;

    p = mod(p + w, w * 2.) - w;
    d = length(p - 0.095 * vec2(sin(a), cos(a))) - r;
    d = smin(d, length(P) - 0.25, 0.05);

    // "push" away any surfaces close to the lights
    d = -smin(-d, length(P - ro.xy) - 0.05, 0.05);
    d = -smin(-d, length(P - ro.zw) - 0.075, 0.05);
    d = smin(d, -sdBox(P, sq(vec2(1))), 0.065);

	return d;
}

float shadow(vec2 uv, vec2 ro, vec2 rd, vec4 lights) {
    float lim = 0.0005;
    float res = -1.0;
    float inc = lim * 2.0;
    float t = inc;
    float maxt = length(ro - uv);

    if (map(uv, lights) < 0.0) return 0.0;

    for (int i = 0; i < STEPS; i++) {
        if (t >= maxt) return -1.0;
        float d = map(uv - rd * t, lights);
        if (d <= 0.0) return 0.0;

        t = min(t + d * 0.2, maxt);
        res = t;
    }

    return res;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = sq(sr(fragCoord.xy / iResolution.xy));
    vec2 ro1 = (iMouse.z > 0.
        ? sq(sr(iMouse.xy / iResolution.xy))
        : 0.5 * vec2(
            sin(iTime),
            cos(iTime)
        ));

    vec2 ro2 = (sin(iTime * 0.2) + 2.5) * 0.15 * vec2(cos(iTime), sin(iTime));
    vec4 lights = vec4(ro1, ro2);

    vec2 rd1 = normalize(uv - ro1);
    vec2 rd2 = normalize(uv - ro2);
    float s1 = shadow(uv, ro1, rd1, lights) > -0.5 ? 0.35 : 1.0;
    float s2 = shadow(uv, ro2, rd2, lights) > -0.5 ? 0.35 : 1.0;
    float l1 = s1 * pow(max(0.0, 1.0 - length(ro1 - uv) * 0.8), 2.5);
    float l2 = s2 * pow(max(0.0, 1.0 - length(ro2 - uv) * 0.8), 2.5);
    float d = map(uv, lights);

    vec3 lcol1 = vec3(1, 0.5, 0.3);
    vec3 lcol2 = vec3(0, 1, 1);

    bool inside = d < 0.0;
    bool stroke = d > -0.005 && inside; 
    vec3 m = inside ? vec3(stroke ? 0 : 5) : vec3(1, 0.9, 0.7);

	fragColor = vec4(m * (l1 * lcol1 + l2 * lcol2), 1);
}

//
// This is the noise algorithm that I used:

// Description : Array and textureless GLSL 2D/3D/4D simplex 
//               noise functions.
//      Author : Ian McEwan, Ashima Arts.
//  Maintainer : ijm
//     Lastmod : 20110822 (ijm)
//     License : Copyright (C) 2011 Ashima Arts. All rights reserved.
