vec3 rainbow(float value) // smooth
{
    //return .5 + .5 * cos(2. * 3.1415926 * (value + vec3(0,1,2) / 3.)); // original
    // variants based on the fake-sine function https://www.shadertoy.com/view/4sd3zM
    //vec3 v = 2.*(.25 + value + vec3(0,1,2) / 3.);
    vec3 v = 2.*(.25 + value + vec3(0,1,2) * fract(iTime * .1));
    //return 0.5 + 0.5 * sign(.5-fract(v*.5))*(1.-pow(abs(2. * fract(v) - 1.),vec3(1.75)));
    vec3 w = abs(2. * fract(v) - 1.);
    return 0.5 + 0.5 * sign(.5-fract(v*.5))*(1.-w*w);
    //vec3 m = mirror(value + vec3(0,1,2) / 3.);
    //return m * m;
}

vec3 rainbow2(float value) // crisp
{
    //return abs(2. * fract(value + vec3(0,1,2) / 8.) - 1.);
    return abs(2. * fract(value + vec3(0,1,2) * fract(iTime * .1)) - 1.);
