}

#define TL  30.0
#define TL2 33.0

float hash(float n)
{
    return fract(sin(n)*43758.5453);
}

float noise(vec2 p)
{
    return hash(p.x + p.y*57.0);
}

float smoothNoise(vec2 p)
{
    float n0=noise(floor(p)+vec2(0,0));
    float n1=noise(floor(p)+vec2(1,0));
    float n2=noise(floor(p)+vec2(0,1));
    float n3=noise(floor(p)+vec2(1,1));
    float u=fract(p.x),v=fract(p.y);
    return mix(mix(n0,n1,u),mix(n2,n3,u),v);
}

bool blocked(vec2 p)
{
    float r=acos(-1.)*2./8.;
    vec2 p2=(p-.5)*mat2(cos(r),sin(r),sin(r),-cos(r))*length(vec2(1));
    vec2 c=floor(p2); 
    bool s=step(.5,noise(c))>.5;
    bool hv=mod(c.x+c.y,2.)>.5;
    return hv==s;
}

float mazeDist(vec3 p)
{
    vec3 cp=fract(p)-vec3(.5),acp=abs(cp);
    float r=acos(-1.)*2./8.;
    vec2 p2=(p.xz-.5)*mat2(cos(r),sin(r),sin(r),-cos(r))*length(vec2(1));
    vec2 c=floor(p2),f=fract(p.xz);
    float a=step(.5,noise(c));
    float s=.1;
    if(a>.5)
        return acp.x-s;
    return acp.z-s;
}

float f(vec3 p)
{
    return min(length(vec2(max(0.,mazeDist(p)),p.y-.04))-.08,p.y);
}

vec2 startPoint(float t)
{
    return vec2(floor(t)*7., floor(t)*3.);
}

vec2 startDirection(float t)
{
    return vec2(-1.+mod(t*2.,2.),0.);
}

float cubic(float x)
{
    return (3.*x-2.*x*x)*x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time=iTime;
    vec2 c=vec2(fragCoord/iResolution.xy-.5)*2.;
    c.x*=iResolution.x/iResolution.y;
    float time2=time*.7;
    float tsec=floor(time2/TL2);
    vec2 bp=startPoint(tsec),pbp=bp;
    vec2 bd=startDirection(tsec),pbd=bd;

    float st=mod(time2,TL2);

    for(int i=0;i<int(TL);++i)
    {
        if(i>int(floor(st)))
            break;
        vec2 od=bd.yx*vec2(1.,-1.)*(1.-2.*step(.5,hash(float(i))));
        pbp=bp;
        pbd=bd;
        if(!blocked(bp+bd/2.))
        {
            bp+=bd;
            if(!blocked(bp+od/2.))
                bd=od;
        }
        else
            bd=bd.yx*vec2(1,-1);
    }

    bp=mix(pbp,bp,fract(min(st,TL-1e-4)));
    bd=normalize(mix(pbd,bd,fract(min(st,TL-1e-4))));
    float ry=.5;

    if(floor(st)>=TL)
    {
        float t=(st-TL)/(TL2-TL);
        bp=mix(bp,startPoint(tsec+1.),cubic(t));
        float ba=mix(atan(bd.y,bd.x),atan(startDirection(tsec+1.).y,startDirection(tsec+1.).x),cubic(t));
        bd=vec2(cos(ba),sin(ba));
        ry+=1.*sin(t*acos(-1.));
    }

    vec3 up=vec3(0,1,0);
    float p=-.3;
    vec2 yz=vec2(c.y,1.8)*mat2(cos(p),sin(p),-sin(p),cos(p));;
        vec3 rd=normalize(yz.x*up+yz.y*vec3(bd.x,0,bd.y)-cross(up,vec3(bd.x,0,bd.y))*c.x);
    vec3 ro=vec3(bp.x,ry,bp.y);

    float t=0.,d=0.;
    for(int i=0;i<120;++i)
    {
        d=f(ro+rd*t);
        if(d<1e-3||t>18.)
            break;
        t+=d;
    }

    if(t>18.)
    {
        fragColor.rgb=mix(vec3(1),vec3(.6,.6,1),rd.y);
        return;
    }

    vec3 rp=ro+rd*t;
    float eps=1e-1;
    float d2=clamp((f(rp+normalize(vec3(-1,1,1.5))*eps)-d)/eps,-1.,1.);

    float eps2=1e-3;
    float d22=clamp((f(rp+normalize(vec3(-1,1,1.5))*eps2)-d)/eps2,-1.,1.);

    float eps3=2e-1;
    float d23=clamp((f(rp+normalize(vec3(0,1,0))*eps3)-d)/eps3,-1.,1.);

    float eps4=2e-1;
    float d24=clamp((f(rp+normalize(rd)*eps4)-d)/eps4,-1.,1.);

    vec3 irr=.5*vec3(1,1,.8)*(mix(.5,1.,1.-clamp(d22-d2,0.,1.)))*(.5+.5*d22);

    irr+=.5*vec3(.8,.85,1.)*pow(.5+.5*d23,3.);

    vec3 alb=mix(vec3(1.),vec3(.4,.35,.3)*1.4,step(rp.y,.1e-2))*mix(.7,1.,step(fract(rp.x*2.+.5*step(.5,fract(rp.z*2.))),.5));

    vec3 spec=vec3(pow(clamp(.6+.25*d24,0.,1.)*1.4,16.))*14.*
        (smoothNoise(rp.xz*256.)/2.+smoothNoise(rp.xz*512.)/3.);

    fragColor.rgb=sqrt(mix(vec3(1),alb*irr+spec,exp2(-t/25.)));
}

// Binary Tree variant with (almost) fix line-size - written 2015 by Jakob Thomsen
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

#define mirror(v) abs(2. * fract(v / 2.) - 1.)

float C01(float v)
{
    v += .25;
    float w = abs(2. * fract(v*2.) - 1.);
    return 0.5 + 0.5 * sign(.5-fract(v))*(1.-w*w);
}
/*
float c01(float v)
{
    return 0.5 + 0.5 * cos(2.0 * 3.1415926 * v);
}
*/
void mainImage( out vec4 o, in vec2 U )
//void mainImage( inout vec4 o, vec2 U ) // ERROR: overloaded functions must have the same parameter qualifiers
{
    U /= iResolution.xy;
    U.y = mirror(U.y+1.);
    //U.y = 2./(2.-U.y) - 1.;
    U.y = 1.-log2(2.-U.y);
    float y = (1.-U.y);
    //float f = 7.*(1.-mirror(iTime*.1));
    float f = 7.*(1.-C01(iTime*.05));
    U.y *= f;
    U.x *= exp2(ceil(U.y))/2.;
    U = fract(U);
    o -= o;
    //o += 1. - smoothstep(0., (.4 - .2 * U.y), abs(4. * abs(U.x - .5) - C01(.5 * (U.y + 1.0)))); // "plant"
    o += 1. - smoothstep(0., f*(.02 - .01 * U.y)/y, abs(4. * abs(U.x - .5) - C01(.5 * (U.y + 1.0)))); // fixed size
}

//This was an experiment so I could learn some GLSL.
//Thanks to vug for his tutorial!  I also referenced swellbastion to use a texture for noise. Thank you!

#define PI 3.14159265359

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = vec2(fragCoord.xy / iResolution.xy); 
	//aspect ratio
    vec2 r =  2.0*vec2(fragCoord.xy - 0.5*iResolution.xy)/iResolution.y;
    //coordinates [0,1]  origin is the bottom left

	//animate
    float t = iTime;
    r = r * 8.0;

 //https://www.shadertoy.com/view/Md23DV   
    //vertical lines
    float v1 = sin(r.x +t);
    //vertical waves
    float v2 = sin(r.y +t);
    //diagonal
    float v3 = sin(r.x+r.y +t);
    //rings
    float v4 = sin(sqrt(r.x*r.x+r.y*r.y) +5.0*t);
    //blobs
	float v = v1+v2+v3+v4;

	vec3 ret;	
	// mix colors
	v *= 1.0;
	ret = vec3(sin(v), sin(v+(.5+t)*PI), sin(v+1.0*PI)-.5);

	ret = 0.5 + 0.5*ret;

    vec3 pixel = ret;


    if (p.y > 0.5) { // if top half of screen
        p.y -= 0.5;
        fragColor = vec4(pixel, 1.0);
    }
 //https://www.shadertoy.com/view/ld33RN#   
    else { // if bottom half of screen
                p.y = 0.5 - p.y;

        vec2 noiseStretch = vec2(10000.0, 1500.0);
        float distanceRiverTraveled = t * -0.02;

        vec2 noisePosition = fragCoord / noiseStretch;
        noisePosition.x += distanceRiverTraveled;

    	float noise = texture(iChannel0, noisePosition).r;

    	float xMove = noise * 0.15 - 0.075;

    	vec2 imagePosition = vec2(
    		p.x + xMove,
        	p.y
    	);

        pixel.x += noise;
        pixel.y +=  noise;
        pixel.z += v1 *noise;
        vec4 image = vec4(pixel, imagePosition);


        float waterBrightness = 0.95;
		fragColor = image * waterBrightness;


    }

}

vec3 paintCircle (vec2 uv, vec2 center, float rad, float width) {

    vec2 diff = center-uv;
	float distan = sqrt(dot(diff, diff));
    float circle = smoothstep(rad-width, rad, distan) - smoothstep(rad, rad+width, distan);
    return vec3(circle);
}

float chessboard(vec2 uv)
{
    uv = floor(uv*12.0); 
    return mod(uv.y + uv.x, 2.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 backgroundColour = vec4(chessboard(uv));

    vec2 center = vec2(0.5);
    float radius = 1.0;    
    vec3 color = paintCircle(uv, center, radius, 0.1);
