vec3 Strand(in vec2 fragCoord, in vec3 color, in float hoffset, in float hscale, in float vscale, in float timescale)
{
    float glow = 0.01 * iResolution.x;
    fragCoord.x += vscale * 100.0;
    float twopi = 6.28318530718;
    float curve = 1.0 - abs(fragCoord.x - (sin(mod(fragCoord.y * hscale / 100.0 / iResolution.y * 1000.0 + iTime * timescale + hoffset, twopi)) * iResolution.x * 0.25 * vscale + iResolution.x / 2.0));
    float i = clamp(curve, 0.0, 1.0) * 0.3;
    i += clamp((glow * 2.0 + curve) / (glow * 2.0), 0.0, 1.0) * 0.3 ;
    i += clamp((glow * 8.03 + curve) / (glow * 8.03), 0.0, 1.0) * 0.25 ;
    i += clamp((glow * 50.03 + curve) / (glow * 50.03), 0.0, 1.0) * 0.05 ;

	float len = sin(hoffset * timescale * 0.01 + hscale * timescale * 0.0001) * 10.0 + sqrt(pow(fragCoord.x - iResolution.x * 0.5, 2.0) + pow(fragCoord.y * 0.5, 2.0));
    float d = (len + (vscale * 3.0 - 0.5) * iResolution.y) / (0.1 * iResolution.y);
    return clamp(i, 0.0, 1.0) * (3.0 - d) * color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float timescale = -5.0;
	vec3 c = vec3(0, 0, 0);
    c += Strand(fragCoord, vec3(1.0, 0.25, 0), 0.7934 + iTime * 30.0, 1.0, 0.05, 10.0 * timescale);
    c += Strand(fragCoord, vec3(0.9, 0.21, 0), 0.645 + iTime * 30.0, 1.5, 0.2, 10.3 * timescale);
    c += Strand(fragCoord, vec3(0.85, 0.3, 0), 0.735 + iTime * 30.0, 1.3, 0.19, 8.0 * timescale);
    c += Strand(fragCoord, vec3(0.93, 0.23, 0.0), 0.9245 + iTime * 30.0, 3.0, 0.14, 12.0 * timescale);
    c += Strand(fragCoord, vec3(0.97, 0.19, 0), 0.7234 + iTime * 30.0, 1.9, 0.23, 14.0 * timescale);
    c += Strand(fragCoord, vec3(0.83, 0.24, 0), 0.84525 + iTime * 30.0, 1.2, 0.29, 9.0 * timescale);


	fragColor = vec4(c.r, c.g, c.b, 1.0);
}

#define PI 3.141592

float hash(float n) {return fract(sin(n * 0.021) * 43758.5453123);}//from iq

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    vec2 mo = iMouse.xy / iResolution.xy;
    mo -= 0.5;
    mo.x *= iResolution.x / iResolution.y;

    float time = iTime * 0.5;

    uv.x += 0.1 * sin(time * 0.1);
    uv.y += 0.05 * sin(time * 0.2);

    uv.x *= (1.0 - 0.001 * abs(sin(time * 20.0)));

    float subd = 25.0 + 2.0 * sin(time * 0.1);
    float radius = 0.1 + 0.45 + 0.05 * abs(sin(time));
    vec3  color = vec3(0.0);

    float hsub = subd*2.0;
    vec2 grid = uv * subd;
    grid = vec2(floor(grid.x)/subd, floor(grid.y)/subd);
    vec2 gridC = vec2(grid.x + 1.0/hsub, grid.y+1.0/hsub);

    vec2 mDist = vec2(sin(time)*(0.7 + 0.3 * cos(time * 0.5)), cos(time)*0.3) - grid;

    float weight = 0.0;
    if(length(mDist) < radius) {
        weight = smoothstep(0.0, 0.3, max(0.0, radius - length(mDist)));
    }

    float qbRadius = clamp(0.5*smoothstep(0.5, 0.1, length(mDist)), 0.2, 0.5);
    float qbWeight = 0.85*smoothstep(0.5, 0.1, length(mDist));   

    vec2 cellD = uv - gridC;
	float finalRadius = qbRadius/subd;
    float rings = 0.0;
    if(length(cellD) < qbRadius/subd) {
        float col = 0.0;
        col = smoothstep(0.9,0.85,length(cellD)/(qbRadius/subd));
        col -= 0.93*smoothstep(1.0,0.9,length(cellD)/(qbRadius/subd*qbWeight));

        rings += col;
    }

    float r = 0.5 + 0.5*((grid.x+0.5) * cos(time * 3.0));
    float b = 0.7;
    float g = 0.5 + 0.5*((grid.y+0.5) * sin(time * 2.0));

    color = 
        vec3(0.03) 
        + rings*vec3(r, g, b)*0.05
        + rings*weight*vec3(r, g, b)*2.5
        + weight*vec3(r, g, b)*0.3
        + vec3(hash(uv.x * 0.05), hash(uv.y* 0.05), hash(uv.x + uv.y))* 0.01;

    fragColor = vec4(color.rgb, 1.0);
    //fragColor = vec4(r,g,b, 1.0);
}

