float distanceTo(float x, float y, float centerX, float centerY) {
	float deltaX = x - centerX;
    float deltaY = y - centerY;
    return sqrt(deltaX * deltaX + deltaY * deltaY);
}

void mainImage( out vec4 color, in vec2 pixCoords )
{
    float zoom = (iResolution.x / 10.0) * (1.0 + 0.1 * cos(20.0 * iTime));
    float cameraX = 5.0 + 3.0 * cos(iTime * 0.5);
    float cameraY = 2.0 + 3.0 * sin(iTime * 1.1);

    float cameraAngle = 20.0*(3.14/180.0)+iTime*3.0;

    float xBeforeRot = (pixCoords.x / zoom) - cameraX;
    float yBeforeRot = (pixCoords.y / zoom) - cameraY;

    float x = (xBeforeRot*cos(cameraAngle))+(yBeforeRot*sin(cameraAngle));
    float y = (-xBeforeRot*sin(cameraAngle))+(yBeforeRot*cos(cameraAngle));


    float radius = 0.3 + 0.2 * rand(vec2(floor(x), floor(y)));// * cos(3.0 * iTime + x + y);
    radius *= 0.8 + 0.3 * cos(10.0 * rand(vec2(floor(x), floor(y))) * iTime);
    float distance = distanceTo(mod(x, 1.0), mod(y, 1.0), 0.5, 0.5);
    if (distance < radius) {
        color = vec4(cos(20.0 * distance / radius), sin(iTime*10.0), cos(iTime*20.0), 1.0);
        return;
    }

    float light = (cos(y * 3.0 + 6.0 * iTime) + 1.0) * 0.5;
    color = vec4(cos(iTime*20.0), light, light * (0.5 + 0.5 * cos(x * 30.0)), 1.0);

}

#define STEPS 80

#define sr(a) (a * 2.0 - 1.0)
#define rs(a) (a * 0.5 + 0.5)
#define sq(a) (a * vec2(1, iResolution.y / iResolution.x))

