float smin(float a, float b, float k) {
  float h = clamp(0.5 + 0.5 * (b - a) / k, 0.0, 1.0);
  return mix(b, a, h) - k * h * (1.0 - h);
}

float t = iTime * 1.;

float map(vec2 p, vec2 ro) {
    vec2 P = p;

    float w = 0.15;
    float r = 0.03125;
    float a = t;
    float d = 0.0;

    p.y += iTime * 0.15;

    vec2 idx = floor(p / w * 0.5 + 0.5);

    r += sin((idx.x + idx.y) * 2. + t * 5.) * 0.009;
    a += sin((idx.x + idx.y));

    p = mod(p + w, w * 2.) - w;
    d = length(p - 0.095 * vec2(sin(a), cos(a))) - r;
    d = smin(d, length(P) - 0.25, 0.05);
    d = -smin(-d, length(P - ro) - 0.05, 0.05);

	return d;
}

float shadow(vec2 uv, vec2 ro, vec2 rd) {
    float lim = 0.0005;
    float res = -1.0;
    float inc = lim * 2.0;
    float t = inc;
    float maxt = length(ro - uv);

    if (map(uv, ro) < 0.0) return 0.0;

    for (int i = 0; i < STEPS; i++) {
        if (t >= maxt) return -1.0;
        float d = map(uv - rd * t, ro);
        if (d <= 0.0) return 0.0;

        t = min(t + d * 0.2, maxt);
        res = t;
    }

    return res;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = sq(sr(fragCoord.xy / iResolution.xy));
    vec2 ro = (iMouse.z > 0.
        ? sq(sr(iMouse.xy / iResolution.xy))
        : 0.5 * vec2(
            sin(iTime),
            cos(iTime)
        ));

    vec2 rd = normalize(uv - ro);
    float s = shadow(uv, ro, rd) > -0.5 ? 0.35 : 1.0;
    float l = s * pow(max(0.0, 1.0 - length(ro - uv) * 0.8), 2.5);
    float d = map(uv, ro);

    bool inside = d < 0.0;
    bool stroke = d > -0.005 && inside; 
    vec3 m = inside ? vec3(stroke ? 0 : 5) : vec3(1, 0.9, 0.7);

	fragColor = vec4(m * l, 1);
}

// SineTree3d (a ray-traced Sine Tree) - written 2015-11-05 by Jakob Thomsen
// A tree-like structure without recursion, using trigonometric functions with fract for branching - suitable for ray-tracing :-)
// Thanks to FabriceNeyret2 for streamlining the functions.
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
// (ported from http://sourceforge.net/projects/shaderview/)

#define pi 3.1415926

float sfract(float val, float scale)
{
    return (2.0 * fract(0.5 + 0.5 * val / scale) - 1.0) * scale;
}

float mirror(float v)
{
    return abs(2.0 * fract(v * 0.5) - 1.0);
}

float sinetree2d(vec2 v)
{
    v.x = mirror(v.x);
    float n = 3.0; // fract(t / 6.0) * 6.0;
    float br = exp2(ceil(v.y * n));
    float fr = fract(v.y * n);
    float val = cos(pi * (v.x * br + pow(fr, 1.0 - fr) * 0.5 * sign(sin(pi * v.x * br))));
    return 1.0 - pow(0.5 - 0.5 * val, (fr * 1.5 + 0.5) * 1.0);
}

float fn(vec3 v)
{
    return max(sinetree2d(vec2(length(v.xy), v.z)), sinetree2d(vec2(atan(v.x, v.y), v.z))); // JT's SineTree 3d (original)
    // Variations by FabriceNeyret2
    //return sinetree2d(vec2(atan(v.x, v.y), v.z));
    //return sinetree2d(vec2(length(v.xy), v.z));
}

vec3 nrm(vec3 v)
{
    vec3 n;
    float d = 0.01;
    n.x = fn(v + vec3(d, 0.0, 0.0)) - fn(v + vec3(-d, 0.0, 0.0));
    n.y = fn(v + vec3(0.0, d, 0.0)) - fn(v + vec3(0.0, -d, 0.0));
    n.z = fn(v + vec3(0.0, 0.0, d)) - fn(v + vec3(0.0, 0.0, -d));
    return normalize(n);
}

float comb(float v, float s)
{
    return pow(0.5 + 0.5 * cos(v * 2.0 * pi), s);
}

vec4 tex(vec3 v)
{
    float d = abs(v.z * 20. + sfract(-iTime, 4.) * 5.); 
    vec4 c0 = clamp(vec4(0, v.z, 1. - v.z, 0), 0.,1.);
    vec4 c1 = vec4(0.5 + 0.5 * nrm(v), 1.0);
    vec4 c = mix(c0, c1, vec4(0.5 - 0.5 * cos(0.05 * iTime * 2.0 * pi))); // change color-palette to enhance branching structure visibility
    return exp(-d*d) + c * abs(nrm(v).x);
}

vec3 camera(vec2 uv, float depth)
{
    float phi = iTime*.1,  C=cos(phi), S=sin(phi);

    vec3 v = vec3(uv, depth);

    v *= mat3( 0, 1,-1,
              -1,-1,-1, 
               1,-1,-1 );

    v.zy *=  mat2 (C,S,-S,C); // could be mixed above

    return v;
}

void mainImage( out vec4 o,  vec2 uv )
{
    float t = iTime * 0.1;
    vec2 R  = iResolution.xy;
        uv = ( 2. * uv - R)  / R.y;

    vec3 w = vec3(0), v;

    for(float layer = 0.; layer < 1.; layer += 1./256.) 
        v = camera(uv, 2. * layer - 1.),
        abs(v.x) < 1. && abs(v.y) < 1. && abs(v.z) < 1. && abs(fn(v)) < .05 ?  w = v : w;

    // o = tex(w);
    o = all(equal(w, vec3(0))) ? vec4(0.0) : tex(w); // avoids flashing background
}

#define M_2PI 6.28318530718
vec2 polar(vec2 dPoint)
{
    return vec2(sqrt(dPoint.x * dPoint.x + dPoint.y * dPoint.y), atan(dPoint.y, dPoint.x));
}

float rand(vec2 co)
{
    return fract(sin(dot(co.xy, vec2(12.9898,78.233))) * 43758.5453);
}

vec2 decart(vec2 pPoint)
{
    return vec2(pPoint.x * cos(pPoint.y), pPoint.x * sin(pPoint.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 screen = iResolution.xy;
    vec2 center = screen / 2.0;
    vec2 frag = fragCoord.xy - center;
    vec2 fragPolar = polar(frag);
    float lenCenter = length(center);

	const float bandPass = 720.0;
    const float angleDisp = M_2PI / (bandPass + 1.0);

    const float particlesCount = 200.0;
    const float particleLifetime = 10.0;
    const float particleMaxSize = 7.5;
    float particleMaxSizeNorm = particleMaxSize / lenCenter;

    float globTime = iTime / particleLifetime;
    float timeDelta = bandPass;

    const float polarRadiusClip = 0.05;
    const float polarRadiusMax = 0.75;
    float polarRadiusDelta = polarRadiusMax - polarRadiusClip; 

    float presence = 0.0;
    vec2 pPoint;

    for (float i = 0.0; i < particlesCount; i += 1.0)
    {
        float phase = i / particlesCount;

        float localTime = globTime + timeDelta * (2.0 * phase - 1.0) + phase;
        float particleTime = fract(localTime);
        float spaceTransform = pow(particleTime, 8.0);

        pPoint.x = lenCenter * ((polarRadiusClip + polarRadiusDelta * phase) + spaceTransform);

        // +30 FPS :)
        if (abs(pPoint.x - fragPolar.x) > particleMaxSize) continue;

        pPoint.y = floor(particleTime + bandPass * rand(vec2(floor(localTime), 1))) * angleDisp;

        vec2 dPoint = decart(pPoint);        
        float particleSize = particleMaxSize * spaceTransform;
        float localPresence = particleSize * (1.0 - clamp(length(dPoint - frag), 0.0, 1.0));
        presence += localPresence;
    }
    presence = clamp(presence, 0.0, 1.0);
    fragColor = vec4(presence, presence, presence, 1.0);
}

vec2 rotate(vec2 pos, float angle) {
	float c = cos(angle);
	float s = sin(angle);

	return mat2(c, s, -s, c) * pos;
}

float smin( float a, float b, float k )
{
    float res = exp( -k*a ) + exp( -k*b );
    return -log( res )/k;
}

float smin( float a, float b )
{
    return smin(a, b, 3.0);
}

float iPlane(vec3 pos) {
    return pos.y;
}

float iSphere(vec3 pos, float radius) {
    return length(pos) - radius;
}


float map(vec3 pos) {
    float dPlane = iPlane(pos);
    float dSphere = iSphere(pos, 2.0);

    float s1 = iSphere(pos - vec3(sin(iTime*.55)*1.85, cos(iTime*.19) * 0.95, sin(iTime*.91) * 1.21) * 1.0, 2.6451);
    float s2 = iSphere(pos - vec3(cos(iTime*.43)*1.55, sin(iTime*.38) * 1.12, cos(iTime*.76) * 1.67) * 1.4, 2.564821);
    float s3 = iSphere(pos - vec3(sin(iTime*.26)*2.52, cos(iTime*.57) * 0.56, sin(iTime*.12) * 1.58) * 1.2, 2.98441);
    float s4 = iSphere(pos - vec3(sin(iTime*.97)*1.72, sin(iTime*.22) * 0.81, cos(iTime*.34) * 0.97) * 1.5, 2.12373);
    float s5 = iSphere(pos - vec3(sin(iTime*.62)*1.47, cos(iTime*.76) * 0.73, sin(iTime*.75) * 1.45) * 1.7, 2.2748186);

    return smin(s1, smin(s2, smin(s3, smin(s4, s5))));
}

const float PI = 3.141592657;
const vec3 lightDirection = vec3(1.0, 1.0, -1.0);
const vec4 lightBlue = vec4(186.0, 209.0, 222.0, 255.0)/255.0;

float diffuse(vec3 normal) {
    return max(dot(normal, normalize(lightDirection)), 0.0);   
}

float specular(vec3 normal, vec3 dir) {
	vec3 h = normalize(normal - dir);
	return pow(max(dot(h, normal), 0.0), 40.0);
}

vec3 getColor(vec3 pos, vec3 normal, vec3 dir) {
    pos.xz = rotate(pos.xz, sin(iTime*.25)*.2);
    pos.yz = rotate(pos.yz, cos(sin(iTime*.25)*.2)+iTime*.1) * .3;    
    float zOffset = 200.0 + sin(cos(iTime*0.443256)*0.716786) * 200.0;
    zOffset = 1.0 + zOffset*.05;
    float grey = fract(pos.z*zOffset - iTime*.1);
    grey = 1.0 - sin(grey * PI);
    grey *= mix(1.0 + sin(pos.z*50.0 - iTime*.1) * 0.5, 0.0, .25);
    grey = grey * grey;

    float diff = diffuse(normal);
    float spec = specular(normal, dir);

//    return vec3(grey+diff*.75);
    return vec3(grey+spec*.75+diff*.5);
}

vec3 computeNormal(vec3 pos) {
	vec2 eps = vec2(0.01, 0.0);

	vec3 normal = vec3(
		map(pos + eps.xyy) - map(pos - eps.xyy),
		map(pos + eps.yxy) - map(pos - eps.yxy),
		map(pos + eps.yyx) - map(pos - eps.yyx)
	);
	return normalize(normal);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = -1.0 + uv * 2.0;
    uv.x *= iResolution.x / iResolution.y;

    float focus = 1.25;
    vec3 pos = vec3(0.0, 0.0, -10.0);
    vec3 dir = normalize(vec3(uv, focus));

    vec4 color = vec4(.0);
    float d;

    const int NUM_ITER = 64;
    for(int i=0; i<NUM_ITER; i++) {
        d = map(pos);
        if(d < 0.0001) {
            vec3 normal = computeNormal(pos);
            color.rgb = getColor(pos, normal, dir);
            color.a = 1.0;
            break;
        }

        pos += d * dir;
        if(length(pos) > 10.0) break;
    }

    color = color;

	fragColor = vec4(color) * lightBlue;
}

