float sphere(vec3 pos, float radius) {	return length(pos) - radius;	}
float displacement(vec3 p) {	return sin(2.0*p.x+iTime*.983265)*sin(2.0*p.y+iTime*.57834)*sin(1.0*p.z+iTime*0.857834) * .5 + .5;	}
