}


float map(vec3 pos) {
    float dSphere = iSphere(pos, 4.5);

    float s1 = iSphere(pos - vec3(sin(iTime*.55)*1.85, cos(iTime*.19) * 0.95, sin(iTime*.91) * 1.21) * 1.0, 2.6451);
    float s2 = iSphere(pos - vec3(cos(iTime*.43)*1.55, sin(iTime*.38) * 1.12, cos(iTime*.76) * 1.67) * 1.4, 2.564821);
    float s3 = iSphere(pos - vec3(sin(iTime*.26)*2.52, cos(iTime*.57) * 0.56, sin(iTime*.12) * 1.58) * 1.2, 2.98441);
    float s4 = iSphere(pos - vec3(sin(iTime*.97)*1.72, sin(iTime*.22) * 0.81, cos(iTime*.34) * 0.97) * 1.5, 2.12373);
    float s5 = iSphere(pos - vec3(sin(iTime*.62)*1.47, cos(iTime*.76) * 0.73, sin(iTime*.75) * 1.45) * 1.7, 2.2748186);

    float d = smin(s1, smin(s2, smin(s3, smin(s4, s5))));

    return max(d, dSphere);
}

const float PI = 3.141592657;
const vec3 lightDirection = vec3(1.0, 1.0, -1.0);
const vec4 lightBlue = vec4(186.0, 209.0, 222.0, 255.0)/255.0;

float diffuse(vec3 normal) {
    return max(dot(normal, normalize(lightDirection)), 0.0);   
}

float specular(vec3 normal, vec3 dir) {
	vec3 h = normalize(normal - dir);
	return pow(max(dot(h, normal), 0.0), 40.0);
}

vec3 getColor(vec3 pos, vec3 normal, vec3 dir) {
    pos.xz = rotate(pos.xz, sin(iTime*.25)*.2);
    pos.yz = rotate(pos.yz, cos(sin(iTime*.25)*.2)+iTime*.1) * .3;    
    float zOffset = 200.0 + sin(cos(iTime*0.443256)*0.716786) * 200.0;
    zOffset = 1.0 + zOffset*.05;
    float grey = fract(pos.z*zOffset - iTime*.1);
    grey = 1.0 - sin(grey * PI);
    grey *= mix(1.0 + sin(pos.z*50.0 - iTime*.1) * 0.5, 0.0, .25);
    grey = grey * grey;

    float diff = diffuse(normal);
    float spec = specular(normal, dir);

//    return vec3(grey+diff*.75);
    return vec3(grey+spec*.75+diff*.5);
}

vec3 computeNormal(vec3 pos) {
	vec2 eps = vec2(0.01, 0.0);

	vec3 normal = vec3(
		map(pos + eps.xyy) - map(pos - eps.xyy),
		map(pos + eps.yxy) - map(pos - eps.yxy),
		map(pos + eps.yyx) - map(pos - eps.yyx)
	);
	return normalize(normal);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = -1.0 + uv * 2.0;
    uv.x *= iResolution.x / iResolution.y;

    float focus = 1.25;
    vec3 pos = vec3(0.0, 0.0, -10.0);
    vec3 dir = normalize(vec3(uv, focus));

    vec4 color = vec4(.0);
    float d;

    const int NUM_ITER = 64;
    for(int i=0; i<NUM_ITER; i++) {
        d = map(pos);
        if(d < 0.0001) {
            vec3 normal = computeNormal(pos);
            color.rgb = getColor(pos, normal, dir);
            color.a = 1.0;
            break;
        }

        pos += d * dir;
        if(length(pos) > 10.0) break;
    }

    color = color;

	fragColor = vec4(color) * lightBlue;
}
