// ray computation vars
const float PI = 3.14159265359;
const float fov = 50.0;

// epsilon-type values
const float S = 0.01;
const float EPSILON = 0.01;

// const delta vectors for normal calculation
const vec3 deltax = vec3(S ,0, 0);
const vec3 deltay = vec3(0 ,S, 0);
const vec3 deltaz = vec3(0 ,0, S);

float distanceToNearestSurface(vec3 p){
	float s = 1.0;
    vec3 d = abs(p) - vec3(s);
    return min(max(d.x, max(d.y,d.z)), 0.0)
        + length(max(d,0.0));
}


// better normal implementation with half the sample points
// used in the blog post method
vec3 computeSurfaceNormal(vec3 p){
    float d = distanceToNearestSurface(p);
    return normalize(vec3(
        distanceToNearestSurface(p+deltax)-d,
        distanceToNearestSurface(p+deltay)-d,
        distanceToNearestSurface(p+deltaz)-d
    ));
}


vec3 computeLambert(vec3 p, vec3 n, vec3 l){
    return vec3(dot(normalize(l-p), n));
}

vec3 intersectWithWorld(vec3 p, vec3 dir){
    float dist = 0.0;
    float nearest = 0.0;
    vec3 result = vec3(0.0);
    for(int i = 0; i < 20; i++){
        nearest = distanceToNearestSurface(p + dir*dist);
        if(nearest < EPSILON){
            vec3 hit = p+dir*dist;
            vec3 light = vec3(100.0*sin(iTime), 30.0*cos(iTime), 50.0*cos(iTime));
            result = computeLambert(hit, computeSurfaceNormal(hit), light);
            break;
        }
        dist += nearest;
    }
    return result;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float cameraDistance = 10.0;
    vec3 cameraPosition = vec3(10.0*sin(iTime), 0.0, 10.0*cos(iTime));
	vec3 cameraDirection = vec3(-1.0*sin(iTime), 0.0, -1.0*cos(iTime));
	vec3 cameraUp = vec3(0.0, 1.0, 0.0);

    // generate the ray for this pixel
    const float fovx = PI * fov / 360.0;
    float fovy = fovx * iResolution.y/iResolution.x;
    float ulen = tan(fovx);
    float vlen = tan(fovy);

    vec2 camUV = uv*2.0 - vec2(1.0, 1.0);
    vec3 nright = normalize(cross(cameraUp, cameraDirection));
    vec3 pixel = cameraPosition + cameraDirection + nright*camUV.x*ulen + cameraUp*camUV.y*vlen;
    vec3 rayDirection = normalize(pixel - cameraPosition);

    vec3 pixelColour = intersectWithWorld(cameraPosition, rayDirection);
    fragColor = vec4(pixelColour, 1.0);
}

const int RAY_MARCHING_ITERATIONS = 95;
const float RAY_MARCHING_TMAX = 40.0;
const float MATERIAL_SKY = -1.0;
const float MATERIAL_FLOOR = 1.49;
const float MATERIAL_BALL = 47.0;
const vec3 COLOR_SKY = vec3(0.8, 0.7, 0.5);
const vec3 COLOR_BALL = vec3(0.2, 0.5, 0.9);
const vec3 COLOR_FLOOR = vec3(0.2, 0.2, 0.2);


vec2 hash( vec2 p ) {
	p = vec2( dot(p,vec2(107.1,301.7)),
			  dot(p,vec2(151.5,353.3)) );
	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

float noise( in vec2 p ) {
    vec2 i = floor( p );
    vec2 f = fract( p );
	vec2 u = f*f*(3.0-2.0*f);
    return mix( mix( dot(hash( i + vec2(0.0,0.0)), f - vec2(0.0,0.0)), 
                     dot(hash( i + vec2(1.0,0.0)), f - vec2(1.0,0.0)), u.x),
                mix( dot(hash( i + vec2(0.0,1.0)), f - vec2(0.0,1.0)), 
                     dot(hash( i + vec2(1.0,1.0)), f - vec2(1.0,1.0)), u.x), u.y);
}

float fractalnoise(in vec2 p, float scale, float opacity) {
	float f = 0.0;
    p *= scale;
    mat2 m = mat2( 1.6,  1.2, -1.2,  1.6 );
    f  = 0.5000*noise(p); 
    p = m*p;
    f += 0.2500*noise(p); 
    p = m*p;
    f += 0.1250*noise(p); 
    p = m*p;
    f += 0.0625*noise(p); 
    p = m*p;
    f += 0.0312*noise(p); 
	return (1.0 - opacity) + opacity * f;
}

// Objects	
float objectFloor(vec3 pos, float level) {
	return pos.y - level;
} 

float objectSphere(vec3 pos, float radius) {
	return length(pos) - radius;
} 

// Dado un vector de (distancia, material) y otro (distancia, material) retorna el que tenga menor distancia
vec2 opU(vec2 d1, vec2 d2) {
	return (d1.x < d2.x) ? d1 : d2;
}

// PodrÃ­a separarse dos versiones donde una no devuelva el material y usarlo en el calculo de normales (ver si optimiza x default)
vec2 hit(in vec3 pos) {
	vec3 sphereRotPoint = vec3(pos.x + cos(iTime + pos.z), 
                               pos.y + 0.5 * sin(iTime + pos.z), 
                               mod(pos.z + iTime * 4.0, 0.75))
                           	- vec3(-1.25, 0.1, 0.2);

    vec2 sphereZ = vec2(objectSphere(
                    	vec3(pos.x, pos.y, mod(pos.z + iTime, 1.0)) 
       						- vec3(-1.25, 0.1, 0.2), 0.25), MATERIAL_BALL),
         sphereRot = vec2(objectSphere(sphereRotPoint, 0.15), MATERIAL_BALL),
         sphereRotRot =
        	vec2(objectSphere(
        		sphereRotPoint + 
        		vec3(0.25 * cos(iTime * 2.0),
                     0.25 * sin(iTime * 2.0),
                     0.0),
                  0.06),
                 	MATERIAL_BALL),
        sphereRotRot2 = vec2(objectSphere(
        		sphereRotPoint + 
        		vec3(0.25 * cos(iTime * 2.0 + 6.28 * 0.33),
                     0.25 * sin(iTime * 2.0 + 6.28 * 0.33),
                     0.0),
                  0.06),
                 	MATERIAL_BALL),
        sphereRotRot3 = vec2(objectSphere(
        		sphereRotPoint + 
        		vec3(0.25 * cos(iTime * 2.0 + 6.28 * 0.66),
                     0.25 * sin(iTime * 2.0 + 6.28 * 0.66),
                     0.0),
                  0.06),
                 	MATERIAL_BALL),
        floorPlane = vec2(objectFloor(pos - vec3(0.25, -1.0, 0.0), 0.0), MATERIAL_FLOOR);

    vec2 res = opU(opU(opU(sphereRotRot, opU(sphereRotRot2, sphereRotRot3)), floorPlane), 
                   opU(sphereRot, sphereRot));

    return res;
}

vec3 calcNormal(in vec3 pos) {
	vec3 eps = vec3(0.001, 0.0, 0.0);
	vec3 nor = vec3(
		hit(pos + eps.xyy).x - hit(pos - eps.xyy).x,
		hit(pos + eps.yxy).x - hit(pos - eps.yxy).x,
		hit(pos + eps.yyx).x - hit(pos - eps.yyx).x);
	return normalize(nor);
}

mat3 setCamera(in vec3 ro, in vec3 ta, float cr) {
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr), 0.0);
	vec3 cu = normalize(cross(cw, cp));
	vec3 cv = normalize(cross(cu, cw));
	return mat3(cu, cv, cw);
}

bool isMaterial (float material, float value) {
	return abs(material - value) < 0.01;
}

vec3 colorForMaterial(float material, vec3 p) {
	// #TODO: Pasar a tabla
	if (isMaterial(material, MATERIAL_FLOOR)) {
        return 0.4 + COLOR_FLOOR * mod(floor(p.x * 4.0) + floor(p.z * 4.0), 2.0);
	}
	else if (isMaterial(material, MATERIAL_BALL))
		return COLOR_BALL;
	else if (isMaterial(material, MATERIAL_SKY))
		return vec3(fractalnoise(vec2(p.x / 20.0, p.z), 0.1, 0.2));
		//return COLOR_SKY;
	else
		// #NOTE: green is error color :)
		return vec3(0.0, 1.0, 0.0);
}

vec2 castRayToWorld(in vec3 ro, in vec3 rd) {
	float tmin=1.0, tmax=RAY_MARCHING_TMAX, precis=0.01, t=tmin, m=-1.0;

	for (int i=0; i< RAY_MARCHING_ITERATIONS; i++) {
		vec2 p = hit(ro + rd * t);
		if ((precis > p.x) || (t > tmax)) break;
		// En x viene la distancia al objeto
		t += p.x;
		// El material vuelve en la segunda coordenada
		m = p.y;
	}

	// m = -1.0 significa que pega en el infinito
	if (t > tmax) m = -1.0;
	return vec2(t, m);
}

vec3 render(in vec3 ro, in vec3 rd) {
	vec2 value = castRayToWorld(ro, rd);
	float distance = value.x;
	float material = value.y;
	vec3 color;

	if (isMaterial(material, MATERIAL_SKY)) 
		// #ADD: PodrÃ­a depender de la posicion tambiÃ©n, para usar un hash + perlin
		color = COLOR_SKY;
	else {
		vec3 pos = ro + distance * rd;

		// Material
		color = colorForMaterial(material, pos);
		// Normal
		vec3 normal = calcNormal(pos);
		//vec3 ref = reflect(rd, normal);
		// #TODO: Factorizar para tener varias luces
		vec3 light = normalize(vec3(-0.6, 0.7, -0.5));	
		float dif = clamp(dot(normal, light), 0.0, 1.0);
		// Lighting
			// Ambient occlusion
			// Shadow
			// Ambient lighting
			float amb = clamp(0.5+0.5*normal.y, 0.0, 1.0);

		// Mix
		vec3 brdf = vec3(dif);
		brdf += 1.2 * amb * vec3(0.50, 0.20, 1.00);
		color *= brdf;
	}

	return color;
} 

