vec3 cameraPos;
float focalLength;

vec3 backgroundColor = vec3(0.0);
vec3 baseCheckerboardColor = vec3(0.2, 0.9, 0.2);

vec2 screenCenter;
const int numLayers = 10;

bool IsCheckerboardAtPixel(in vec2 fragCoord, in float checkerboardZ)
{
    float z = checkerboardZ - cameraPos.z;

    //Dont draw checkerboard if it's behind the camera
    if(z <= 0.) return false;

    //Transform pixel coords to world coords
    //Inverse of [screenxy = ((worldxy - camxy)/z * scale) + center] is [worldxy = camxy + (z * ((screenxy - center)/scale)]
    vec2 worldxy = cameraPos.xy + ((fragCoord - screenCenter)/focalLength) * z;

    // Uncomment for spinning!
    //mat2 rotationMatrix = mat2(cos(iTime/3.0), -sin(iTime/3.0),
    //                           sin(iTime/3.0), cos(iTime/3.0));
    //worldxy = cameraPos.xy + (rotationMatrix * (worldxy - cameraPos.xy));

    // Uncomment for melting!
    //worldxy.x += sin(worldxy.y + iTime - (checkerboardZ * checkerboardZ));
    //worldxy.y += sin(worldxy.x + iTime + (checkerboardZ * checkerboardZ));

    //checkboard squares are 1x1
    worldxy = mod(worldxy, 2.);
    return (worldxy.x < 1.) == (worldxy.y < 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    screenCenter = iResolution.xy / 2.0;

    //+z is towards checkerboard, -z is away from checkerboard
    cameraPos = vec3(10.0 * cos(iTime / 2.0), 
                     10.0 * sin(iTime / 2.0), 
                     -((sin(iTime) + 1.0) * 2.0 - 0.5)
                    );

    //holding mouse lets you move camera around a bit
    cameraPos.xy -= iMouse.xy / 50.;

    //Not sure if focal length is correct term but it gets the idea across
    //Multiply by resolution so scale stays the same when resolution is higher
    focalLength = 100.0 * (iResolution.x/1000.0);

    //Generate checkerboard layers
    fragColor = vec4(backgroundColor, 1.0);
    for(int i=0; i < numLayers; ++i)
    {
        float checkerZ = float(i)*0.8;

        //Colors get darker at lower layers
        float colorMult = float(numLayers - i)/float(numLayers);
        vec4 planeColor = vec4(baseCheckerboardColor * colorMult, 1.0);

        //Check if the checkerboard is visible at this pixel
        if(IsCheckerboardAtPixel(fragCoord, checkerZ))
        {
            fragColor = planeColor;
            break;
        }
    }
}

vec2 p0 = vec2( 0.0, 0.5 ); //start
vec2 p1 = vec2( 0.0, 1.0 ); //ctrl1
vec2 p2 = vec2( 1.0, 0.0 ); //ctrl2
vec2 p3 = vec2( 1.0, 0.5 ); //end

vec2 getPoint ( float t ) {

    float tp = 1.0 - t;

    return t * t * t         * p3 +
           3.0 * t * t * tp  * p2 +
           3.0 * t * tp * tp * p1 +
           tp * tp * tp      * p0;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    p1.y = ( sin( iTime ) + 1.0 ) * 0.5; // 0 to 1

    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 point = getPoint( uv.x );

    if ( uv.y - point.y > 0.0 ) {

      fragColor = vec4(1.0);

    } else {

      fragColor = vec4( uv, 0.0, 1.0);

    }

}

// based on www.shadertoy.com/view/Xds3zN Created by inigo quilez - iq/2013
