int
mb( in vec2 p_ri ) {

    vec2
    c = p_ri;

    float
    tmpR;

    for( int i = 0; i < 255; i++ ) {

        tmpR = c.x;

    	c.x = c.x * c.x - c.y * c.y - p_ri.x; 
    	c.y = 2. * tmpR * c.y - p_ri.y;

        if( 16. < c.x * c.x + c.y * c.y ) {

        	return i;
        }
    }

    return 255;
}

