mat3
    crss( in vec3 p_vecLHS ) {

        return mat3( +0., -p_vecLHS.z, +p_vecLHS.y, +p_vecLHS.z, +0., -p_vecLHS.x, -p_vecLHS.y, +p_vecLHS.x, +0. );
    }

mat3
    parallel( in vec3 p_axeN ) {

        return mat3( p_axeN.x * p_axeN, p_axeN.y * p_axeN, p_axeN.z * p_axeN );
    }

vec3
    parallel( in vec3 p_axeN, in vec3 p_vec ) {

        return dot( p_axeN, p_vec ) * p_axeN;
    }

mat3
    orthogonal( in vec3 p_axeN ) {

        return I3 - parallel( p_axeN );
    }

vec3
    orthogonal( in vec3 p_axeN, in vec3 p_vec ) {

        return p_vec - parallel( p_axeN, p_vec );
    }

mat3
    rot( vec3 p_axe, float p_alpha ) {

        vec3
            n = normalize( p_axe );

        return parallel( n ) + cos( p_alpha ) * orthogonal( n ) + sin( p_alpha ) * crss( n );
    }

vec3
    rot( in vec3 p_axe, in vec3 p_vec, float p_alpha ) {

        vec3
            n = normalize( p_axe ),
            p = dot( n, p_vec ) * n;

        return p + cos( p_alpha ) * ( p_vec - p ) + sin( p_alpha ) * cross( n, p_vec );
    }

void
    setOutput( in vec3 p_pnt, in vec3 p_norm, in vec3 p_col ) {

        vec3
            dist = p_pnt - inp.eye;

        float
            distSqr = dot( dist, dist );

        if( distSqr < outp.dst ) {

            outp.pnt = p_pnt;
            outp.nrm = p_norm;
            outp.col = p_col;
        	outp.dst = distSqr;
        }
    }

