	float value = t - floor(t);			
	// smooth out the discontinuity
	value = value*(1.0 - smoothstep(0.95,1.0,value));  
	value = 0.333 + value*0.667;
	vec3 color = vec3(value);		    
    return mix( color, objClr, mixVal );        				
}

