void arrow(vec2 u, vec2 n) {
	vec2 o = vec2(n.y, -n.x);
	move_to(u);
	u += n;
	line_to(u);
	move_to(u - o*0.2);
	line_to(u + o*0.2);
	line_to(u + n*0.4);
	close_path();
}

float sdcircle(vec2 p, float r) {
    return length(p) - r;
}

float sdbox(vec2 p, vec2 b)
{
  vec2 d = abs(p) - b;
  return min(max(d.x,d.y),0.0) +
         length(max(d,0.0));
}

float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float dtf(vec2 p, float t) {
    float a = sin(t*0.5)*3.0;
    vec2 sc = vec2(sin(a),cos(a));    
    vec2 rp = vec2(sc.y*p.x-sc.x*p.y, dot(sc,p));
    float w = sin(t);
    float b = sdbox(p - vec2(0.5,w*0.5), vec2(0.5));
    return smin(b,mix(sdcircle(p,0.5),sdbox(rp, vec2(0.25, 0.5)),sin(t)*0.5+0.5),
        mix(0.0,0.1,sin(t*12.0)*0.5+0.5));
}
vec2 grad(vec2 p, float t) {
    vec2 eps = vec2(0.0,1e-3);
    return vec2(
		dtf(p + eps.yx, t) - dtf(p - eps.yx, t),
        dtf(p + eps.xy, t) - dtf(p - eps.xy, t)) / 2e-3;
}

float timef = 0.0;
float myf2(vec2 p) {
    return dtf(p, timef);
}

// maps n=0 to blue, n=1 to red, n=0.5 to green
vec3 normhue(float n) {
    return hue(0.66667 * (1.0 - clamp(n,0.0,1.0)));
}

void paint() {
    float t = iTime;

    // clear screen

    float timest = 1.0/60.0;
    float t0 = iTime;
    float t1 = t0 + timest;

    vec2 o = get_origin();
    vec2 v = grad(o, t0);
    float dt = (dtf(o, t0) - dtf(o, t1)) / timest;

    set_source_rgb(vec3(v*dt,0.0)*0.5+0.5);
    clear();

    float dm = dtf(o, t1);
    set_line_width_px(1.0);
    set_source_rgba(vec4(vec3(1.0,1.0,1.0),0.5));
	for (int i = 0; i < 5; ++i) {
		add_field(dm-float(i)*0.2);
		stroke();
	}    
    // graphs only look good at pixel size
    set_line_width_px(1.0);
    timef = t0;
    graph2D(myf2);
    set_source_rgba(vec4(vec3(0.0,1.0,1.0),1.0));
    stroke();
    timef = t1;
    graph2D(myf2);
    set_source_rgba(vec4(vec3(1.0),1.0));
    stroke();


    vec2 m = get_query();
    vec2 mv = grad(m, t0);
    float mdt = (dtf(m, t0) - dtf(m, t1));

    arrow(m, mv*mdt*20.0);
    stroke();

}

