bool
hit( out vec3 p_hitp, in vec3 p_eye, in vec3 p_dir, in Ball p_ball ) {

    vec3
    dist = p_ball.pos - p_eye;

    float
    aa = dot( p_dir, p_dir ),
    ab = dot( p_dir, dist ),
    bb = dot( dist, dist ),
    ds = ( p_ball.rad * p_ball.rad - bb ) * aa + ab * ab;

    if( 0. <= ds ) {

    	ds = sqrt( +ds );

        float
        l = ( ab - ds ) / aa;

        p_hitp = p_eye + l * p_dir;

        return true;
    }

    return false;
}

