float df_obj(vec3 p)
{
	p += trans();
	p *= rot();

	//p = mod(p+vec3(2.0),4.0)-vec3(2.0);
	float s0 = dot(abs(p),vec3(1.0))-1.5;
	s0*=.5;

	//p*=rot;

	float s1 = length(p+vec3(1.,.0,.0))-.5;
	float s2 = length(p+vec3(.0,1.,.0))-.5;
	float s3 = length(p+vec3(.0,.0,1.))-.5;
	float s4 = length(p-vec3(1.,.0,.0))-.5;
	float s5 = length(p-vec3(.0,1.,.0))-.5;
	float s6 = length(p-vec3(.0,.0,1.))-.5;
	s0 = min(s0,s1);
	s0 = min(s0,s2);
	s0 = min(s0,s3);
	s0 = max(s0,-s4);
	s0 = max(s0,-s5);
	s0 = max(s0,-s6);

	p*=rot();

	s0 = min(s0,max(max(abs(p.x+2.0),abs(p.y)),abs(p.z))-.25);
	s0 = min(s0,max(max(abs(p.x-2.0),abs(p.y)),abs(p.z))-.25);
	s0 = min(s0,max(max(abs(p.y+2.0),abs(p.z)),abs(p.x))-.25);
	s0 = min(s0,max(max(abs(p.y-2.0),abs(p.z)),abs(p.x))-.25);
	s0 = min(s0,max(max(abs(p.z+2.0),abs(p.x)),abs(p.y))-.25);
	s0 = min(s0,max(max(abs(p.z-2.0),abs(p.x)),abs(p.y))-.25);

	p*=rot();

	s0 = min(s0,max(max(abs(p.x+4.0),abs(p.y)),abs(p.z))-.25);
	s0 = min(s0,max(max(abs(p.x-4.0),abs(p.y)),abs(p.z))-.25);
	s0 = min(s0,max(max(abs(p.y+4.0),abs(p.z)),abs(p.x))-.25);
	s0 = min(s0,max(max(abs(p.y-4.0),abs(p.z)),abs(p.x))-.25);
	s0 = min(s0,max(max(abs(p.z+4.0),abs(p.x)),abs(p.y))-.25);
	s0 = min(s0,max(max(abs(p.z-4.0),abs(p.x)),abs(p.y))-.25);

	return s0;
}


float df(vec3 p)
{
	return min(df_obj(p),df_back(p));
}


vec3 nf(vec3 p)
{
	float e = .01;
	float dfp = df(p);
	return vec3(
		(dfp+df(p+vec3(e,.0,.0)))/e,
		(dfp+df(p+vec3(.0,e,.0)))/e,
		(dfp+df(p+vec3(.0,.0,e)))/e);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
	uv.x *= iResolution.x/iResolution.y;

	vec2 mouse = iMouse.xy/ iResolution.xy*2.0-1.0;
	mouse.x *= iResolution.x/iResolution.y*4.0;

	mat3 rotmat = rotate_y(mouse.x) * rotate_x(mouse.y);

	vec3 pos = vec3(.0,.0,-3.0)*rotmat ;
	vec3 dir = normalize(vec3(uv*.5,1.0-length(uv)*.25))*rotmat;

	vec3 light_dir = normalize(vec3(.4,.5,.6));
	vec3 light_color = vec3(.6,.5,.4);

	float dist;

	for (int i=0; i<80; i++)
	{
		dist = df(pos);
		pos+=dir*dist;
		if (dist<.00001) break;
	}

	vec3 color = vec3(1.0);
	vec3 n = nf(pos);

	vec3 dfdxn = dFdx(n);
	vec3 dfdyn = dFdy(n);

	float lines = length((abs(dfdxn)+abs(dfdyn))*3.0);
	lines = lines*3.75;
	lines = lines-1.0;
	lines = clamp(lines,.0,1.0);
	if (lines>1.0)lines = 1.0;

	if (length(pos)>5.0)
	{
		color = vec3(1.0);
	}
	else
	{
		float oa = 0.5;//df(pos+n)*.5+.5;
		float od = 1.0;

		for (int i=0; i<30; i++)
		{
			float fi = float(i);
			oa += df_obj(pos+noise(fi)-vec3(.5))*.15;
		}

		oa = min(1.0,oa);

		vec3 ocdir = light_dir;
		vec3 ocpos = pos+ocdir*.1;
		for (int i=0; i<60; i++)
		{
			float dist = df_obj(ocpos);
			od = min(od,dist*10.);
			ocpos += ocdir*dist*.3;
			if (dist<.0||dist>10.0) break;
		}


		od = max(.0,od);

		//oa -= mod(oa,.33);

		float diffuse = max(.0,dot(n,light_dir)*.8+.2)*od*oa*1.5;



		color = vec3(.1,.2,.3)*oa + diffuse*light_color;


	}

	vec3 color0 = mix(color,vec3(.0),lines);
	vec3 color1 = n*.4+.4;
	vec3 color2 = dfdxn+dfdyn;
	vec3 color3 = vec3(lines);

	float mt = mod(t(),32.0);
	float mti = mod(t(),1.0);

	if (mt<7.0){color = color0;}
	else if (mt<8.0){color = mix(color0,color1,mti);}
	else if (mt<11.0){color = color1;}
	else if (mt<12.0){color = mix(color1,color2,mti);}
	else if (mt<13.0){color = color2;}
	else if (mt<14.0){color = mix(color2,color3,mti);}
	else if (mt<18.0){color = color3;}
	else if (mt<19.0){color = mix(color2,color1-color3,mti);}
	else if (mt<24.0){color = color1-color3;}
	else if (mt<25.0){color = mix(color1-color3,color,mti);}
	else if (mt<29.0){;}
	else if (mt<30.0){color = mix(color,color0,mti);}
	else if (mt<32.0){color = color0;}

	color += noise(vec3(fragCoord.xy,t()*60.0))*0.01;



	fragColor = vec4(pow(color,vec3(.5)),1.0);
}

float waves(vec2 p) {
    p.y += sin(p.x * 6.0) * 0.1;
    p.y -= sin(iTime) * 0.15;
	p.y += sin(p.x * 12.0) * 0.06 * sin(iTime);

    p.y = mod(p.y, 0.25 + sin(iTime)*0.05);

    float res = abs(p.y);
    res = smoothstep(0.11, 0.1, res);

    return res;
}

float circle(vec2 p) {
    p.y += sin(p.x * 7.0 + iTime) * 0.35;
    return smoothstep(0.6, 0.61, length(p));
}

void mainImage( out vec4 o, in vec2 p ) {
    p /= iResolution.xy;
	p -= 0.5;
    p.x *= iResolution.x / iResolution.y;


    float res = waves(p);
    res = max(res, circle(p));

    o.rgb = vec3(res) + (1.0 - circle(p)) * -abs(p.y) * 0.5 - circle(p) * 0.5 + circle(p) * (1.0 - length(p)) * 0.3;

    p.y += sin(p.x * 0.21 + texture(iChannel0, vec2((p.x + 1.0) * 0.5, 0.0)).r * 0.1);

    o.r /= p.y + 0.07 * 2.0;
    o.b += p.y * 0.4;

}

void mainImage(out vec4 o, in vec2 p) {
    p = (p / iResolution.xy) - 0.5;
    p.x *= iResolution.x / iResolution.y;

    vec2 q = vec2(p.x, mod(p.y + iTime * 0.4, 0.15));
    q.x /= abs(q.y) * 8.0 + -1.0 + sin(iTime) * 0.6;

    float tri = smoothstep(0.2, 0.02, max(abs(q.y), smoothstep(0.6, 0.61, abs(q.x))));
    o.rgb = (1.0 - tri) * vec3((0.5 + p.y) * 0.75, (1.0 - abs(p.x)) * 0.02, (1.0 - p.y) * 0.2) +
        	tri * vec3(1.0, p.y * 4.0, 0.0);
}

#define PI 3.1415926535897932384626433832795

float atan2(in float x, in float y)
{
    if (x > 0.0) {
        return atan(y/x);
    } else if (x < 0.0) {
        if (y >= 0.0) {
            return PI + atan(y/x);
        } else {
            return PI - atan(y/x);
        }
    }

    return sign(y)*PI/2.0;
}

float computeHeartBeatFrequency(float t){   
    float ss = pow(t, 0.2)*1.2;
    ss = 1.0 + ss*sin(2.0*PI*t)*exp(-t*4.0);

    return ss;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // convert screen coordinates to -1:1
    vec2 uv = fragCoord.xy / vec2(iResolution.xy);
    uv = 2.0*uv + vec2(-1.0);

    float aspect = iResolution.x/iResolution.y;

    vec2 position = vec2(uv.x * aspect, uv.y);

    // correct point coordinates
    float dx = 0.05;
    float dy = -0.1;

    position.x -= dx;
    position.y += dy;

    // time
    float speedRedction = 1.;
    float time = mod(iTime/speedRedction, 1.5)/1.5;

    float heartBeat = computeHeartBeatFrequency(time);

    // heart function
    float theta = atan2(position.y, position.x + dx);
    float xh = 11.0*pow(sin(theta), 5.0);
    float yh = 10.9*cos(theta) - 4.83*cos(2.27*theta) - 3.33*cos(3.32*theta) - 1.1*cos(3.77*theta);

    xh = xh/(30.0 - 10.0*heartBeat);
    yh = yh/(30.0 - 10.0*heartBeat);

    vec2 heartPoint = vec2(xh, yh);

    // distances
    float distHeart = distance(heartPoint, vec2(0,0));
    float distPoint = distance(position, vec2(0,0));

    // colors
    vec3 color = vec3(1.0);
    vec3 lcolor = vec3(0.2 + sin(abs(theta)), 0.0, 0.0)*1.5*heartBeat*heartBeat;
    vec3 bcolor = vec3(0.1 - 0.01*position.y)*(1.0 - 0.25*length(position))*heartBeat*heartBeat;

    float thres = 0.1;

    if (distHeart > distPoint) {
        color = mix(lcolor, bcolor, smoothstep(-thres*0.5, thres*0.9, (distHeart - distPoint)));
    } else {
        color = mix(bcolor, lcolor, smoothstep(-thres*0.5, thres*0.5, (distHeart - distPoint)));
    }

    fragColor = vec4(color, 1.0);
}

