// how to convert pa_map to a classic map function
// t is the ray scalar
// returns function value at that point, and distance
// to next root or horizon
vec2 map(float t) {
    poly2 f = pa_map(pa_init(t));
    return vec2(f.a[0], nextevent(f));
}

float rayf(float t) {
    return pa_map(pa_init(t)).a[0];
}

float rayff(float t) {
    return pa_map(pa_init(t)).a[1];
}

float rayfff(float t) {
    return pa_map(pa_init(t)).a[2];
}

float raynextroot(float t) {
    return nextroot(pa_map(pa_init(t)));
}

float rayhorizon(float t) {
    return pa_map(pa_init(t)).h;
}

void paint() {
	vec2 ms = ((iMouse.xy/iResolution.xy)*2.0-1.0) * aspect;

    // clear screen

	set_source_rgb(vec3(0.0,0.0,0.5));
	clear();

	set_line_width_px(1.3);

    // draw zero crossing
    move_to(-2.0,0.0);
    line_to(2.0,0.0);
    set_line_width_px(1.0);
    set_source_rgb(vec3(1.0,0.0,0.0));
    stroke();

    #ifdef DEBUG_VIZ

    // draw 1D graph of estimated distance to horizon
    graph1D(rayhorizon);
    set_line_width_px(1.3);
    set_source_rgb(vec3(1.0,0.0,1.0));
    stroke();    

    // draw 1D graph of estimated distance to root
    graph1D(raynextroot);
    set_line_width_px(1.3);
    set_source_rgb(vec3(0.0,1.0,1.0));
    stroke();    

    // draw 1D graph of second derivative
    graph1D(rayfff);
    set_line_width_px(1.3);
    set_source_rgb(vec3(0.5,1.0,0.0));
    stroke();    

    // draw 1D graph of first derivative
    graph1D(rayff);
    set_line_width_px(1.3);
    set_source_rgb(vec3(1.0,1.0,0.0));
    stroke();    

    #endif

    // draw 1D graph of ray distances
    graph1D(rayf);
    set_line_width_px(1.3);
    set_source_rgb(vec3(1.0));
    stroke();    

	float maxt = aspect.x;

	float precis = 0.01;
	float t = (iMouse.z > 0.5)?ms.x:-aspect.x;

	for(int i = 0; i < 20; i++) {
		if(t > maxt) continue;
		set_source_rgb(hsl(float(i)/20.0, 1.0, 0.5));
        vec2 d = map(t);
        float w = t + d.y;
        move_to(t, 0.0);
        line_to(t, d.x);  
        line_to(w, 0.0);
		stroke();
        if (abs(d.x) <= precis) {
            circle(t, 0.0, 0.03);
            fill();
        }        
        t = w + 0.001;
	}
    if (t > maxt) {
        set_source_rgb(vec3(0.0,1.0,1.0));
        move_to(aspect.x * 0.99, -1.0);
        line_to(aspect.x * 0.99, 1.0);
        stroke();
    }


}

// Max Sills 2016 licensed under the MIT license.
//
// Quaternion rotation around axis, of angle, centered at center.
vec3 qRotate(vec3 axis, float angle, vec3 center, vec3 point)
{
float x = axis.x * sin(angle / 2.);
float y = axis.y * sin(angle / 2.);
float z = axis.z * sin(angle / 2.);
float w = cos(angle / 2.);

mat3 rotation = mat3(1. - 2.*y*y - 2.*z*z, 2.*(x*y+w*z), 2.*(x*z-w*y),
                     2.*(x*y-w*z), 1. - 2.*x*x - 2.*z*z, 2.*(y*z +w*x),
                     2.*(x*z + w*y), 2.*(y*z-w*x),1. - 2.*x*x - 2.*y*y);

return center + (rotation * (point-center));
}

// Return <theta, phi, distance>
vec3 udBox( vec3 p, vec3 b )
{
  vec3 newP = qRotate(vec3(0.,1.,0.),iTime,vec3(0.), p);
  float theta = acos(normalize(p).z) - acos(normalize(newP).z);
  float phi   = atan(p.y/p.x) - atan(newP.y/newP.x);
  return vec3(phi,theta, length(max(abs(newP)-b,0.0)));
}

vec3 march(vec3 eye, vec3 ray)				
{
    vec3 box = vec3(0);
    float t = 0.0;
    for (int i = 0; i < 32; ++i)				
    {	
        vec3 p = eye + t* normalize(ray-eye);	
        box = udBox(p, vec3(1,1,1));
        float distance = box.z;
        t += distance * 0.5;					
    }
    return vec3(box.x,box.y,t);							
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;	// Scale pixel coords to 0..1 range instead
    uv = uv * 2.0 - 1.0;						// Now to the -1 to +1 range
    uv.x *= iResolution.x / iResolution.y;		// Correct for aspect ratio.

    // Image plane is at (0, 0, -1)
    vec3 ray = vec3(uv, -1.0);	
    vec3 eye = vec3(0.0, 0.0, -3);

    // Tilt the viewing pyramid a bit based on cell.
    float angle = floor(fract(uv.x)*4.)+floor(fract(uv.y)*4.);
    float angle2= floor(fract(uv.x)*20.)+floor(fract(uv.y)*20.);
    angle = mix(angle,angle2, .1*abs(cos(iTime/10.)));
    ray = qRotate(vec3(1.,0.,0.),angle,vec3(0.), ray);
    eye = qRotate(vec3(1.,0.,0.),angle,vec3(0.), eye);

    vec3 tr     = march(eye, ray);		
    vec2 pos    = tr.xy;
    float t     = tr.z;

    fragColor = (1.0/t)*vec4(cos(pos.x), cos(pos.y), sin(pos.x), 0);
}

