//---------------------------------------------
vec3 rotationCoord(vec3 n, float t)
{
 vec3 result;

   vec2 sc = vec2(sin(t), cos(t));
   mat3 rotate;

      rotate = mat3( sc.y,  0.0, -sc.x,
                     0.0,   1.0,  0.0,
                     sc.x,  0.0, sc.y);   

  result = n * rotate;
  return result;
}

