vec2 SinCos( const in float x )
{
	return vec2(sin(x), cos(x));
}
vec3 RotateZ( const in vec3 vPos, const in vec2 vSinCos )
{
	return vec3( vSinCos.y * vPos.x + vSinCos.x * vPos.y, -vSinCos.x * vPos.x + vSinCos.y * vPos.y, vPos.z);
}

vec3 RotateZ( const in vec3 vPos, const in float fAngle )
{
	return RotateZ( vPos, SinCos(fAngle) );
}
vec2 tree(vec2 uv)
{
    uv.x = uv.x*2.0-1.0;
    float turns = 6.0+5.0*saw(iTime);
    uv = RotateZ(vec3(uv.xy, 0.0), PI/(1.0*turns)).xy;
    float theta = atan(uv.y, uv.x);

    float rot = float(int((theta/PI*.5+.5)*turns))/turns;

    vec2 xy = RotateZ(vec3(uv.xy, 0.0), 
                      PI*2.0*(rot)+PI/turns).xy;
    //xy = sign(xy)*log(abs(xy));
    //return vec2(saw(theta*turns+PI*stair(xy.x*1.0)), 1.0-jag(xy.x*4.0));
    //return vec2(saw(xy.y*turns+PI*stair(xy.x*1.0)), 1.0-jag(xy.x*4.0));
    return vec2(saw(xy.y*turns+PI*stair(xy.x*1.0)), 1.0-saw(xy.x*4.0*PI));
}

