    float l = length(color)*sin(iTime*4.409614)*5.5+sin(iTime*2.0);
    fragColor = vec4( vec3(cos(l), sin(iTime)*cos(l+3.14*5.0/3.0), sin(iTime)*cos(l+3.14*2.0/3.0))*.5+.5, 1.0 );
}

//iq's smooth HSV to RGB
vec3 hsv2rgb_smooth( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

	rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	

	return c.z * mix( vec3(1.0), rgb, c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 color;

    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv - vec2(.5);
    uv.x = uv.x * (iResolution.x / iResolution.y);

    float d = distance(uv, vec2(0));

    // scale the figure
    d = d * 30.;

    float hue = floor(d) / 20. + iTime * .1;

    float value = .9;

    // create space between the rings
    value = value * smoothstep(0., .2, fract(d));
    value = value * smoothstep(.9, .7, fract(d));

    // limit the number of rings
    value = value * float(d > 2.) * float(d < 13.);

    vec3 hsv = vec3(hue, .75, value);
	color = hsv2rgb_smooth(hsv);

    fragColor = vec4(color, 1);
}

#define pi            3.1415926535
