#define factor        ( 1. / float( MB_MAX_ITERS ) )
const mat3
id3x3 = mat3( 1., 0., 0., 0., 1., 0., 0., 0., 1. );

float
time,
gSR[ 7 ];

vec2
xy,
mxy,
txy;

vec3
hit,
gSP[ 7 ],
col[ 7 ];

struct Datas {

	vec3
    eye,
