Datas
rTD; //run time datas

mat3
proj( in vec3 p_r ) {

	return mat3( p_r.x * p_r, p_r.y * p_r, p_r.z * p_r );
}

mat3
antiproj( in vec3 p_r ) {

	return id3x3 - proj( p_r );
}

void
init( in vec2 p_xy ) {

    time = 1. * iTime;

    float
    rxy = iResolution.x / iResolution.y;

    xy   = ( p_xy / iResolution.xy -.5 );
    xy.x *= rxy;

    mxy  = ( iMouse.xy / iResolution.xy -.5 ); 
    mxy.x *= rxy;

    txy = p_xy / iResolution.xy;
    txy.x /= 6.;
    txy.x += mod( float( int( 6. * time ) ), 6. ) / 6.4;
    //txy.x = fract( txy.x );

    rTD.eye  = vec3( 1. * mxy.x, 0., 10.5 + 20. *  mxy.y );
    rTD.rayN = normalize( rTD.eye - vec3( xy, 0. ) );

	gSP[ 0 ] = vec3( .5 * sin( time ), 0., cos( time ) - 2. );
	gSR[ 0 ] = 1. / 16.;
	col[ 0 ] = vec3( 1., .15, .025 );

    gSP[ 1 ] = vec3( 0., .5 * cos( time ), sin( time ) - 2. );
	gSR[ 1 ] = 1. / 16.;
	col[ 1 ] = vec3( .025, 1., .15 );

    gSP[ 2 ] = vec3( .5 * cos( time ), .5 * sin( time ),  - 2. );
	gSR[ 2 ] = 1. / 16.;
	col[ 2 ] = vec3( .15, .025, 1. );

    gSP[ 3 ] = vec3( -.5 * sin( time ), 0., -cos( time ) - 2. );
	gSR[ 3 ] = 1. / 16.;
	col[ 3 ] = vec3( 0., .85, .975 );

    gSP[ 4 ] = vec3( 0., -.5 * cos( time ), -sin( time ) - 2. );
	gSR[ 4 ] = 1. / 16.;
	col[ 4 ] = vec3( .975, 0., .85 );

    gSP[ 5 ] = vec3( -.5 * cos( time ), -.5 * sin( time ),  - 2. );
	gSR[ 5 ] = 1. / 16.;
	col[ 5 ] = vec3( .85, .975, 0. );

    gSP[ 6 ] = vec3( 0., 0.,  -2. );
	gSR[ 6 ] = 1. / 16.;
	col[ 6 ] = vec3( 1., 1., 1. );
}

bool
intersectSphere( in vec3 p_distCenterEye, in float p_radSqr ) {

    float
    lambda = dot( rTD.rayN, p_distCenterEye ),
    radSqr = dot( p_distCenterEye, p_distCenterEye ) - lambda * lambda; 

    if( p_radSqr < radSqr ) {

    	return false;
    }

    hit = rTD.eye + ( sqrt( p_radSqr - radSqr ) - lambda ) * rTD.rayN;

    return true;
}

int
mb( in vec2 p_ri ) {

    vec2
    c = p_ri;

    float
    tmpR;

    for( int i = 0; i < 255; i++ ) {

        tmpR = c.x;

    	c.x = c.x * c.x - c.y * c.y - p_ri.x; 
    	c.y = 2. * tmpR * c.y - p_ri.y;

        if( 16. < c.x * c.x + c.y * c.y ) {

        	return i;
        }
    }

    return 255;
}

